import React, { useState, useEffect } from 'react';
import './Watchlist.css';

const Watchlist = () => {
  const FINNHUB_API_KEY = 'd17tfrhr01qteuvpuh10d17tfrhr01qteuvpuh1g'; // Replace with your Finnhub API key
  const [marketIndices, setMarketIndices] = useState([]);
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [socket, setSocket] = useState(null);

  // Initial data fetch
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        // Fetch initial indices data
       
        setMarketIndices([
          { 
            name: 'NIFTY', 
            number: '50', 
            value: niftyData.c, 
            change: niftyData.d, 
            changePercent: niftyData.dp 
          },
          { 
            name: 'SENSEX', 
            number: '', 
            value: sensexData.c, 
            change: sensexData.d, 
            changePercent: sensexData.dp 
          }
        ]);

        // Define the stocks we want to track
        const stockSymbols = ['AMZN', 'MSFT', 'INFY', 'AAPL'];
        
        // Fetch initial stock prices
        const stockPromises = stockSymbols.map(symbol => 
          fetch(`https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`)
        );

        const stockResponses = await Promise.all(stockPromises);
        const stockData = await Promise.all(stockResponses.map(res => res.json()));
        console.log(stockData);

        setStocks(stockSymbols.map((symbol, index) => ({
          symbol,
          price: stockData[index].c,
          change: stockData[index].d,
          changePercent: stockData[index].dp
        })));

        setLoading(false);
      } catch (err) {
        console.error('Initial fetch failed:', err);
        setError('Failed to load initial data. Trying real-time connection...');
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  // Initialize WebSocket connection after initial data is loaded
  useEffect(() => {
    if (loading || stocks.length === 0) return;

    const ws = new WebSocket(`wss://ws.finnhub.io?token=${FINNHUB_API_KEY}`);
    
    ws.onopen = () => {
      console.log('WebSocket connected');
      // Subscribe to all stocks
      stocks.forEach(stock => {
        ws.send(JSON.stringify({ type: 'subscribe', symbol: `BSE:${stock.symbol}` }));
      });
      // Subscribe to indices
      ws.send(JSON.stringify({ type: 'subscribe', symbol: 'NSE:NIFTY_50' }));
      ws.send(JSON.stringify({ type: 'subscribe', symbol: 'BSE:SENSEX' }));
    };

    ws.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.type === 'trade') {
        const symbolParts = data.symbol.split(':');
        const exchange = symbolParts[0];
        const symbol = symbolParts[1];
        const price = data.p;

        // Update stocks
        if (exchange === 'BSE') {
          setStocks(prevStocks => 
            prevStocks.map(stock => 
              stock.symbol === symbol 
                ? { 
                    ...stock, 
                    price: price,
                    change: price - (stock.price - stock.change), // Calculate new change
                    changePercent: ((price - (stock.price - stock.change)) / (stock.price - stock.change)) * 100
                  } 
                : stock
            )
          );
        }

       
        
      }
    };

    ws.onerror = (err) => {
      console.error('WebSocket error:', err);
      setError('Real-time connection failed. Showing last fetched prices.');
    };

    ws.onclose = () => {
      console.log('WebSocket disconnected');
      setError('Real-time connection lost. Trying to reconnect...');
      // You might want to implement reconnection logic here
    };

    setSocket(ws);

    return () => {
      if (ws) {
        // Unsubscribe before closing
        stocks.forEach(stock => {
          ws.send(JSON.stringify({ type: 'unsubscribe', symbol: `BSE:${stock.symbol}` }));
        });
       
        ws.close();
      }
    };
  }, [loading, stocks]);

  const formatNumber = (num) => {
    if (num === undefined || num === null) return '--';
    return num.toLocaleString('en-IN', { maximumFractionDigits: 2 });
  };

  if (loading) {
    return <div className="watchlist-loading">Loading market data...</div>;
  }

  return (
    <div className="watchlist">
     

      {/* Stock List */}
      <div className="stock-list">
        {stocks.map((stock, i) => (
          <div key={i} className="stock-row">
            <div className="stock-info">
              <div className="stock-symbol">{stock.symbol}</div>
              <div className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
                {stock.change >= 0 ? '+' : ''}{formatNumber(stock.change)}
              </div>
            </div>
            <div className="stock-price">{formatNumber(stock.price)}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Watchlist;
