import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Navbar.css';

const stocks = [
  { symbol: 'AAPL', change: 1.25, up: true },
  { symbol: 'MSFT', change: 0.85, up: true },
  { symbol: 'TSLA', change: 2.15, up: false },
  { symbol: 'GOOGL', change: 0.42, up: true },
  { symbol: 'AMZN', change: 1.73, up: true },
  { symbol: 'NVDA', change: 3.21, up: true },
  { symbol: 'META', change: 0.67, up: true },
  { symbol: 'NFLX', change: 1.92, up: false },
  { symbol: 'AMD', change: 2.45, up: true },
  { symbol: 'INTC', change: 0.33, up: false },
];

const Navbar = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const getInitials = (name) => {
    if (!name) return '';
    const names = name.split(' ');
    return names.map(n => n[0]).join('').toUpperCase();
  };

  return (
    <>
    {/* Stock Ticker */}
      <div className="stock-ticker-container">
        <div className="stock-ticker">
          {stocks.map((stock, index) => (
            <span 
              key={index} 
              className={`stock-item ${stock.up ? 'up' : 'down'}`}
            >
              {stock.symbol} {stock.change}%
            </span>
          ))}
          {/* Duplicate for seamless looping */}
          {stocks.map((stock, index) => (
            <span 
              key={`dup-${index}`} 
              className={`stock-item ${stock.up ? 'up' : 'down'}`}
            >
              {stock.symbol} {stock.change}%
            </span>
          ))}
        </div>
      </div>
      <nav className="navbar">
        <div className="navbar-brand">
          <Link to="/">Stock Sentinel</Link>
        </div>
        <div className="navbar-links">
          {isAuthenticated ? (
            <>
              <Link to="/dashboard">Dashboard</Link>
              <Link to="/holdings">Positions</Link>
              <Link to="/orders">Orders</Link>
              <Link to="/positions">Holdings</Link>
              <Link to="/funds">Funds</Link>
              <Link to="/app">App</Link>
              
              <div className="profile-containerr">
                <Link to="/profile" className="profile-circle">
                  {getInitials(user?.name)}
                </Link>
                <span className="welcome-message">Welcome, {user?.name}</span>
              </div>
              
              <button onClick={handleLogout} className="logout-buttonn">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login">Login</Link>
              <Link to="/register">Sign Up</Link>
            </>
          )}
        </div>
      </nav>
      
      
    </>
  );
};

export default Navbar;
