import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './AIPrediction.css';

// Multiple API endpoints as fallbacks
const DATA_SOURCES = {
  price: [
    {
      url: `https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol={symbol}&apikey=OH0ZQBRUAB4ABC4X`,
      parser: (data) => data['Global Quote'] ? {
        current: data['Global Quote']['05. price'],
        change: data['Global Quote']['09. change'],
        changePercent: data['Global Quote']['10. change percent']
      } : null
    },
    {
      url: `https://api.polygon.io/v2/aggs/ticker/{symbol}/prev?adjusted=true&apiKey=TpJTOM9gDn4q_3d1L0Gyp4mFlap7OSLh`,
      parser: (data) => data.results ? {
        current: data.results[0].c,
        change: (data.results[0].c - data.results[0].o).toFixed(2),
        changePercent: ((data.results[0].c - data.results[0].o) / data.results[0].o * 100).toFixed(2) + '%'
      } : null
    },
    {
      url: `https://api.twelvedata.com/quote?symbol={symbol}&apikey=689a986b8a534491b05fff638cd7f1b1`,
      parser: (data) => data ? {
        current: data.close,
        change: (data.close - data.open).toFixed(2),
        changePercent: data.percent_change
      } : null
    }
  ],
  historical: [
    {
      url: `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY&symbol={symbol}&outputsize=compact&apikey=OH0ZQBRUAB4ABC4X`,
      parser: (data) => data['Time Series (Daily)'] ? Object.entries(data['Time Series (Daily)']).slice(0, 5).map(([date, values]) => ({
        date,
        close: values['4. close'],
        volume: values['5. volume']
      })) : null
    },
    {
      url: `https://api.polygon.io/v2/aggs/ticker/{symbol}/range/1/day/{from}/{to}?adjusted=true&sort=asc&limit=5&apiKey=TpJTOM9gDn4q_3d1L0Gyp4mFlap7OSLh`,
      parser: (data) => data.results ? data.results.map(item => ({
        date: new Date(item.t).toISOString().split('T')[0],
        close: item.c,
        volume: item.v
      })) : null
    }
  ],
  news: [
    {
      url: `https://finnhub.io/api/v1/company-news?symbol={symbol}&from={from}&to={to}&token=d1aopp9r01qjhvtqeebgd1aopp9r01qjhvtqeec0`,
      parser: (data) => Array.isArray(data) ? data.filter(item => item.headline && item.image && item.url).slice(0, 5) : null
    },
    {
      url: `https://newsapi.org/v2/everything?q={symbol}&from={from}&sortBy=publishedAt&apiKey=2a9b9b9b9b9b9b9b9b9b9b9b9b9b9b9b`,
      parser: (data) => data.articles ? data.articles.filter(item => item.title && item.urlToImage && item.url).slice(0, 5) : null
    }
  ]
};

const AIPrediction = () => {
  const { symbol } = useParams();
  const navigate = useNavigate();
  const [state, setState] = useState({
    prediction: null,
    sentiment: null,
    loading: true,
    error: null,
    price: null,
    historical: [],
    news: [],
    technicals: {},
    marketSentiment: null
  });

  // Robust data fetcher with fallbacks
  const fetchWithFallbacks = async (type, params) => {
    const fromDate = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
    const toDate = new Date().toISOString().split('T')[0];
    
    for (const source of DATA_SOURCES[type]) {
      try {
        const url = source.url
          .replace('{symbol}', params.symbol)
          .replace('{from}', fromDate)
          .replace('{to}', toDate);
        
        const response = await axios.get(url);
        const parsedData = source.parser(response.data);
        
        if (parsedData) {
          return parsedData;
        }
      } catch (err) {
        console.warn(`Failed to fetch from ${source.url}:`, err);
        continue;
      }
    }
    throw new Error(`All ${type} sources failed`);
  };

  // Enhanced data fetching with caching
  const fetchAllData = async (symbol) => {
    try {
      setState(prev => ({ ...prev, loading: true, error: null }));
      
      const [priceData, historicalData, newsData, technicals, marketSentiment] = await Promise.all([
        fetchWithFallbacks('price', { symbol }),
        fetchWithFallbacks('historical', { symbol }),
        fetchWithFallbacks('news', { symbol }),
        fetchTechnicalIndicators(symbol),
        fetchMarketSentiment()
      ]);

      // Calculate enhanced sentiment
      const sentimentAnalysis = calculateEnhancedSentiment(
        priceData.changePercent,
        newsData,
        technicals,
        marketSentiment,
        historicalData
      );

      // Prepare data for AI prediction
      const aiPrompt = prepareAIPrompt(symbol, priceData, historicalData, newsData, technicals, marketSentiment, sentimentAnalysis);
      
      // Generate AI prediction
      const prediction = await generateAIPrediction(aiPrompt);
      
      setState({
        prediction,
        sentiment: sentimentAnalysis,
        loading: false,
        error: null,
        price: priceData,
        historical: historicalData,
        news: newsData,
        technicals,
        marketSentiment
      });
      
    } catch (err) {
      console.error("Error fetching data:", err);
      setState(prev => ({ ...prev, loading: false, error: 'Failed to load data. Please try again later.' }));
    }
  };

  // Enhanced technical indicators
  const fetchTechnicalIndicators = async (symbol) => {
    try {
      // Try multiple sources
      const [rsi, sma, macd] = await Promise.all([
        fetchRSI(symbol),
        fetchSMA(symbol),
        fetchMACD(symbol)
      ]);
      
      return {
        rsi: rsi?.value || null,
        sma: sma?.value || null,
        macd: macd?.value || null
      };
    } catch (err) {
      console.error("Error fetching technical indicators:", err);
      return null;
    }
  };

  const fetchRSI = async (symbol) => {
    try {
      const response = await axios.get(
        `https://www.alphavantage.co/query?function=RSI&symbol=${symbol}&interval=daily&time_period=14&series_type=close&apikey=OH0ZQBRUAB4ABC4X`
      );
      return response.data?.TechnicalAnalysis?.RSI?.[0] || null;
    } catch (err) {
      console.warn("Alpha Vantage RSI failed, trying Twelve Data");
      try {
        const response = await axios.get(
          `https://api.twelvedata.com/rsi?symbol=${symbol}&interval=1day&outputsize=30&apikey=689a986b8a534491b05fff638cd7f1b1`
        );
        return response.data?.values?.[0] || null;
      } catch (err) {
        console.error("All RSI sources failed");
        return null;
      }
    }
  };

  // Similar implementations for fetchSMA and fetchMACD...

  // Enhanced market sentiment
  const fetchMarketSentiment = async () => {
    try {
      const [fgi, indices, vix] = await Promise.all([
        axios.get('https://api.alternative.me/fng/?limit=1'),
        axios.get(`https://www.alphavantage.co/query?function=BATCH_STOCK_QUOTES&symbols=SPY,QQQ,DIA&apikey=OH0ZQBRUAB4ABC4X`),
        axios.get(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=^VIX&apikey=OH0ZQBRUAB4ABC4X`)
      ]);
      
      return {
        fearGreedIndex: fgi.data?.data?.[0]?.value || null,
        spyChange: indices.data?.StockQuotes?.[0]?.change || null,
        qqqChange: indices.data?.StockQuotes?.[1]?.change || null,
        diaChange: indices.data?.StockQuotes?.[2]?.change || null,
        vix: vix.data['Global Quote']?.['05. price'] || null
      };
    } catch (err) {
      console.error("Error fetching market sentiment:", err);
      return null;
    }
  };

  // Comprehensive sentiment analysis
  const calculateEnhancedSentiment = (priceChange, newsItems, technicals, marketSentiment, historicalData) => {
    // Base sentiment from price change (-1 to 1)
    const priceChangeNum = parseFloat(priceChange) || 0;
    let sentimentScore = Math.min(1, Math.max(-1, priceChangeNum / 5));
    
    // Technical indicators influence
    if (technicals?.rsi) {
      const rsi = parseFloat(technicals.rsi);
      const rsiInfluence = rsi < 30 ? 0.2 : rsi > 70 ? -0.2 : 0;
      sentimentScore += rsiInfluence;
    }
    
    // Market sentiment influence
    if (marketSentiment) {
      const fgi = parseFloat(marketSentiment.fearGreedIndex) || 50;
      const fgiInfluence = (fgi - 50) / 100;
      sentimentScore += fgiInfluence * 0.3;
      
      const spyChange = parseFloat(marketSentiment.spyChange) || 0;
      const qqqChange = parseFloat(marketSentiment.qqqChange) || 0;
      const diaChange = parseFloat(marketSentiment.diaChange) || 0;
      const marketPerformance = (spyChange + qqqChange + diaChange) / 3;
      sentimentScore += marketPerformance / 10;
      
      // VIX (volatility index) influence
      if (marketSentiment.vix) {
        const vix = parseFloat(marketSentiment.vix);
        sentimentScore -= (vix - 20) / 50; // Higher VIX reduces sentiment
      }
    }
    
    // Historical trend influence
    if (historicalData?.length > 1) {
      const trend = parseFloat(historicalData[0].close) > parseFloat(historicalData[4].close) ? 0.1 : -0.1;
      sentimentScore += trend;
    }
    
    // Enhanced news analysis
    if (newsItems?.length > 0) {
      let newsSentiment = analyzeNewsSentiment(newsItems);
      sentimentScore = (sentimentScore * 0.5) + (newsSentiment * 0.3);
    }
    
    // Normalize and determine sentiment
    sentimentScore = Math.min(1, Math.max(-1, sentimentScore));
    
    return {
      score: sentimentScore.toFixed(2),
      label: getSentimentLabel(sentimentScore),
      confidence: getConfidenceLevel(sentimentScore)
    };
  };

  // AI prediction generation
  const generateAIPrediction = async (prompt) => {
    try {
      const response = await axios.post('https://openrouter.ai/api/v1/chat/completions', {
        model: "anthropic/claude-3-haiku",
        messages: prompt.messages,
        max_tokens: 1500
      }, {
        headers: {
          'Authorization': `Bearer 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`,
          'Content-Type': 'application/json'
        }
      });

      return cleanPredictionText(response.data.choices[0].message.content);
    } catch (err) {
      console.error("AI prediction failed:", err);
      throw err;
    }
  };

  // Load data when symbol changes
  useEffect(() => {
    if (symbol) {
      fetchAllData(symbol);
    }
  }, [symbol]);
  if (!symbol) {
    return (
      <div className="ai-prediction-container">
        <div className="empty-state">
          <div className="empty-icon">📈</div>
          <h3>No Stock Selected</h3>
          <p>Please select a stock symbol to view AI predictions</p>
          <button onClick={() => navigate('/watchlist')} className="primary-button">
            Browse Stocks
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-prediction-container">
      <div className="ai-prediction-header">
        <div className="header-content">
          <h1 className="stock-symbol">{symbol.toUpperCase()}</h1>
          {priceData && (
            <div className={`price-display ${parseFloat(priceData.change) >= 0 ? 'positive' : 'negative'}`}>
              <span className="price">${priceData.current}</span>
              <span className="change">
                {parseFloat(priceData.change) >= 0 ? '↑' : '↓'} 
                {priceData.change} ({priceData.changePercent})
              </span>
              {priceData.volume && (
                <span className="volume">Vol: {priceData.volume.toLocaleString()}</span>
              )}
            </div>
          )}
          <p className="stock-name">Advanced 24-Hour AI Prediction Analysis</p>
        </div>
        <button onClick={() => navigate(-1)} className="back-button">
          ← Back to Watchlist
        </button>
      </div>

      {loading ? (
        <div className="loading-state">
          <div className="loading-animation">
            <div className="loading-bar"></div>
            <div className="loading-bar"></div>
            <div className="loading-bar"></div>
          </div>
          <p>Analyzing comprehensive market data for {symbol.toUpperCase()}...</p>
          <p className="loading-subtext">Fetching price data, news, technical indicators, and market sentiment</p>
        </div>
      ) : error ? (
        <div className="error-state">
          <div className="error-icon">⚠️</div>
          <h3>Prediction Error</h3>
          <p>{error}</p>
          <button onClick={() => window.location.reload()} className="primary-button">
            Retry Analysis
          </button>
        </div>
      ) : (
        <>
          {prediction && (
            <div className="prediction-section">
              <div className="prediction-card">
                <div className="card-header">
                  <div className="sentiment-indicator">
                    <div className={`sentiment-dot ${sentiment.label.replace(' ', '-')}`}></div>
                    <span className="sentiment-label">
                      {sentiment.label} sentiment ({sentiment.score}) - Confidence: {sentiment.confidence}
                    </span>
                  </div>
                  <h2 className="prediction-title">Advanced AI Prediction Analysis</h2>
                </div>
                
                <div className="prediction-content">
                  {prediction.map((line, i) => (
                    <p key={i} className="prediction-line">{line}</p>
                  ))}
                </div>
                
                <div className="prediction-footer">
                  <div className="disclaimer">
                    <p><strong>Disclaimer:</strong> This AI-generated forecast is for informational purposes only and should not be considered financial advice. Predictions are based on historical data and statistical models which may not account for all market variables. Always conduct your own research before making investment decisions.</p>
                  </div>
                  <button 
                    onClick={() => generateEnhancedPrediction(symbol)} 
                    className="refresh-button"
                  >
                    ↻ Refresh with Latest Data
                  </button>
                </div>
              </div>
            </div>
          )}

          <div className="data-sections">
            <div className="technical-section">
              <h2 className="section-title">Technical Indicators</h2>
              {technicalIndicators ? (
                <div className="indicators-grid">
                  <div className="indicator-card">
                    <h3>RSI (14-day)</h3>
                    <p className="indicator-value">{technicalIndicators.rsi || 'N/A'}</p>
                    <p className="indicator-analysis">
                      {technicalIndicators.rsi > 70 ? 'Overbought' : 
                       technicalIndicators.rsi < 30 ? 'Oversold' : 'Neutral'}
                    </p>
                  </div>
                  {/* Add more indicators as needed */}
                </div>
              ) : (
                <div className="empty-indicators">
                  <p>No technical indicators available</p>
                </div>
              )}
            </div>

            
          </div>

          <div className="news-section">
            <h2 className="section-title">Latest Market News</h2>
            {news.length > 0 ? (
              <div className="news-grid">
                {news.map((item, index) => (
                  <a 
                    key={index} 
                    href={item.url} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="news-card"
                  >
                    <div className="news-image-container">
                      <img 
                        src={item.image} 
                        alt={item.headline} 
                        className="news-image"
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = 'https://via.placeholder.com/300x200?text=No+Image';
                        }}
                      />
                    </div>
                    <div className="news-content">
                      <h3 className="news-headline">{item.headline}</h3>
                      <div className="news-meta">
                        <span className="news-source">{item.source}</span>
                        <span className="news-date">
                          {item.date ? new Date(item.date).toLocaleDateString() : 'Recent'}
                        </span>
                      </div>
                      <p className="news-summary">{item.summary || 'Read more...'}</p>
                    </div>
                  </a>
                ))}
              </div>
            ) : (
              <div className="empty-news">
                <p>No recent news found for {symbol.toUpperCase()}</p>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default AIPrediction;
