// src/components/AISummary/AISummary.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AISummary.css';
import authService from '../../api/auth';

const AISummary = ({ symbols = [] }) => {
  const [summary, setSummary] = useState('');
  const [sentiment, setSentiment] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchAISummary = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Call your backend API which will call OpenAI
      const response = await axios.post('/api/ai/summary', {
        symbols: symbols.join(',')
      }, {
        headers: authService.getAuthHeader()
      });

      if (response.data.success) {
        setSummary(response.data.summary);
        setSentiment(response.data.sentiment);
      } else {
        throw new Error(response.data.message || 'Failed to get AI analysis');
      }
    } catch (err) {
      console.error('Error fetching AI summary:', err);
      setError('Failed to load AI analysis. Please try again later.');
      
      // Fallback mock data
      if (symbols.length > 0) {
        setSummary(`Today's market showed mixed performance for ${symbols.join(', ')}. Technical indicators suggest ${symbols[0]} may see increased volatility.`);
        setSentiment(`News sentiment for ${symbols.join(', ')} is generally positive, with analysts expecting moderate growth in the sector.`);
      } else {
        setSummary("Market overview unavailable - no symbols selected");
        setSentiment("Sentiment analysis unavailable - no symbols selected");
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (symbols.length > 0) {
      fetchAISummary();
    } else {
      setSummary("Select stocks in your watchlist to get AI analysis");
      setSentiment("");
    }
  }, [symbols]);

  return (
    <div className="ai-summary">
      <h3>AI Market Analysis</h3>
      
      {loading ? (
        <div className="loading">Generating AI analysis...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <>
          <div className="summary-section">
            <h4>Today's Stock Movement Summary</h4>
            <p>{summary}</p>
          </div>
          
          {sentiment && (
            <div className="sentiment-section">
              <h4>News Sentiment & Public Reaction</h4>
              <p>{sentiment}</p>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default AISummary;
