import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';

const Positions = () => {
  const { user, isAuthenticated } = useAuth();
  const [positions, setPositions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [marketPrices, setMarketPrices] = useState({});
  const [socketStatus, setSocketStatus] = useState('disconnected');
  const wsRef = useRef(null);
  const reconnectAttempts = useRef(0);
  const maxReconnectAttempts = 5;
  const reconnectInterval = useRef(null);
  const priceUpdateQueue = useRef({});
  const debounceTimer = useRef(null);

  // Debounce function to prevent too frequent updates
  const debouncePriceUpdates = () => {
    if (debounceTimer.current) {
      clearTimeout(debounceTimer.current);
    }
    debounceTimer.current = setTimeout(() => {
      setMarketPrices(prev => ({ ...prev, ...priceUpdateQueue.current }));
      priceUpdateQueue.current = {};
    }, 500); // Update every 500ms max
  };

  // WebSocket connection management
  const setupWebSocket = () => {
    if (wsRef.current) {
      wsRef.current.close();
    }

    setSocketStatus('connecting');
    const ws = new WebSocket('wss://ws.finnhub.io?token=YOUR_FINNHUB_API_KEY');
    wsRef.current = ws;

    ws.onopen = () => {
      reconnectAttempts.current = 0;
      setSocketStatus('connected');
      setError(null);
      
      // Subscribe to symbols from positions
      const symbols = positions.map(p => p.symbol);
      if (symbols.length > 0) {
        ws.send(JSON.stringify({
          type: 'subscribe',
          symbol: symbols.map(sym => `BSE:${sym}`)
        }));
      }
    };

    ws.onmessage = (e) => {
      try {
        const data = JSON.parse(e.data);
        if (data.type === 'trade') {
          const symbol = data.symbol.split(':')[1];
          const price = data.p;
          
          // Queue the price update for debouncing
          priceUpdateQueue.current[symbol] = price;
          debouncePriceUpdates();
        }
      } catch (err) {
        console.error('Error processing WebSocket message:', err);
      }
    };

    ws.onerror = (err) => {
      console.error('WebSocket error:', err);
      setSocketStatus('error');
      setError('Market data connection error');
      attemptReconnect();
    };

    ws.onclose = (e) => {
      console.log('WebSocket closed:', e.code, e.reason);
      setSocketStatus('disconnected');
      if (e.code !== 1000) { // If not normal closure
        attemptReconnect();
      }
    };
  };

  const attemptReconnect = () => {
    if (reconnectAttempts.current >= maxReconnectAttempts) {
      setError('Failed to connect to market data after multiple attempts');
      return;
    }

    reconnectAttempts.current += 1;
    const delay = Math.min(5000, reconnectAttempts.current * 1000); // Exponential backoff
    
    reconnectInterval.current = setTimeout(() => {
      if (isAuthenticated && positions.length > 0) {
        setupWebSocket();
      }
    }, delay);
  };

  // Fetch initial positions data
  useEffect(() => {
    const fetchPositions = async () => {
      if (!isAuthenticated) {
        setLoading(false);
        return;
      }

      try {
        const token = JSON.parse(localStorage.getItem('user'))?.token;
        if (!token) throw new Error('No authentication token found');

        const response = await axios.get('/api/auth/user', {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        if (response.data.success) {
          const userPositions = response.data.user.positions || [];
          setPositions(userPositions);
          
          // Initialize market prices with current LTP values
          const initialPrices = {};
          userPositions.forEach(pos => {
            initialPrices[pos.symbol] = pos.ltp || pos.avgPrice;
          });
          setMarketPrices(initialPrices);

          // Setup WebSocket if we have positions
          if (userPositions.length > 0) {
            setupWebSocket();
          }
        }
      } catch (err) {
        console.error('Error fetching positions:', err);
        setError('Failed to load positions. Please try again.');
        if (user?.positions) {
          setPositions([...user.positions]);
          const initialPrices = {};
          user.positions.forEach(pos => {
            initialPrices[pos.symbol] = pos.ltp || pos.avgPrice;
          });
          setMarketPrices(initialPrices);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchPositions();

    return () => {
      // Cleanup WebSocket and intervals
      if (wsRef.current) {
        wsRef.current.close();
      }
      if (reconnectInterval.current) {
        clearTimeout(reconnectInterval.current);
      }
      if (debounceTimer.current) {
        clearTimeout(debounceTimer.current);
      }
    };
  }, [user, isAuthenticated]);

  if (loading) return <div className="loading">Loading positions...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="positions">
      <div className="connection-status">
        Market Data: {socketStatus === 'connected' ? 'ðŸ”´ Live' : 'Disconnected'}
      </div>
      
      <h2>Your Positions</h2>
      {positions && positions.length > 0 ? (
        <div className="table-responsive">
          <table className="positions-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Instrument</th>
                <th>Qty.</th>
                <th>Avg.</th>
                <th>LTP</th>
                <th>P&L</th>
                <th>Chg.</th>
              </tr>
            </thead>
            <tbody>
              {positions.map((position) => {
                const currentPrice = marketPrices[position.symbol] || position.ltp || position.avgPrice;
                const pnl = (currentPrice - position.avgPrice) * position.quantity;
                const change = position.avgPrice 
                  ? ((currentPrice - position.avgPrice) / position.avgPrice) * 100
                  : 0;
                
                return (
                  <tr key={`${position.symbol}-${position.product}`}>
                    <td>{position.product || 'CNC'}</td>
                    <td>{position.symbol || 'N/A'}</td>
                    <td>{position.quantity || 0}</td>
                    <td>â‚¹{(position.avgPrice || 0).toFixed(2)}</td>
                    <td>â‚¹{currentPrice.toFixed(2)}</td>
                    <td className={pnl >= 0 ? 'positive' : 'negative'}>
                      â‚¹{pnl.toFixed(2)}
                    </td>
                    <td className={change >= 0 ? 'positive' : 'negative'}>
                      {change.toFixed(2)}%
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="no-positions">You don't have any open positions.</p>
      )}
    </div>
  );
};

export default Positions;
