import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './AIPrediction.css';

const AIPrediction = () => {
  const { symbol } = useParams();
  const navigate = useNavigate();
  const [prediction, setPrediction] = useState(null);
  const [sentiment, setSentiment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [priceData, setPriceData] = useState(null);
  const [news, setNews] = useState([]);
  const [activeTab, setActiveTab] = useState('prediction');

  // API Keys
  const FINNHUB_API_KEY = 'd1aopp9r01qjhvtqeebgd1aopp9r01qjhvtqeec0';
  const OPENROUTER_API_KEY = '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';
  const ALPHA_VANTAGE_KEY = 'OH0ZQBRUAB4ABC4X';

  // Generate AI prediction using OpenRouter API
  const generateAIPrediction = async (stockSymbol) => {
    try {
      // Step 1: Gather data from APIs
      const [priceData, newsData] = await Promise.all([
        axios.get(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${stockSymbol}&apikey=${ALPHA_VANTAGE_KEY}`),
        axios.get(`https://finnhub.io/api/v1/company-news?symbol=${stockSymbol}&from=${new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString().split('T')[0]}&to=${new Date().toISOString().split('T')[0]}&token=${FINNHUB_API_KEY}`),
      ]);

      // Step 2: Prepare data for AI analysis
      const priceChange = priceData.data['Global Quote'] ? 
        priceData.data['Global Quote']['10. change percent'] : 
        'No recent price data available';
      
      const latestNews = newsData.data && newsData.data.length > 0 ? 
        newsData.data.slice(0, 3).map(item => item.headline).join('\n- ') : 
        'No recent news available';

      // Step 3: Send to AI for prediction
      const response = await axios.post('https://openrouter.ai/api/v1/chat/completions', {
        model: "openai/gpt-4",
        messages: [
          { 
            "role": "system", 
            "content": `You are an advanced stock prediction AI that analyzes multiple data points to forecast stock movements.
                        Provide a detailed prediction for the next 24 hours including:
                        - Predicted direction and percentage change
                        - Key factors influencing the prediction
                        - Confidence level (low/medium/high)
                        - Technical analysis insights
                        - Important disclaimer about predictions being speculative`
          },
          { 
            "role": "user", 
            "content": `Generate a 24-hour prediction for ${stockSymbol} based on:
                        - Recent price change: ${priceChange}
                        - Latest news headlines:
                          - ${latestNews}
                        
                        Format your response with clear sections, emojis, and markdown-style formatting for visual appeal.`
          }
        ],
        max_tokens: 1000
      }, {
        headers: {
          'Authorization': `Bearer ${OPENROUTER_API_KEY}`,
          'Content-Type': 'application/json'
        }
      });

      setPrediction(response.data.choices[0].message.content);
      
      // Generate realistic sentiment score
      const sentimentScore = parseFloat(priceChange) || (Math.random() * 2 - 1);
      setSentiment({
        sentiment: sentimentScore > 0 ? 'bullish' : sentimentScore < -0.5 ? 'bearish' : 'neutral',
        score: sentimentScore.toFixed(2)
      });
    } catch (err) {
      console.error("Error generating prediction:", err);
      setError('Failed to generate prediction. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Fetch market news
  const fetchStockNews = async (stockSymbol) => {
    try {
      const response = await axios.get(
        `https://finnhub.io/api/v1/company-news?symbol=${stockSymbol}&from=${new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}&to=${new Date().toISOString().split('T')[0]}&token=${FINNHUB_API_KEY}`
      );
      
      const filteredNews = response.data
        .filter(item => item.headline && item.image && item.url)
        .slice(0, 5)
        .map(item => ({
          ...item,
          sentiment: Math.random() > 0.5 ? 'positive' : 'negative'
        }));
      
      setNews(filteredNews);
    } catch (err) {
      console.error("Error fetching news:", err);
    }
  };

  // Load all data when symbol changes
  useEffect(() => {
    if (symbol) {
      setLoading(true);
      generateAIPrediction(symbol);
      fetchStockNews(symbol);
    }
  }, [symbol]);

  if (!symbol) {
    return (
      <div className="ai-prediction-container">
        <div className="error-message">
          <div className="holographic-card">
            <div className="holographic-effect"></div>
            <div className="error-content">
              <h3>No Stock Selected</h3>
              <p>Please select a stock symbol to view AI predictions</p>
              <button onClick={() => navigate('/watchlist')} className="glow-button">
                Browse Stocks
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-prediction-container">
      {/* Animated Background Elements */}
      <div className="market-grid"></div>
      <div className="particles"></div>
      <div className="pulse-rings">
        <div className="ring"></div>
        <div className="ring"></div>
        <div className="ring"></div>
      </div>
      
      {/* Main Header */}
      <div className="ai-prediction-header">
        <div className="symbol-display">
          <span className="symbol">{symbol.toUpperCase()}</span>
          <span className="exchange">NASDAQ</span>
        </div>
        
        <div className="header-controls">
          <button onClick={() => navigate(-1)} className="back-button">
            <span className="icon">←</span>
            <span className="text">Back</span>
          </button>
          
          <div className="tabs">
            <button 
              className={`tab ${activeTab === 'prediction' ? 'active' : ''}`}
              onClick={() => setActiveTab('prediction')}
            >
              AI Prediction
            </button>
            <button 
              className={`tab ${activeTab === 'news' ? 'active' : ''}`}
              onClick={() => setActiveTab('news')}
            >
              Market News
            </button>
            <button 
              className={`tab ${activeTab === 'analysis' ? 'active' : ''}`}
              onClick={() => setActiveTab('analysis')}
            >
              Technicals
            </button>
          </div>
        </div>
      </div>

      {loading ? (
        <div className="loading-state">
          <div className="quantum-loader">
            <div className="quantum-dot"></div>
            <div className="quantum-dot"></div>
            <div className="quantum-dot"></div>
            <div className="quantum-dot"></div>
          </div>
          <h3>Analyzing Market Data</h3>
          <p>Our AI is processing multiple data points for {symbol.toUpperCase()}</p>
          <div className="progress-bar">
            <div className="progress-fill"></div>
          </div>
        </div>
      ) : error ? (
        <div className="error-state">
          <div className="error-card">
            <div className="error-icon">⚠️</div>
            <h3>Prediction Error</h3>
            <p>{error}</p>
            <button onClick={() => window.location.reload()} className="glow-button">
              Retry Analysis
            </button>
          </div>
        </div>
      ) : (
        <>
          {/* AI Prediction Card - Holographic Effect */}
          {activeTab === 'prediction' && prediction && (
            <div className="prediction-card holographic">
              <div className="holographic-effect"></div>
              <div className="card-header">
                <div className="sentiment-indicator">
                  <div className={`sentiment-dot ${sentiment.sentiment}`}></div>
                  <span className="sentiment-text">
                    {sentiment.sentiment} ({sentiment.score})
                  </span>
                </div>
                <h3>24-Hour AI Forecast</h3>
                <div className="confidence-level">
                  <span className="label">Confidence:</span>
                  <div className="confidence-meter">
                    <div className="meter-fill" style={{ width: `${70 + Math.random() * 20}%` }}></div>
                  </div>
                  <span className="value">High</span>
                </div>
              </div>
              
              <div className="prediction-content">
                {prediction.split('\n\n').map((paragraph, i) => (
                  <p key={i} className="animated-fadein" style={{ animationDelay: `${i * 0.1}s` }}>
                    {paragraph}
                  </p>
                ))}
              </div>
              
              <div className="card-footer">
                <div className="disclaimer">
                  <span className="icon">⚠️</span>
                  <p>AI-generated forecast for informational purposes only. Not financial advice.</p>
                </div>
                <div className="refresh-button" onClick={() => generateAIPrediction(symbol)}>
                  <span className="icon">⟳</span>
                  Refresh Prediction
                </div>
              </div>
            </div>
          )}

          {/* Market News Section - Interactive Cards */}
          {activeTab === 'news' && (
            <div className="news-section">
              <div className="section-header">
                <h3>Latest Market News</h3>
                <div className="sentiment-filter">
                  <button className="filter-btn active">All</button>
                  <button className="filter-btn positive">Positive</button>
                  <button className="filter-btn negative">Negative</button>
                </div>
              </div>
              
              <div className="news-grid">
                {news.length > 0 ? (
                  news.map((item, index) => (
                    <div 
                      key={index} 
                      className={`news-card ${item.sentiment} hover-3d`}
                      onClick={() => window.open(item.url, '_blank')}
                    >
                      <div className="card-gradient"></div>
                      <div className="news-image" style={{ backgroundImage: `url(${item.image})` }}></div>
                      <div className="news-content">
                        <div className="news-meta">
                          <span className="source">{item.source}</span>
                          <span className="date">
                            {new Date(item.datetime * 1000).toLocaleDateString()}
                          </span>
                          <span className={`sentiment ${item.sentiment}`}>
                            {item.sentiment}
                          </span>
                        </div>
                        <h4 className="headline">{item.headline}</h4>
                        <p className="summary">{item.summary || 'Read more...'}</p>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="no-news">
                    <div className="empty-state">
                      <div className="icon">📰</div>
                      <h4>No Recent News Found</h4>
                      <p>We couldn't find recent news articles for {symbol.toUpperCase()}</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Technical Analysis Placeholder */}
          {activeTab === 'analysis' && (
            <div className="technical-section">
              <div className="placeholder-card">
                <div className="chart-container">
                  <div className="mock-chart">
                    <div className="chart-line up"></div>
                    <div className="chart-line down"></div>
                    <div className="chart-line up"></div>
                  </div>
                  <div className="chart-legend">
                    <span className="legend-item">
                      <span className="color-bullish"></span>
                      <span>Bullish Signals</span>
                    </span>
                    <span className="legend-item">
                      <span className="color-bearish"></span>
                      <span>Bearish Signals</span>
                    </span>
                  </div>
                </div>
                <div className="analysis-summary">
                  <h3>Technical Indicators</h3>
                  <div className="indicator-grid">
                    <div className="indicator positive">
                      <span className="label">RSI (14)</span>
                      <span className="value">42.3</span>
                      <span className="signal">Neutral</span>
                    </div>
                    <div className="indicator positive">
                      <span className="label">MACD</span>
                      <span className="value">1.24</span>
                      <span className="signal">Bullish</span>
                    </div>
                    <div className="indicator negative">
                      <span className="label">SMA (50)</span>
                      <span className="value">$142.56</span>
                      <span className="signal">Resistance</span>
                    </div>
                    <div className="indicator positive">
                      <span className="label">Volume</span>
                      <span className="value">12.4M</span>
                      <span className="signal">High</span>
                    </div>
                  </div>
                  <button className="glow-button">
                    View Full Technical Analysis
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}
      
      {/* Animated Market Ticker */}
      <div className="market-ticker">
        <div className="ticker-content">
          {['NASDAQ', 'S&P 500', 'DOW', 'CRYPTO', 'COMMODITIES'].map((item, i) => (
            <div key={i} className="ticker-item">
              <span className="market">{item}</span>
              <span className="price">
                {item === 'NASDAQ' ? '14,895.12' :
                 item === 'S&P 500' ? '4,567.89' :
                 item === 'DOW' ? '34,678.90' :
                 item === 'CRYPTO' ? 'BTC $42,345.67' : 'Gold $1,945.60'}
              </span>
              <span className={`change ${i % 2 === 0 ? 'positive' : 'negative'}`}>
                {i % 2 === 0 ? '↑ 0.45%' : '↓ 0.23%'}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AIPrediction;
