import React, { useState, useEffect } from 'react';
import './Watchlist.css';

const Watchlist = () => {
  const FINNHUB_API_KEY = 'YOUR_FINNHUB_API_KEY'; // Replace with your Finnhub API key
  const [marketIndices, setMarketIndices] = useState([
    { name: 'NIFTY', number: '50', value: 10865.65, change: -1.37, changePercent: -1.37 },
    { name: 'SENSEX', number: '', value: 35400.61, change: 1.41, changePercent: 1.41 }
  ]);

  const [stocks, setStocks] = useState([
    { symbol: 'RELIANCE', price: 2456.75, change: 12.50 },
    { symbol: 'HDFCBANK', price: 1567.30, change: 8.75 },
    { symbol: 'INFY', price: 1678.90, change: -3.20 },
    { symbol: 'TCS', price: 3452.10, change: -5.25 }
  ]);

  const [loading, setLoading] = useState(false); // WebSocket connects instantly
  const [error, setError] = useState(null);
  const [socket, setSocket] = useState(null);

  // Initialize WebSocket connection
  useEffect(() => {
    const ws = new WebSocket(`wss://ws.finnhub.io?token=${FINNHUB_API_KEY}`);
    
    ws.onopen = () => {
      console.log('WebSocket connected');
      // Subscribe to all stocks
      stocks.forEach(stock => {
        ws.send(JSON.stringify({ type: 'subscribe', symbol: `BSE:${stock.symbol}` }));
      });
      // Subscribe to indices
      ws.send(JSON.stringify({ type: 'subscribe', symbol: 'NSE:NIFTY_50' }));
      ws.send(JSON.stringify({ type: 'subscribe', symbol: 'BSE:SENSEX' }));
    };

    ws.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.type === 'trade') {
        // Update stocks
        setStocks(prevStocks => 
          prevStocks.map(stock => 
            stock.symbol === data.symbol.split(':')[1] 
              ? { 
                  ...stock, 
                  price: data.p,
                  change: data.p - (stock.price - stock.change) // Calculate new change
                } 
              : stock
          )
        );

        // Update indices
        if (data.symbol === 'NSE:NIFTY_50' || data.symbol === 'BSE:SENSEX') {
          setMarketIndices(prevIndices => 
            prevIndices.map(index => 
              index.name === (data.symbol.includes('NIFTY') ? 'NIFTY' : 'SENSEX')
                ? {
                    ...index,
                    value: data.p,
                    change: data.p - (index.value - index.change),
                    changePercent: ((data.p - (index.value - index.change)) / (index.value - index.change)) * 100
                  }
                : index
            )
          );
        }
      }
    };

    ws.onerror = (err) => {
      console.error('WebSocket error:', err);
      setError('Real-time connection failed. Data may be delayed.');
    };

    setSocket(ws);

    return () => {
      if (ws) ws.close();
    };
  }, []);

  // Initial data fetch (optional - for when WebSocket fails)
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const response = await fetch(
          `https://finnhub.io/api/v1/quote?symbol=RELIANCE&token=${FINNHUB_API_KEY}`
        );
        const data = await response.json();
        // Update initial prices if needed
      } catch (err) {
        console.error('Initial fetch failed:', err);
      }
    };
    fetchInitialData();
  }, []);

  const formatNumber = (num) => {
    return num.toLocaleString('en-IN', { maximumFractionDigits: 2 });
  };

  return (
    <div className="watchlist">
      {/* Market Indices */}
      <div className="market-indices">
        {marketIndices.map((index, i) => (
          <div key={i} className="index-card">
            <div className="index-name">{index.name}</div>
            {index.number && <div className="index-number">{index.number}</div>}
            <div className="index-value">{formatNumber(index.value)}</div>
            <div className={`index-change ${index.change >= 0 ? 'positive' : 'negative'}`}>
              {index.change >= 0 ? '+' : ''}{index.change.toFixed(2)} ({index.changePercent >= 0 ? '+' : ''}{index.changePercent.toFixed(2)}%)
            </div>
          </div>
        ))}
      </div>

      {error && (
        <div className="market-data-error">
          {error}
        </div>
      )}

      {/* Stock List */}
      <div className="stock-list">
        {stocks.map((stock, i) => (
          <div key={i} className="stock-row">
            <div className="stock-info">
              <div className="stock-symbol">{stock.symbol}</div>
              <div className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
                {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(2)}
              </div>
            </div>
            <div className="stock-price">{formatNumber(stock.price)}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Watchlist;
