// client/src/components/AISummary/AISummary.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import './AISummary.css';

const AISummary = () => {
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const location = useLocation();
  const { user } = useAuth();
  const [symbol, setSymbol] = useState('');

  useEffect(() => {
    // Get symbol from URL params if coming from Watchlist
    const searchParams = new URLSearchParams(location.search);
    const stockSymbol = searchParams.get('symbol');
    if (stockSymbol) {
      setSymbol(stockSymbol);
      fetchSummary(stockSymbol);
    }
  }, [location]);

  const fetchSummary = async (stockSymbol) => {
    setLoading(true);
    setError(null);
    try {
      const token = JSON.parse(localStorage.getItem('user'))?.token;
      if (!token) throw new Error('No authentication token found');

      const response = await axios.post('/api/ai/summary', { 
        symbol: stockSymbol 
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      setSummary(response.data.summary);
    } catch (err) {
      console.error('Error fetching AI summary:', err);
      setError(err.response?.data?.message || 'Failed to generate summary');
    } finally {
      setLoading(false);
    }
  };

  const handleRegenerate = () => {
    if (symbol) {
      fetchSummary(symbol);
    }
  };

  return (
    <div className="ai-summary-container">
      <h2>AI Stock Analysis</h2>
      {symbol && <h3>{symbol} Summary</h3>}
      
      {loading ? (
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Generating AI summary...</p>
        </div>
      ) : error ? (
        <div className="error-message">
          {error}
          <button onClick={handleRegenerate} className="retry-button">
            Try Again
          </button>
        </div>
      ) : (
        <>
          <div className="summary-content">
            {summary ? (
              <div className="summary-text" dangerouslySetInnerHTML={{ __html: summary.replace(/\n/g, '<br />') }} />
            ) : (
              <p>No summary available. Please select a stock to analyze.</p>
            )}
          </div>
          {summary && (
            <button onClick={handleRegenerate} className="regenerate-button">
              Regenerate Summary
            </button>
          )}
        </>
      )}
    </div>
  );
};

export default AISummary;
