import React, { useState, useEffect, useRef, useCallback } from 'react';
import './Watchlist.css';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { createChart } from 'lightweight-charts';

// ChartModal component with improved data fetching and error handling
const ChartModal = ({ symbol, onClose }) => {
  const chartContainerRef = useRef(null);
  const chartInstance = useRef(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeframe, setTimeframe] = useState('1D'); // Default to 1 day
  const TWELVE_DATA_API_KEY = '689a986b8a534491b05fff638cd7f1b1';

  // Cleanup chart when component unmounts
  const cleanupChart = useCallback(() => {
    if (chartInstance.current) {
      try {
        chartInstance.current.remove();
        chartInstance.current = null;
      } catch (e) {
        console.warn("Error during chart cleanup:", e);
      }
    }
    if (chartContainerRef.current) {
      chartContainerRef.current.innerHTML = '';
    }
  }, []);

  // Fetch stock data from Twelve Data API
  const fetchStockData = useCallback(async (symbol, timeframe) => {
    try {
      setLoading(true);
      setError(null);
      
      let interval, outputsize;
      switch(timeframe) {
        case '1D':
          interval = '5min';
          outputsize = 288; // 24 hours in 5min intervals
          break;
        case '1W':
          interval = '30min';
          outputsize = 336; // 7 days in 30min intervals
          break;
        case '1M':
          interval = '1day';
          outputsize = 30; // 30 days
          break;
        case '3M':
          interval = '1day';
          outputsize = 90; // 90 days
          break;
        case '1Y':
          interval = '1week';
          outputsize = 52; // 52 weeks
          break;
        default:
          interval = '1day';
          outputsize = 30;
      }

      const response = await axios.get(`https://api.twelvedata.com/time_series`, {
        params: {
          symbol: symbol,
          interval: interval,
          outputsize: outputsize,
          apikey: TWELVE_DATA_API_KEY
        }
      });
      
      if (!response.data || !response.data.values) {
        throw new Error('Invalid response from API');
      }

      const timeSeries = response.data.values;
      if (timeSeries.length === 0) {
        throw new Error('No data available');
      }

      // Process data for chart
      const chartData = timeSeries.map(item => ({
        time: new Date(item.datetime).getTime() / 1000,
        open: parseFloat(item.open),
        high: parseFloat(item.high),
        low: parseFloat(item.low),
        close: parseFloat(item.close),
        volume: parseFloat(item.volume) || 0
      })).sort((a, b) => a.time - b.time);

      return chartData;
    } catch (err) {
      console.error("Error fetching from Twelve Data:", err);
      throw err; // Re-throw to trigger fallback
    }
  }, []);

  // Fallback to Finnhub if Twelve Data fails
  const fetchFallbackData = useCallback(async (symbol, timeframe) => {
    try {
      let resolution, from;
      const to = Math.floor(Date.now() / 1000);
      
      switch(timeframe) {
        case '1D':
          resolution = '5';
          from = to - 24 * 60 * 60;
          break;
        case '1W':
          resolution = '30';
          from = to - 7 * 24 * 60 * 60;
          break;
        case '1M':
          resolution = 'D';
          from = to - 30 * 24 * 60 * 60;
          break;
        case '3M':
          resolution = 'D';
          from = to - 90 * 24 * 60 * 60;
          break;
        case '1Y':
          resolution = 'W';
          from = to - 365 * 24 * 60 * 60;
          break;
        default:
          resolution = 'D';
          from = to - 30 * 24 * 60 * 60;
      }

      const response = await axios.get(`https://finnhub.io/api/v1/stock/candle`, {
        params: {
          symbol: symbol,
          resolution: resolution,
          from: from,
          to: to,
          token: 'd17tfrhr01qteuvpuh10d17tfrhr01qteuvpuh1g'
        }
      });

      if (!response.data || !response.data.c) {
        throw new Error('Invalid response from fallback API');
      }

      // Process Finnhub data
      const chartData = response.data.t.map((time, i) => ({
        time: time,
        open: response.data.o[i],
        high: response.data.h[i],
        low: response.data.l[i],
        close: response.data.c[i],
        volume: response.data.v[i] || 0
      }));

      return chartData;
    } catch (err) {
      console.error("Error with fallback API:", err);
      throw err;
    }
  }, []);

  // Render chart with the processed data
  const renderChart = useCallback((data) => {
    cleanupChart();
    
    if (!chartContainerRef.current || data.length === 0) return;
    
    const container = chartContainerRef.current;
    container.innerHTML = '';
    
    const chart = createChart(container, {
      width: container.clientWidth,
      height: 400,
      layout: {
        backgroundColor: '#ffffff',
        textColor: '#333',
      },
      grid: {
        vertLines: {
          color: '#eee',
        },
        horzLines: {
          color: '#eee',
        },
      },
      crosshair: {
        mode: 'normal',
      },
      timeScale: {
        borderColor: '#ccc',
      },
    });
    
    chartInstance.current = chart;
    
    // Add candlestick series
    const candleSeries = chart.addCandlestickSeries({
      upColor: '#26a69a',
      downColor: '#ef5350',
      borderVisible: false,
      wickUpColor: '#26a69a',
      wickDownColor: '#ef5350',
    });
    
    candleSeries.setData(data);
    
    // Add volume series if we have volume data
    if (data[0].volume !== undefined) {
      const volumeSeries = chart.addHistogramSeries({
        color: '#26a69a',
        priceFormat: {
          type: 'volume',
        },
        priceScaleId: '',
        scaleMargins: {
          top: 0.8,
          bottom: 0,
        },
      });
      
      volumeSeries.setData(data.map(item => ({
        time: item.time,
        value: item.volume,
        color: item.close > item.open ? '#26a69a' : '#ef5350'
      })));
    }
    
    chart.timeScale().fitContent();
    
    // Handle window resize
    const handleResize = () => {
      if (chartInstance.current && chartContainerRef.current) {
        chartInstance.current.applyOptions({
          width: chartContainerRef.current.clientWidth
        });
      }
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [cleanupChart]);

  // Load data when symbol or timeframe changes
  useEffect(() => {
    if (!symbol) return;

    const loadData = async () => {
      try {
        let chartData = await fetchStockData(symbol, timeframe);
        renderChart(chartData);
      } catch (err) {
        console.log("Attempting fallback...");
        try {
          const fallbackData = await fetchFallbackData(symbol, timeframe);
          renderChart(fallbackData);
        } catch (fallbackErr) {
          console.error("Both APIs failed:", fallbackErr);
          setError('Failed to load chart data. Please try again later.');
          cleanupChart();
        }
      } finally {
        setLoading(false);
      }
    };

    loadData();

    return () => {
      cleanupChart();
    };
  }, [symbol, timeframe, fetchStockData, fetchFallbackData, renderChart, cleanupChart]);

  return (
    <div className="chart-modal">
      <div className="chart-modal-content">
        <div className="chart-modal-header">
          <h3>{symbol} Price Chart</h3>
          <div className="timeframe-selector">
            {['1D', '1W', '1M', '3M', '1Y'].map((tf) => (
              <button
                key={tf}
                className={timeframe === tf ? 'active' : ''}
                onClick={() => setTimeframe(tf)}
              >
                {tf}
              </button>
            ))}
          </div>
          <button className="close-modal" onClick={onClose}>×</button>
        </div>
        <div className="chart-modal-body">
          {loading ? (
            <div className="chart-loading">Loading chart data...</div>
          ) : error ? (
            <div className="chart-error">{error}</div>
          ) : (
            <div 
              ref={chartContainerRef} 
              className="chart-container"
              style={{ width: '100%', height: '400px' }}
            />
          )}
        </div>
      </div>
    </div>
  );
};

// Rest of the components (SummaryModal, BuySellModal, Watchlist) remain the same...
// Only the ChartModal component was modified

const SummaryModal = ({ symbol, onClose }) => {
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockSummary = async () => {
      try {
        const OPENROUTER_API_KEY = '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';
        const apiUrl = 'https://openrouter.ai/api/v1/chat/completions';

        const response = await axios.post(apiUrl, {
          model: "openai/gpt-3.5-turbo",
          messages: [
            { 
              "role": "system", 
              "content": "You are a financial analyst providing concise stock summaries. Provide key information including company overview, recent performance, and analyst sentiment. Keep it under 200 words." 
            },
            { 
              "role": "user", 
              "content": `Provide a summary analysis of ${symbol} stock. Include key metrics, recent performance, and outlook.` 
            }
          ]
        }, {
          headers: {
            'Authorization': `Bearer ${OPENROUTER_API_KEY}`,
            'Content-Type': 'application/json'
          }
        });

        setSummary(response.data.choices[0].message.content);
        setLoading(false);
      } catch (err) {
        console.error("Error generating summary:", err);
        setError('Failed to generate summary. Please try again.');
        setLoading(false);
      }
    };

    fetchStockSummary();
  }, [symbol]);

  return (
    <div className="summary-modal">
      <div className="summary-modal-content">
        <div className="summary-modal-header">
          <h3>{symbol} Stock Summary</h3>
          <button className="close-modal" onClick={onClose}>×</button>
        </div>
        <div className="summary-modal-body">
          {loading ? (
            <div className="summary-loading">Generating summary...</div>
          ) : error ? (
            <div className="summary-error">{error}</div>
          ) : (
            <div className="summary-text">
              {summary.split('\n').map((paragraph, i) => (
                <p key={i}>{paragraph}</p>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const BuySellModal = ({ 
  type, 
  symbol, 
  price, 
  onClose, 
  onSubmit, 
  walletBalance 
}) => {
  const [quantity, setQuantity] = useState(1);
  const [product, setProduct] = useState('CNC');
  const [total, setTotal] = useState(price);

  useEffect(() => {
    setTotal((quantity * price).toFixed(2));
  }, [quantity, price]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ symbol, quantity: parseInt(quantity), price, product, type });
  };

  return (
    <div className="trade-modal">
      <div className="trade-modal-content">
        <div className="trade-modal-header">
          <h3>{type} {symbol}</h3>
          <button className="close-modal" onClick={onClose}>×</button>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Price:</label>
            <input type="text" value={price} readOnly />
          </div>
          <div className="form-group">
            <label>Quantity:</label>
            <input 
              type="number" 
              min="1" 
              value={quantity} 
              onChange={(e) => setQuantity(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label>Product:</label>
            <select value={product} onChange={(e) => setProduct(e.target.value)}>
              <option value="CNC">CNC</option>
              <option value="MIS">MIS</option>
              <option value="NRML">NRML</option>
            </select>
          </div>
          <div className="form-group">
            <label>Total:</label>
            <input type="text" value={total} readOnly />
          </div>
          {type === 'BUY' && (
            <div className="wallet-info">
              <p>Wallet Balance: ₹{walletBalance.toFixed(2)}</p>
              {total > walletBalance && (
                <p className="error">Insufficient funds</p>
              )}
            </div>
          )}
          <button 
            type="submit" 
            className={`trade-button ${type.toLowerCase()}`}
            disabled={type === 'BUY' && total > walletBalance}
          >
            {type}
          </button>
        </form>
      </div>
    </div>
  );
};

const Watchlist = ({ onWatchlistUpdate }) => {
  const FINNHUB_API_KEY = 'd17tfrhr01qteuvpuh10d17tfrhr01qteuvpuh1g'; 
  const [marketIndices, setMarketIndices] = useState([]);
  const [stocks, setStocks] = useState([]);
  const ws = useRef(null);
  const [filteredStocks, setFilteredStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [socketStatus, setSocketStatus] = useState('disconnected');
  const socketRef = useRef(null);
  const [searchTerm, setSearchTerm] = useState('');
  const reconnectTimerRef = useRef(null);
  const isMountedRef = useRef(false);
  const [hoveredStock, setHoveredStock] = useState(null);
  const { user, isAuthenticated, updateWalletBalance } = useAuth();
  const [tradeModal, setTradeModal] = useState(null);
  const [walletBalance, setWalletBalance] = useState(user?.walletBalance || 0);
  const [notification, setNotification] = useState(null);
  const [summaryModal, setSummaryModal] = useState(null);
  const [chartModalSymbol, setChartModalSymbol] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (searchTerm === '') {
      setFilteredStocks(stocks);
    } else {
      const filtered = stocks.filter(stock => 
        stock.symbol.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredStocks(filtered);
    }
  }, [searchTerm, stocks]);

  const handleViewChart = (symbol, e) => {
    e.stopPropagation();
    setChartModalSymbol(symbol);
  };

  useEffect(() => {
    if (user?.walletBalance !== undefined) {
      setWalletBalance(user.walletBalance);
    }
  }, [user]);

  useEffect(() => {
    if (isAuthenticated) {
      const fetchUserData = async () => {
        try {
          const response = await fetch('/api/auth/user', {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
          });
          const data = await response.json();
          if (data.success) {
            setWalletBalance(data.user.walletBalance);
            updateWalletBalance(data.user.walletBalance);
          }
        } catch (err) {
          console.error('Error fetching user data:', err);
        }
      };
      fetchUserData();
    }
  }, [isAuthenticated, updateWalletBalance]);

  const setupWebSocket = () => {
    if (reconnectTimerRef.current) {
      clearTimeout(reconnectTimerRef.current);
      reconnectTimerRef.current = null;
    }

    if (socketRef.current) {
      socketRef.current.close();
    }

    setSocketStatus('connecting');
    const ws = new WebSocket(`wss://ws.finnhub.io?token=${FINNHUB_API_KEY}`);
    socketRef.current = ws;

    ws.onopen = () => {
      if (!isMountedRef.current) return;
      console.log('WebSocket connected');
      setSocketStatus('connected');
      setError(null);
      
      try {
        stocks.forEach(stock => {
          if (ws.readyState === WebSocket.OPEN) {
            ws.send(JSON.stringify({ type: 'subscribe', symbol: `BSE:${stock.symbol}` }));
          }
        });
        
        if (ws.readyState === WebSocket.OPEN) {
          ws.send(JSON.stringify({ type: 'subscribe', symbol: 'NSE:NIFTY_50' }));
          ws.send(JSON.stringify({ type: 'subscribe', symbol: 'BSE:SENSEX' }));
        }
      } catch (err) {
        console.error('Error sending subscriptions:', err);
        setError('Failed to subscribe to updates. Please refresh the page.');
      }
    };

    ws.onmessage = (e) => {
      if (!isMountedRef.current) return;
      try {
        const data = JSON.parse(e.data);
        if (data.type === 'trade') {
          const symbolParts = data.symbol.split(':');
          const exchange = symbolParts[0];
          const symbol = symbolParts[1];
          const price = data.p;

          if (exchange === 'BSE') {
            setStocks(prevStocks => 
              prevStocks.map(stock => 
                stock.symbol === symbol 
                  ? { 
                      ...stock, 
                      price: price,
                      change: price - (stock.price - stock.change),
                      changePercent: ((price - (stock.price - stock.change)) / (stock.price - stock.change)) * 100
                    } 
                  : stock
              )
            );
          }

          if (data.symbol === 'NSE:NIFTY_50' || data.symbol === 'BSE:SENSEX') {
            setMarketIndices(prevIndices => 
              prevIndices.map(index => 
                index.name === (data.symbol.includes('NIFTY') ? 'NIFTY' : 'SENSEX')
                  ? {
                      ...index,
                      value: price,
                      change: price - (index.value - index.change),
                      changePercent: ((price - (index.value - index.change)) / (index.value - index.change)) * 100
                    }
                  : index
              )
            );
          }
        }
      } catch (err) {
        console.error('Error processing WebSocket message:', err);
      }
    };

    ws.onerror = (err) => {
      if (!isMountedRef.current) return;
      console.error('WebSocket error:', err);
      setSocketStatus('error');
      setError('Connection error. Attempting to reconnect...');
      scheduleReconnect();
    };

    ws.onclose = (e) => {
      if (!isMountedRef.current) return;
      console.log('WebSocket disconnected', e.code, e.reason);
      setSocketStatus('disconnected');
      
      if (e.code !== 1000) {
        scheduleReconnect();
      }
    };
  };

  const scheduleReconnect = () => {
    if (!isMountedRef.current) return;
    if (reconnectTimerRef.current) return;
    
    reconnectTimerRef.current = setTimeout(() => {
      if (!isMountedRef.current) return;
      console.log('Attempting to reconnect...');
      setupWebSocket();
    }, 3000);
  };

  useEffect(() => {
    isMountedRef.current = true;
    
    if (loading || stocks.length === 0) return;
    
    setupWebSocket();

    return () => {
      isMountedRef.current = false;
      
      if (socketRef.current) {
        try {
          stocks.forEach(stock => {
            socketRef.current.send(JSON.stringify({ type: 'unsubscribe', symbol: `BSE:${stock.symbol}` }));
          });
          socketRef.current.send(JSON.stringify({ type: 'unsubscribe', symbol: 'NSE:NIFTY_50' }));
          socketRef.current.send(JSON.stringify({ type: 'unsubscribe', symbol: 'BSE:SENSEX' }));
          
          socketRef.current.close(1000, 'Component unmounting');
        } catch (err) {
          console.error('Error during WebSocket cleanup:', err);
        }
      }
      
      if (reconnectTimerRef.current) {
        clearTimeout(reconnectTimerRef.current);
      }
    };
  }, [loading, stocks]);

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const niftyResponse = await fetch(
          `https://finnhub.io/api/v1/quote?symbol=BSESN&token=${FINNHUB_API_KEY}`
        );
        
        const sensexResponse = await fetch(
          `https://finnhub.io/api/v1/quote?symbol=BSE:SENSEX&token=${FINNHUB_API_KEY}`
        );
        
        const niftyData = await niftyResponse.json();
        const sensexData = await sensexResponse.json();

        setMarketIndices([
          { 
            name: 'NIFTY', 
            number: '50', 
            value: niftyData.c, 
            change: niftyData.d, 
            changePercent: niftyData.dp 
          },
          { 
            name: 'SENSEX', 
            number: '', 
            value: sensexData.c, 
            change: sensexData.d, 
            changePercent: sensexData.dp 
          }
        ]);

        const stockSymbols = [
          'AAPL', 'MSFT', 'PYPL', 'BABA', 'MSTR', 
          'GOOGL', 'AMZN', 'META', 'TSLA', 'NVDA', 
          'INTC', 'AMD', 'DIS', 'NFLX', 'ASML', 
          'QCOM', 'F', 'GM', 'HD', 'COST'
        ];
        
        const stockPromises = stockSymbols.map(symbol => 
          fetch(`https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`)
        );

        const stockResponses = await Promise.all(stockPromises);
        const stockData = await Promise.all(stockResponses.map(res => res.json()));

        const initialStocks = stockSymbols.map((symbol, index) => ({
          symbol,
          price: stockData[index].c,
          change: stockData[index].d,
          changePercent: stockData[index].dp
        }));

        setStocks(initialStocks);
        setFilteredStocks(initialStocks);
        setLoading(false);
      } catch (err) {
        console.error('Initial fetch failed:', err);
        setError('Failed to load initial data. Trying real-time connection...');
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  const formatNumber = (num) => {
    if (num === undefined || num === null) return '--';
    return num.toLocaleString('en-IN', { maximumFractionDigits: 2 });
  };

  const handleOptionClick = (action, symbol, e, price) => {
    e.stopPropagation();
    
    if (action === 'Buy' || action === 'Sell') {
      setTradeModal({
        type: action.toUpperCase(),
        symbol,
        price
      });
      return;
    }
  };

  const handleGenerateSummary = (symbol, e) => {
    e.stopPropagation();
    navigate(`/ai-summary/${symbol}`);
  };

  const handleTradeSubmit = async (tradeData) => {
    try {
      const storedUser = localStorage.getItem('user');
      if (!storedUser) {
        throw new Error('User not authenticated');
      }
      
      const { token } = JSON.parse(storedUser);
      
      const response = await axios.post('/api/auth/trade', tradeData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setWalletBalance(response.data.walletBalance);
        updateWalletBalance(response.data.walletBalance);
        setNotification({
          type: 'success',
          message: `${tradeData.type} order for ${tradeData.symbol} executed successfully!`
        });
        setTimeout(() => setNotification(null), 3000);
        setTradeModal(null);
      }
    } catch (err) {
      console.error('Trade error:', err);
      setNotification({
        type: 'error',
        message: err.response?.data?.message || 'Failed to execute trade. Please try again.'
      });
      setTimeout(() => setNotification(null), 3000);
    }
  };

  if (loading) {
    return <div className="watchlist-loading">Loading market data...</div>;
  }

  return (
    <div className="watchlist">
      {notification && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}

      {tradeModal && (
        <BuySellModal
          type={tradeModal.type}
          symbol={tradeModal.symbol}
          price={tradeModal.price}
          onClose={() => setTradeModal(null)}
          onSubmit={handleTradeSubmit}
          walletBalance={walletBalance}
        />
      )}

      {summaryModal && (
        <SummaryModal 
          symbol={summaryModal} 
          onClose={() => setSummaryModal(null)} 
        />
      )}

      {chartModalSymbol && (
        <ChartModal 
          symbol={chartModalSymbol} 
          onClose={() => setChartModalSymbol(null)} 
        />
      )}

      <div className={`connection-status ${socketStatus}`}>
        {socketStatus === 'connected' ? '🔴 Live' : 
         socketStatus === 'connecting' ? 'Connecting...' : 
         socketStatus === 'error' ? 'Connection Error' : 'Disconnected'}
      </div>

      <div className="watchlist-search">
        <input
          type="text"
          placeholder="Search stocks..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      {error && (
        <div className="market-data-error">
          {error}
        </div>
      )}

      <div className="stock-list">
        {filteredStocks.length > 0 ? (
          filteredStocks.map((stock, i) => (
            <div 
              key={i} 
              className="stock-row"
              onMouseEnter={() => setHoveredStock(i)}
              onMouseLeave={() => setHoveredStock(null)}
            >
              <div className="stock-info">
                <div className="stock-symbol">{stock.symbol}</div>
                <div className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
                  {stock.change >= 0 ? '+' : ''}{formatNumber(stock.change)}
                </div>
              </div>
              <div className="stock-price">{formatNumber(stock.price)}</div>
              
              {hoveredStock === i && (
                <div className="stock-options-tooltip" onMouseEnter={() => setHoveredStock(i)}>
                  <button 
                    className="option-button buy" 
                    onClick={(e) => handleOptionClick('Buy', stock.symbol, e, stock.price)}
                  >
                    Buy
                  </button>
                  <button 
                    className="option-button sell" 
                    onClick={(e) => handleOptionClick('Sell', stock.symbol, e, stock.price)}
                  >
                    Sell
                  </button>
                  <button 
                    className="option-button chart" 
                    onClick={(e) => handleViewChart(stock.symbol, e)}
                  >
                    Chart
                  </button>
                  <button 
                    className="option-button summary" 
                    onClick={(e) => handleGenerateSummary(stock.symbol, e)}
                  >
                    Summary
                  </button>
                </div>
              )}
            </div>
          ))
        ) : (
          <div className="no-results">No stocks found matching your search</div>
        )}
      </div>
    </div>
  );
};

export default Watchlist;
