// client/src/components/Orders/Orders.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';

const Orders = () => {
  const { user, isAuthenticated } = useAuth();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        if (isAuthenticated) {
          // If user.orders exists, use it
          if (user?.orders) {
            setOrders(user.orders.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp)));
          } else {
            // Otherwise, fetch fresh data
            const response = await fetch('/api/auth/user', {
              headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
              }
            });
            const data = await response.json();
            if (data.success) {
              setOrders(data.user.orders.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp)));
            }
          }
        }
      } catch (err) {
        console.error('Error fetching orders:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, [user, isAuthenticated]);

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return <div>Loading orders...</div>;
  }

  return (
    <div className="orders">
      <h2>Your Orders</h2>
      {orders.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Time</th>
              <th>Type</th>
              <th>Instrument</th>
              <th>Product</th>
              <th>Qty.</th>
              <th>Avg. Price</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order, index) => (
              <tr key={index}>
                <td>{formatDate(order.timestamp)}</td>
                <td className={order.type.toLowerCase()}>{order.type}</td>
                <td>{order.symbol}</td>
                <td>{order.product}</td>
                <td>{order.quantity}</td>
                <td>â‚¹{order.price.toFixed(2)}</td>
                <td>{order.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>You don't have any orders yet.</p>
      )}
    </div>
  );
};

export default Orders;
