import React, { useState, useEffect } from 'react';
import './Watchlist.css';

const Watchlist = () => {
  const API_KEY = 'ZD2AXKMSZ9EDJO5D';
  const [marketIndices, setMarketIndices] = useState([
    { name: 'NIFTY', number: '50', value: 10865.65, change: -1.37, changePercent: -1.37 },
    { name: 'SENSEX', number: '', value: 35400.61, change: 1.41, changePercent: 1.41 }
  ]);

  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [marketDataError, setMarketDataError] = useState(null);

  useEffect(() => {
    const fetchMarketData = async () => {
  try {
    const getQuote = async (symbol) => {
      const res = await fetch(
        `https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${symbol}&apikey=${API_KEY}`
      );
      const data = await res.json();

      if (data.Note || data.Information || !data['Global Quote']) {
        console.warn(`API warning for ${symbol}:`, data);
        throw new Error('Invalid API response structure');
      }

      const quote = data['Global Quote'];

      return {
        value: parseFloat(quote['05. price']) || 0,
        change: parseFloat(quote['09. change']) || 0,
        changePercent: parseFloat((quote['10. change percent'] || '0%').replace('%', '')) || 0
      };
    };

    const [nifty, sensex] = await Promise.all([
      getQuote('^NSEI'),  // NIFTY
      getQuote('^BSESN')  // SENSEX
    ]);

    setMarketIndices([
      { name: 'NIFTY', number: '50', ...nifty },
      { name: 'SENSEX', number: '', ...sensex }
    ]);

    setMarketDataError(null);
  } catch (error) {
    console.error('Error fetching market data:', error.message);
    setMarketDataError('Failed to load live market data. Using cached values.');
  }
};

    const fetchStocks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/stocks');
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        setStocks(data);
        setError(null);
      } catch (error) {
        console.error('Error fetching stocks:', error);
        setError(error.message);
        // Fallback mock data
        setStocks([
          { symbol: 'HDFCBANK', price: 1567.30, change: 8.75 },
          { symbol: 'INFY', price: 1678.90, change: -3.20 },
          { symbol: 'RELIANCE', price: 2456.75, change: 12.50 },
          { symbol: 'TCS', price: 3452.10, change: -5.25 }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchMarketData();
    fetchStocks();

    // Set up polling for real-time updates (every 5 minutes)
    const interval = setInterval(() => {
      fetchMarketData();
      fetchStocks();
    }, 300000);

    return () => clearInterval(interval);
  }, []);

  const formatNumber = (num) => {
    return num.toLocaleString('en-IN', { maximumFractionDigits: 2 });
  };

  if (loading) return <div className="watchlist-loading">Loading market data...</div>;

  return (
    <div className="watchlist">
      {/* Market Indices */}
      <div className="market-indices">
        {marketIndices.map((index, i) => (
          <div key={i} className="index-card">
            <div className="index-name">{index.name}</div>
            {index.number && <div className="index-number">{index.number}</div>}
            <div className="index-value">{formatNumber(index.value)}</div>
            <div className={`index-change ${index.change >= 0 ? 'positive' : 'negative'}`}>
              {index.change >= 0 ? '+' : ''}{index.change.toFixed(2)} ({index.changePercent >= 0 ? '+' : ''}{index.changePercent.toFixed(2)}%)
            </div>
          </div>
        ))}
      </div>

      {marketDataError && (
        <div className="market-data-error">
          {marketDataError}
        </div>
      )}

      {/* Stock List */}
      <div className="stock-list">
        {error && (
          <div className="watchlist-error">
            {error} (Using mock data)
            <button onClick={() => window.location.reload()}>Retry</button>
          </div>
        )}
        
        {stocks.map((stock, i) => (
          <div key={i} className="stock-row">
            <div className="stock-info">
              <div className="stock-symbol">{stock.symbol}</div>
              <div className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
                {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(2)}
              </div>
            </div>
            <div className="stock-price">{formatNumber(stock.price)}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Watchlist;
