import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './AIPrediction.css';

const AIPrediction = () => {
  const { symbol } = useParams();
  const navigate = useNavigate();
  const [prediction, setPrediction] = useState(null);
  const [sentiment, setSentiment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [news, setNews] = useState([]);

  // API Keys
  const FINNHUB_API_KEY = 'd1aopp9r01qjhvtqeebgd1aopp9r01qjhvtqeec0';
  const OPENROUTER_API_KEY = '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';
  const ALPHA_VANTAGE_KEY = 'OH0ZQBRUAB4ABC4X';

  // Clean AI response by removing markdown hashes
  const cleanPredictionText = (text) => {
    return text
      .replace(/^#+\s*/gm, '') // Remove markdown headers
      .replace(/\*\*(.*?)\*\*/g, '$1') // Remove bold markdown
      .split('\n')
      .filter(line => line.trim() !== '') // Remove empty lines
      .map(line => line.trim());
  };

  // Generate AI prediction using OpenRouter API
  const generateAIPrediction = async (stockSymbol) => {
    try {
      // Step 1: Gather data from APIs
      const [priceData, newsData] = await Promise.all([
        axios.get(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${stockSymbol}&apikey=${ALPHA_VANTAGE_KEY}`),
        axios.get(`https://finnhub.io/api/v1/company-news?symbol=${stockSymbol}&from=${new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString().split('T')[0]}&to=${new Date().toISOString().split('T')[0]}&token=${FINNHUB_API_KEY}`),
      ]);

      // Step 2: Prepare data for AI analysis
      const priceChange = priceData.data['Global Quote'] ? 
        priceData.data['Global Quote']['10. change percent'] : 
        'No recent price data available';
      
      const latestNews = newsData.data && newsData.data.length > 0 ? 
        newsData.data.slice(0, 3).map(item => item.headline).join('\n- ') : 
        'No recent news available';

      // Step 3: Send to AI for prediction
      const response = await axios.post('https://openrouter.ai/api/v1/chat/completions', {
        model: "openai/gpt-3.5-turbo",
        messages: [
          { 
            "role": "system", 
            "content": `You are an advanced stock prediction AI that analyzes multiple data points to forecast stock movements.
                        Provide a detailed prediction for the next 24 hours including:
                        - Predicted direction and percentage change
                        - Key factors influencing the prediction
                        - Confidence level (low/medium/high)
                        - Technical analysis insights
                        - Important disclaimer about predictions being speculative
                        
                        Format your response with clear sections and bullet points for better readability.
                        Do not use markdown headers (###) or bold formatting.`
          },
          { 
            "role": "user", 
            "content": `Generate a 24-hour prediction for ${stockSymbol} based on:
                        - Recent price change: ${priceChange}
                        - Latest news headlines:
                          - ${latestNews}`
          }
        ],
        max_tokens: 1000
      }, {
        headers: {
          'Authorization': `Bearer ${OPENROUTER_API_KEY}`,
          'Content-Type': 'application/json'
        }
      });

      const cleanedPrediction = cleanPredictionText(response.data.choices[0].message.content);
      setPrediction(cleanedPrediction);
      
      // Generate more realistic sentiment score based on price change
      const priceChangeNum = parseFloat(priceChange) || 0;
      const sentimentScore = Math.min(1, Math.max(-1, priceChangeNum / 5));
      setSentiment({
        sentiment: sentimentScore > 0.2 ? 'bullish' : sentimentScore < -0.2 ? 'bearish' : 'neutral',
        score: sentimentScore.toFixed(2)
      });
    } catch (err) {
      console.error("Error generating prediction:", err);
      setError('Failed to generate prediction. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Fetch market news
  const fetchStockNews = async (stockSymbol) => {
    try {
      const response = await axios.get(
        `https://finnhub.io/api/v1/company-news?symbol=${stockSymbol}&from=${new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}&to=${new Date().toISOString().split('T')[0]}&token=${FINNHUB_API_KEY}`
      );
      
      const filteredNews = response.data
        .filter(item => item.headline && item.image && item.url)
        .slice(0, 5);
      
      setNews(filteredNews);
    } catch (err) {
      console.error("Error fetching news:", err);
    }
  };

  // Load all data when symbol changes
  useEffect(() => {
    if (symbol) {
      setLoading(true);
      generateAIPrediction(symbol);
      fetchStockNews(symbol);
    }
  }, [symbol]);

  if (!symbol) {
    return (
      <div className="ai-prediction-container">
        <div className="empty-state">
          <div className="empty-icon">üìà</div>
          <h3>No Stock Selected</h3>
          <p>Please select a stock symbol to view AI predictions</p>
          <button onClick={() => navigate('/watchlist')} className="primary-button">
            Browse Stocks
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-prediction-container">
      <div className="ai-prediction-header">
        <div className="header-content">
          <h1 className="stock-symbol">{symbol.toUpperCase()}</h1>
          <p className="stock-name">24-Hour AI Prediction Analysis</p>
        </div>
        <button onClick={() => navigate(-1)} className="back-button">
          ‚Üê Back to Watchlist
        </button>
      </div>

      {loading ? (
        <div className="loading-state">
          <div className="loading-animation">
            <div className="loading-bar"></div>
            <div className="loading-bar"></div>
            <div className="loading-bar"></div>
          </div>
          <p>Analyzing market data for {symbol.toUpperCase()}...</p>
        </div>
      ) : error ? (
        <div className="error-state">
          <div className="error-icon">‚ö†Ô∏è</div>
          <h3>Prediction Error</h3>
          <p>{error}</p>
          <button onClick={() => window.location.reload()} className="primary-button">
            Retry Analysis
          </button>
        </div>
      ) : (
        <>
          {prediction && (
            <div className="prediction-section">
              <div className="prediction-card">
                <div className="card-header">
                  <div className="sentiment-indicator">
                    <div className={`sentiment-dot ${sentiment.sentiment}`}></div>
                    <span className="sentiment-label">
                      {sentiment.sentiment} sentiment ({sentiment.score})
                    </span>
                  </div>
                  <h2 className="prediction-title">AI Prediction Summary</h2>
                </div>
                
                <div className="prediction-content">
                  {prediction.map((line, i) => (
                    <p key={i} className="prediction-line">{line}</p>
                  ))}
                </div>
                
                <div className="prediction-footer">
                  <div className="disclaimer">
                    <p>Disclaimer: This AI-generated forecast is for informational purposes only and should not be considered financial advice.</p>
                  </div>
                  <button 
                    onClick={() => generateAIPrediction(symbol)} 
                    className="refresh-button"
                  >
                    ‚Üª Refresh Prediction
                  </button>
                </div>
              </div>
            </div>
          )}

          <div className="news-section">
            <h2 className="section-title">Latest Market News</h2>
            {news.length > 0 ? (
              <div className="news-grid">
                {news.map((item, index) => (
                  <a 
                    key={index} 
                    href={item.url} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="news-card"
                  >
                    <div className="news-image-container">
                      <img src={item.image} alt={item.headline} className="news-image" />
                    </div>
                    <div className="news-content">
                      <h3 className="news-headline">{item.headline}</h3>
                      <div className="news-meta">
                        <span className="news-source">{item.source}</span>
                        <span className="news-date">
                          {new Date(item.datetime * 1000).toLocaleDateString()}
                        </span>
                      </div>
                      <p className="news-summary">{item.summary || 'Read more...'}</p>
                    </div>
                  </a>
                ))}
              </div>
            ) : (
              <div className="empty-news">
                <p>No recent news found for {symbol.toUpperCase()}</p>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default AIPrediction;
