import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import './AISummary.css';

const AISummary = () => {
  const { symbol } = useParams();
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const response = await axios.post('/api/auth/ai/summary', { symbol }, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        setSummary(response.data.summary);
      } catch (err) {
        console.error('Error fetching AI summary:', err);
        setError('Failed to generate summary. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (symbol) {
      fetchSummary();
    }
  }, [symbol]);

  if (loading) {
    return <div className="ai-summary-loading">Generating AI summary for {symbol}...</div>;
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>AI Stock Analysis: {symbol}</h2>
        <button onClick={() => navigate(-1)} className="back-button">
          Back to Watchlist
        </button>
      </div>
      
      {error ? (
        <div className="ai-summary-error">{error}</div>
      ) : (
        <div className="ai-summary-content">
          {summary.split('\n').map((paragraph, index) => (
            <p key={index}>{paragraph}</p>
          ))}
        </div>
      )}
    </div>
  );
};

export default AISummary;
