import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import './AISummary.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const AISummary = () => {
  const { symbol } = useParams();
  const navigate = useNavigate();
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState('');
  const [customQueryResult, setCustomQueryResult] = useState(null);
  const [isCustomQuery, setIsCustomQuery] = useState(false);
  const [stockSearch, setStockSearch] = useState('');
  const [chartData, setChartData] = useState(null);
  const [news, setNews] = useState([]);
  const [activeTab, setActiveTab] = useState('summary');
  const [chartSummary, setChartSummary] = useState('');
  const [newsSummary, setNewsSummary] = useState('');
  const [apiLimitReached, setApiLimitReached] = useState(false);

  // Use environment variables for API keys in production
  const FINNHUB_API_KEY = process.env.REACT_APP_FINNHUB_API_KEY || 'd1aopp9r01qjhvtqeebgd1aopp9r01qjhvtqeec0';
  const OPENROUTER_API_KEY = process.env.REACT_APP_OPENROUTER_API_KEY || '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';

  // Rate-limited Finnhub API calls
  const finnhubRateLimited = {
    lastRequestTime: 0,
    minInterval: 1000, // 1 second between requests
    queue: [],
    processing: false,

    async processQueue() {
      if (this.processing || this.queue.length === 0) return;
      this.processing = true;
      
      const now = Date.now();
      const timeSinceLast = now - this.lastRequestTime;
      const delay = Math.max(0, this.minInterval - timeSinceLast);
      
      await new Promise(resolve => setTimeout(resolve, delay));
      
      const { symbol, resolve, reject } = this.queue.shift();
      try {
        const response = await axios.get(
          `https://finnhub.io/api/v1/stock/candle?symbol=${symbol}&resolution=5&from=${Math.floor(Date.now() / 1000) - 86400}&to=${Math.floor(Date.now() / 1000)}&token=${FINNHUB_API_KEY}`
        );
        this.lastRequestTime = Date.now();
        resolve(response.data);
      } catch (error) {
        if (error.response?.status === 403) {
          setApiLimitReached(true);
        }
        reject(error);
      }
      
      this.processing = false;
      this.processQueue();
    },

    getQuote(symbol) {
      return new Promise((resolve, reject) => {
        this.queue.push({ symbol, resolve, reject });
        this.processQueue();
      });
    }
  };

  const generateAISummary = async (content, context) => {
    try {
      const response = await axios.post(
        'https://openrouter.ai/api/v1/chat/completions',
        {
          model: "openai/gpt-3.5-turbo",
          messages: [
            { 
              "role": "system", 
              "content": `You are a financial analyst. Provide a concise 100-150 word summary of the ${context}. 
                          Highlight key points, trends, and important information. Use bullet points for clarity.`
            },
            { 
              "role": "user", 
              "content": content 
            }
          ],
          max_tokens: 500
        },
        {
          headers: {
            'Authorization': `Bearer ${OPENROUTER_API_KEY}`,
            'Content-Type': 'application/json',
            'HTTP-Referer': window.location.href,
            'X-Title': 'Stock Analysis App'
          }
        }
      );
      return response.data.choices[0].message.content;
    } catch (err) {
      console.error("Error generating AI summary:", err);
      return "Could not generate summary. Please try again later.";
    }
  };

  const fetchStockSummary = async (stockSymbol, customQuestion = '') => {
    try {
      setLoading(true);
      setError(null);
      setIsCustomQuery(!!customQuestion);

      const messages = [
        { 
          "role": "system", 
          "content": "You are a financial analyst providing accurate stock information. Provide detailed responses with key metrics when available." 
        }
      ];

      if (customQuestion) {
        messages.push({
          "role": "user",
          "content": `${customQuestion} about ${stockSymbol} stock. Provide detailed analysis with numbers if available.`
        });
      } else {
        messages.push(
          {
            "role": "system",
            "content": "Provide a comprehensive analysis including company overview, recent performance, key metrics, and outlook. Keep it between 200-300 words."
          },
          {
            "role": "user",
            "content": `Provide a detailed analysis of ${stockSymbol} stock. Include important metrics like P/E ratio, market cap, dividend yield if available, and recent performance.`
          }
        );
      }

      const response = await axios.post(
        'https://openrouter.ai/api/v1/chat/completions',
        {
          model: "openai/gpt-3.5-turbo",
          messages: messages,
          max_tokens: 1000
        },
        {
          headers: {
            'Authorization': `Bearer ${OPENROUTER_API_KEY}`,
            'Content-Type': 'application/json',
            'HTTP-Referer': window.location.href,
            'X-Title': 'Stock Analysis App'
          }
        }
      );

      const result = response.data.choices[0].message.content;
      
      if (customQuestion) {
        setCustomQueryResult(result);
      } else {
        setSummary(result);
      }
      
      setLoading(false);
    } catch (err) {
      console.error("Error generating summary:", err);
      setError('Failed to generate response. Please try again.');
      setLoading(false);
    }
  };

  const fetchStockChart = async (stockSymbol) => {
    try {
      if (apiLimitReached) {
        await fetchYahooChart(stockSymbol);
        return;
      }

      const data = await finnhubRateLimited.getQuote(stockSymbol);
      
      if (data.s === 'ok' && data.c?.length > 0) {
        const labels = data.t.map(timestamp => 
          new Date(timestamp * 1000).toLocaleTimeString()
        );
        
        const chartData = {
          labels: labels,
          datasets: [
            {
              label: `${stockSymbol} Price (1D)`,
              data: data.c,
              borderColor: '#3a80e9',
              backgroundColor: 'rgba(58, 128, 233, 0.1)',
              borderWidth: 2,
              tension: 0.1,
              fill: true
            }
          ]
        };
        
        setChartData(chartData);
        
        // Generate chart summary
        const priceData = data.c;
        const openPrice = priceData[0];
        const closePrice = priceData[priceData.length - 1];
        const highPrice = Math.max(...priceData);
        const lowPrice = Math.min(...priceData);
        const volume = data.v ? Math.round(data.v.reduce((a, b) => a + b, 0) / 1000) : null;
        
        const chartContent = `
          Stock: ${stockSymbol}
          Time Period: Last trading day
          Open Price: ${openPrice}
          Close Price: ${closePrice}
          High Price: ${highPrice}
          Low Price: ${lowPrice}
          ${volume ? `Volume: ${volume}K shares` : ''}
          Price Change: ${((closePrice - openPrice) / openPrice * 100).toFixed(2)}%
          Price Range: ${lowPrice} to ${highPrice}
        `;
        
        const summary = await generateAISummary(chartContent, "price chart data");
        setChartSummary(summary);
      } else {
        await fetchYahooChart(stockSymbol);
      }
    } catch (err) {
      console.error("Error fetching chart data:", err);
      await fetchYahooChart(stockSymbol);
    }
  };

  const fetchYahooChart = async (stockSymbol) => {
    try {
     const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
      const yahooUrl = `https://query1.finance.yahoo.com/v8/finance/chart/${stockSymbol}?range=1d&interval=5m`;
      
      const response = await fetch(proxyUrl + encodeURIComponent(yahooUrl));
      const data = await response.json();
      const parsedData = JSON.parse(data.contents);
      
      if (!parsedData.chart.result) {
        throw new Error('No chart data available');
      }
      
      const result = parsedData.chart.result[0];
      const timestamps = result.timestamp;
      const quotes = result.indicators.quote[0];
      
      const chartLabels = timestamps.map(timestamp => 
        new Date(timestamp * 1000).toLocaleTimeString()
      );
      
      const chartData = {
        labels: chartLabels,
        datasets: [
          {
            label: `${stockSymbol} Price (1D)`,
            data: quotes.close,
            borderColor: '#3a80e9',
            backgroundColor: 'rgba(58, 128, 233, 0.1)',
            borderWidth: 2,
            tension: 0.1,
            fill: true
          }
        ]
      };
      
      setChartData(chartData);
      
      // Generate chart summary from Yahoo data
      const priceData = quotes.close.filter(Boolean);
      if (priceData.length > 0) {
        const openPrice = priceData[0];
        const closePrice = priceData[priceData.length - 1];
        const highPrice = Math.max(...priceData);
        const lowPrice = Math.min(...priceData);
        
        const chartContent = `
          Stock: ${stockSymbol}
          Time Period: Last trading day (Yahoo Finance)
          Open Price: ${openPrice}
          Close Price: ${closePrice}
          High Price: ${highPrice}
          Low Price: ${lowPrice}
          Price Change: ${((closePrice - openPrice) / openPrice * 100).toFixed(2)}%
          Price Range: ${lowPrice} to ${highPrice}
        `;
        
        const summary = await generateAISummary(chartContent, "price chart data");
        setChartSummary(summary);
      }
    } catch (err) {
      console.error("Error fetching Yahoo Finance data:", err);
      setChartData(null);
      setChartSummary("Could not generate chart summary due to data loading issues.");
    }
  };

  const fetchStockNews = async (stockSymbol) => {
    try {
      if (apiLimitReached) {
        setNews([]);
        setNewsSummary("API limit reached. News data unavailable.");
        return;
      }

      const response = await axios.get(
        `https://finnhub.io/api/v1/company-news?symbol=${stockSymbol}&from=${new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}&to=${new Date().toISOString().split('T')[0]}&token=${FINNHUB_API_KEY}`
      );
      
      // Filter out news without image or with "Removed" headline
      const filteredNews = response.data
        .filter(item => item.image && item.headline && !item.headline.includes('Removed:'))
        .slice(0, 5); // Limit to 5 news items
      
      setNews(filteredNews);
      
      // Generate news summary
      if (filteredNews.length > 0) {
        const newsContent = filteredNews.map(item => 
          `Headline: ${item.headline}\nSummary: ${item.summary || 'No summary available'}\nSource: ${item.source}\n`
        ).join('\n');
        
        const summary = await generateAISummary(
          `Recent news about ${stockSymbol}:\n${newsContent}`,
          "market news"
        );
        setNewsSummary(summary);
      } else {
        setNewsSummary("No recent news available to summarize.");
      }
    } catch (err) {
      console.error("Error fetching news:", err);
      if (err.response?.status === 403) {
        setApiLimitReached(true);
      }
      setNews([]);
      setNewsSummary("Could not generate news summary due to data loading issues.");
    }
  };

  useEffect(() => {
    if (symbol) {
      fetchStockSummary(symbol);
      fetchStockChart(symbol);
      fetchStockNews(symbol);
    }
  }, [symbol]);

  const handleQuerySubmit = (e) => {
    e.preventDefault();
    if (query.trim() && symbol) {
      fetchStockSummary(symbol, query.trim());
    }
  };

  const handleStockSearch = (e) => {
    e.preventDefault();
    if (stockSearch.trim()) {
      navigate(`/ai-summary/${stockSearch.trim().toUpperCase()}`);
    }
  };

  // If no symbol is selected (direct access to /ai-summary)
  if (!symbol) {
    return (
      <div className="ai-summary-container">
        <div className="ai-summary-header">
          <h2>AI-Powered Stock Analysis</h2>
          <form onSubmit={handleStockSearch} className="search-form">
            <input
              type="text"
              value={stockSearch}
              onChange={(e) => setStockSearch(e.target.value)}
              placeholder="Enter stock symbol (e.g., AAPL)"
              required
            />
            <button type="submit">Analyze</button>
          </form>
        </div>
        <div className="ai-summary-content">
          <p>Please enter a stock symbol to view the AI-powered analysis.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>AI Analysis of {symbol}</h2>
        <form onSubmit={handleStockSearch} className="search-form">
          <input
            type="text"
            value={stockSearch}
            onChange={(e) => setStockSearch(e.target.value)}
            placeholder="Enter another stock symbol"
            required
          />
          <button type="submit">Analyze</button>
        </form>
      </div>

      {apiLimitReached && (
        <div className="api-warning">
          ⚠️ Finnhub API limit reached. Some data may be delayed or unavailable.
        </div>
      )}

      <div className="query-section">
        <form onSubmit={handleQuerySubmit} className="query-form">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Ask a specific question about this stock..."
          />
          <button type="submit">Ask AI</button>
        </form>
      </div>

      <div className="tabs">
        <button 
          className={`tab-button ${activeTab === 'summary' ? 'active' : ''}`}
          onClick={() => setActiveTab('summary')}
        >
          Summary
        </button>
        <button 
          className={`tab-button ${activeTab === 'chart' ? 'active' : ''}`}
          onClick={() => setActiveTab('chart')}
        >
          Price Chart
        </button>
        <button 
          className={`tab-button ${activeTab === 'news' ? 'active' : ''}`}
          onClick={() => setActiveTab('news')}
        >
          Market News
        </button>
      </div>

      <div className="ai-summary-content">
        {loading ? (
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Generating AI-powered response...</p>
          </div>
        ) : error ? (
          <div className="error-message">
            <p>{error}</p>
            <button onClick={() => window.location.reload()} className="retry-button">
              Retry
            </button>
          </div>
        ) : isCustomQuery ? (
          <div className="query-result">
            <h3>Response to your query:</h3>
            <div className="result-text">
              {customQueryResult.split('\n').map((paragraph, i) => (
                <p key={i}>{paragraph}</p>
              ))}
            </div>
            <button 
              onClick={() => setIsCustomQuery(false)} 
              className="show-original-button"
            >
              Show Original Analysis
            </button>
          </div>
        ) : (
          <>
            {activeTab === 'summary' && (
              <div className="summary-section">
                <h3>Comprehensive Analysis of {symbol}</h3>
                <div className="summary-text">
                  {summary.split('\n').map((paragraph, i) => (
                    <p key={i}>{paragraph}</p>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'chart' && (
              <div className="chart-section">
                <h3>{symbol} 1-Day Price Chart</h3>
                {chartData ? (
                  <>
                    <div className="chart-container">
                      <Line 
                        data={chartData} 
                        options={{
                          responsive: true,
                          plugins: {
                            legend: {
                              position: 'top',
                            },
                            tooltip: {
                              mode: 'index',
                              intersect: false,
                            }
                          },
                          scales: {
                            x: {
                              display: true,
                              title: {
                                display: true,
                                text: 'Time'
                              }
                            },
                            y: {
                              display: true,
                              title: {
                                display: true,
                                text: 'Price (USD)'
                              }
                            }
                          }
                        }}
                      />
                    </div>
                    <div className="chart-summary">
                      <h4>Chart Analysis Summary</h4>
                      <div className="summary-text">
                        {chartSummary.split('\n').map((paragraph, i) => (
                          <p key={i}>{paragraph}</p>
                        ))}
                      </div>
                    </div>
                  </>
                ) : (
                  <p className="no-data">Could not load chart data</p>
                )}
              </div>
            )}

            {activeTab === 'news' && (
              <div className="news-section">
                <h3>Latest News About {symbol}</h3>
                {news.length > 0 ? (
                  <>
                    <div className="news-summary-section">
                      <h4>News Summary</h4>
                      <div className="summary-text">
                        {newsSummary.split('\n').map((paragraph, i) => (
                          <p key={i}>{paragraph}</p>
                        ))}
                      </div>
                    </div>
                    <div className="news-grid">
                      {news.map((item, index) => (
                        <div key={index} className="news-card">
                          {item.image && (
                            <div className="news-image">
                              <img src={item.image} alt={item.headline} />
                            </div>
                          )}
                          <div className="news-content">
                            <h4>
                              <a href={item.url} target="_blank" rel="noopener noreferrer">
                                {item.headline}
                              </a>
                            </h4>
                            <p className="news-source">{item.source}</p>
                            <p className="news-summary">
                              {item.summary || 'No summary available'}
                            </p>
                            <p className="news-date">
                              {new Date(item.datetime * 1000).toLocaleDateString()}
                            </p>
                          </div>
                        </div>
                      ))}
                    </div>
                  </>
                ) : (
                  <p className="no-data">No recent news found for this stock</p>
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default AISummary;
