import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import './AISummary.css';

const AISummary = () => {
  const { symbol } = useParams();
  const [analysis, setAnalysis] = useState({
    summary: '',
    loading: true,
    error: null,
    lastUpdated: null
  });
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [retryCount, setRetryCount] = useState(0);

  const fetchSummary = useCallback(async () => {
    try {
      const storedUser = JSON.parse(localStorage.getItem('user'));
      const token = storedUser?.token;

      if (!token) {
        logout();
        navigate('/login');
        return;
      }

      setAnalysis(prev => ({ ...prev, loading: true, error: null }));

      const response = await axios.post(
        `${process.env.REACT_APP_API_URL || ''}/api/auth/ai/summary`,
        { symbol },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          withCredentials: true,
          timeout: 10000 // 10 seconds timeout
        }
      );

      if (!response.data.success) {
        throw new Error(response.data.message || 'Failed to generate summary');
      }

      setAnalysis({
        summary: response.data.summary,
        loading: false,
        error: null,
        lastUpdated: new Date()
      });
    } catch (err) {
      console.error('Error fetching AI summary:', err);
      
      let errorMessage = 'Failed to generate summary';
      let shouldLogout = false;
      let canRetry = false;

      if (err.response) {
        switch (err.response.status) {
          case 401:
            errorMessage = 'Session expired. Please login again.';
            shouldLogout = true;
            break;
          case 429:
            errorMessage = 'Too many requests. Please try again in a few minutes.';
            canRetry = true;
            break;
          case 500:
            errorMessage = 'Server error. Please try again later.';
            canRetry = true;
            break;
          default:
            if (err.response.data?.message) {
              errorMessage = err.response.data.message;
            }
        }
      } else if (err.request) {
        errorMessage = 'Network error. Please check your connection.';
        canRetry = true;
      }

      setAnalysis(prev => ({
        ...prev,
        loading: false,
        error: errorMessage
      }));

      if (shouldLogout) {
        logout();
        navigate('/login');
      }

      if (canRetry && retryCount < 2) {
        setTimeout(() => {
          setRetryCount(prev => prev + 1);
        }, 3000 * (retryCount + 1)); // Exponential backoff
      }
    }
  }, [symbol, navigate, logout, retryCount]);

  useEffect(() => {
    if (symbol) {
      fetchSummary();
    }
  }, [symbol, fetchSummary]);

  const handleRetry = () => {
    setRetryCount(0);
    fetchSummary();
  };

  if (analysis.loading) {
    return (
      <div className="ai-summary-container">
        <div className="ai-summary-header">
          <h2>AI Stock Analysis: {symbol}</h2>
        </div>
        <div className="ai-summary-loading">
          <div className="loading-spinner"></div>
          <p>Generating AI analysis for {symbol}...</p>
          <p className="loading-subtext">This may take a moment</p>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>AI Stock Analysis: {symbol}</h2>
        <div className="ai-summary-actions">
          <button 
            onClick={() => navigate(-1)} 
            className="back-button"
          >
            Back to Watchlist
          </button>
          <button 
            onClick={handleRetry} 
            className="refresh-button"
            disabled={analysis.loading}
          >
            Refresh Analysis
          </button>
        </div>
      </div>
      
      {analysis.error ? (
        <div className="ai-summary-error">
          <div className="error-icon">⚠️</div>
          <p>{analysis.error}</p>
          {retryCount < 2 && (
            <button 
              onClick={handleRetry} 
              className="retry-button"
            >
              Try Again
            </button>
          )}
        </div>
      ) : (
        <>
          {analysis.lastUpdated && (
            <div className="last-updated">
              Last updated: {analysis.lastUpdated.toLocaleTimeString()}
            </div>
          )}
          <div className="ai-summary-content">
            {analysis.summary.split('\n\n').map((paragraph, index) => (
              <p key={index}>{paragraph}</p>
            ))}
          </div>
          <div className="disclaimer">
            <p>Disclaimer: This AI-generated analysis is for informational purposes only and should not be considered financial advice.</p>
          </div>
        </>
      )}
    </div>
  );
};

export default AISummary;
