import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';

const Positions = () => {
  const { user, isAuthenticated } = useAuth();
  const [positions, setPositions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [marketPrices, setMarketPrices] = useState({});

  // Fetch initial positions data
  useEffect(() => {
    const fetchPositions = async () => {
      if (!isAuthenticated) {
        setLoading(false);
        return;
      }

      try {
        const token = JSON.parse(localStorage.getItem('user'))?.token;
        if (!token) throw new Error('No authentication token found');

        const response = await axios.get('/api/auth/user', {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        if (response.data.success) {
          setPositions(response.data.user.positions || []);
          // Initialize market prices with current LTP values
          const prices = {};
          response.data.user.positions.forEach(pos => {
            prices[pos.symbol] = pos.ltp;
          });
          setMarketPrices(prices);
        }
      } catch (err) {
        console.error('Error fetching positions:', err);
        setError('Failed to load positions. Please try again.');
        if (user?.positions) {
          setPositions([...user.positions]);
          const prices = {};
          user.positions.forEach(pos => {
            prices[pos.symbol] = pos.ltp;
          });
          setMarketPrices(prices);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchPositions();
  }, [user, isAuthenticated]);

  // Simulate real-time price updates (replace with actual WebSocket connection)
  useEffect(() => {
    if (!positions.length) return;

    const interval = setInterval(() => {
      const updatedPrices = {...marketPrices};
      positions.forEach(pos => {
        // Simulate price changes (replace with actual market data)
        const change = (Math.random() * 4 - 2); // Random change between -2 and +2
        updatedPrices[pos.symbol] = Math.max(1, (pos.ltp || pos.avgPrice) + change);
      });
      setMarketPrices(updatedPrices);
    }, 3000); // Update every 3 seconds

    return () => clearInterval(interval);
  }, [positions]);

  if (loading) return <div className="loading">Loading positions...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="positions">
      <h2>Your Positions</h2>
      {positions && positions.length > 0 ? (
        <div className="table-responsive">
          <table className="positions-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Instrument</th>
                <th>Qty.</th>
                <th>Avg.</th>
                <th>LTP</th>
                <th>P&L</th>
                <th>Chg.</th>
              </tr>
            </thead>
            <tbody>
              {positions.map((position) => {
                const currentPrice = marketPrices[position.symbol] || position.ltp || position.avgPrice;
                const pnl = (currentPrice - position.avgPrice) * position.quantity;
                const change = position.avgPrice 
                  ? ((currentPrice - position.avgPrice) / position.avgPrice) * 100
                  : 0;
                
                return (
                  <tr key={`${position.symbol}-${position.product}`}>
                    <td>{position.product || 'CNC'}</td>
                    <td>{position.symbol || 'N/A'}</td>
                    <td>{position.quantity || 0}</td>
                    <td>₹{(position.avgPrice || 0).toFixed(2)}</td>
                    <td>₹{currentPrice.toFixed(2)}</td>
                    <td className={pnl >= 0 ? 'positive' : 'negative'}>
                      ₹{pnl.toFixed(2)}
                    </td>
                    <td className={change >= 0 ? 'positive' : 'negative'}>
                      {change.toFixed(2)}%
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="no-positions">You don't have any open positions.</p>
      )}
    </div>
  );
};

export default Positions;
