import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { createChart, CrosshairMode } from 'lightweight-charts';
import './AISummary.css';

const AISummary = () => {
  const { symbol } = useParams();
  const navigate = useNavigate();
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState('');
  const [customQueryResult, setCustomQueryResult] = useState(null);
  const [isCustomQuery, setIsCustomQuery] = useState(false);
  const [stockSearch, setStockSearch] = useState('');
  const [news, setNews] = useState([]);
  const [newsSummary, setNewsSummary] = useState('');
  const [activeTab, setActiveTab] = useState('analysis');
  
  const chartContainerRef = useRef(null);
  const chartInstance = useRef(null);
  const tradingViewWidgetRef = useRef(null);

  // API Keys
  const FINNHUB_API_KEY = 'd1aopp9r01qjhvtqeebgd1aopp9r01qjhvtqeec0';
  const OPENROUTER_API_KEY = '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';
  const ALPHA_VANTAGE_KEY = 'YOUR_ALPHA_VANTAGE_KEY';

  // Cleanup charts before unmount
  const cleanupCharts = () => {
    if (chartInstance.current) {
      chartInstance.current.remove();
      chartInstance.current = null;
    }
    if (tradingViewWidgetRef.current) {
      tradingViewWidgetRef.current.remove();
      tradingViewWidgetRef.current = null;
    }
    const container = chartContainerRef.current;
    if (container) {
      container.innerHTML = '';
    }
  };

  // Fetch stock summary
  const fetchStockSummary = async (stockSymbol, customQuestion = '') => {
    try {
      setLoading(true);
      setError(null);
      setIsCustomQuery(!!customQuestion);

      const messages = [
        { 
          "role": "system", 
          "content": "You are a financial analyst providing accurate stock information." 
        }
      ];

      if (customQuestion) {
        messages.push({
          "role": "user",
          "content": `${customQuestion} about ${stockSymbol} stock.`
        });
      } else {
        messages.push(
          {
            "role": "system",
            "content": "Provide a comprehensive analysis including company overview."
          },
          {
            "role": "user",
            "content": `Provide a detailed analysis of ${stockSymbol} stock.`
          }
        );
      }

      const response = await axios.post('https://openrouter.ai/api/v1/chat/completions', {
        model: "openai/gpt-3.5-turbo",
        messages: messages,
        max_tokens: 1000
      }, {
        headers: {
          'Authorization': `Bearer ${OPENROUTER_API_KEY}`,
          'Content-Type': 'application/json'
        }
      });

      const result = response.data.choices[0].message.content;
      
      if (customQuestion) {
        setCustomQueryResult(result);
      } else {
        setSummary(result);
      }
    } catch (err) {
      console.error("Error generating summary:", err);
      setError('Failed to generate response. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Fetch market news
  const fetchStockNews = async (stockSymbol) => {
    try {
      const response = await axios.get(
        `https://finnhub.io/api/v1/company-news?symbol=${stockSymbol}&from=${new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}&to=${new Date().toISOString().split('T')[0]}&token=${FINNHUB_API_KEY}`
      );
      
      const filteredNews = response.data
        .filter(item => item.headline && item.headline.toLowerCase().includes(stockSymbol.toLowerCase()))
        .slice(0, 5);
      
      setNews(filteredNews);
      generateNewsSummary(stockSymbol, filteredNews);
    } catch (err) {
      console.error("Error fetching news:", err);
    }
  };

  // Generate AI summary of the news
  const generateNewsSummary = async (stockSymbol, newsItems) => {
    try {
      if (!newsItems || newsItems.length === 0) return;
      
      const headlines = newsItems.map(item => item.headline).join('\n- ');
      
      const response = await axios.post('https://openrouter.ai/api/v1/chat/completions', {
        model: "openai/gpt-3.5-turbo",
        messages: [
          { 
            "role": "system", 
            "content": "You are a financial news analyst." 
          },
          { 
            "role": "user", 
            "content": `Provide a summary of recent news about ${stockSymbol}`
          }
        ],
        max_tokens: 300
      }, {
        headers: {
          'Authorization': `Bearer ${OPENROUTER_API_KEY}`,
          'Content-Type': 'application/json'
        }
      });

      setNewsSummary(response.data.choices[0].message.content);
    } catch (err) {
      console.error("Error generating news summary:", err);
    }
  };

  // Render TradingView widget
  const renderTradingViewWidget = (stockSymbol) => {
    cleanupCharts();
    
    const container = chartContainerRef.current;
    if (!container) return;

    const script = document.createElement('script');
    script.src = 'https://s3.tradingview.com/tv.js';
    script.async = true;
    script.onload = () => {
      if (!window.TradingView) return;
      
      tradingViewWidgetRef.current = new window.TradingView.widget({
        autosize: true,
        symbol: stockSymbol,
        interval: 'D',
        timezone: 'Etc/UTC',
        theme: 'light',
        style: '1',
        locale: 'en',
        toolbar_bg: '#f1f3f6',
        enable_publishing: false,
        hide_top_toolbar: true,
        hide_side_toolbar: true,
        allow_symbol_change: false,
        container_id: 'tradingview-widget-container'
      });
    };
    
    script.onerror = () => {
      console.error('Failed to load TradingView script');
    };
    
    container.appendChild(script);
    
    const widgetContainer = document.createElement('div');
    widgetContainer.id = 'tradingview-widget-container';
    widgetContainer.style.width = '100%';
    widgetContainer.style.height = '400px';
    container.appendChild(widgetContainer);
  };

  // Load all data when symbol changes
  useEffect(() => {
    if (!symbol) return;

    setLoading(true);
    fetchStockSummary(symbol);
    fetchStockNews(symbol);
    renderTradingViewWidget(symbol);

    return () => {
      cleanupCharts();
    };
  }, [symbol]);

  const handleQuerySubmit = (e) => {
    e.preventDefault();
    if (query.trim() && symbol) {
      fetchStockSummary(symbol, query);
      setQuery('');
    }
  };

  const handleStockSearch = (e) => {
    e.preventDefault();
    if (stockSearch.trim()) {
      navigate(`/ai-summary/${stockSearch.trim().toUpperCase()}`);
    }
  };

  if (!symbol) {
    return (
      <div className="ai-summary-container">
        <div className="ai-summary-header">
          <h2>Stock Analysis Tool</h2>
        </div>

        <div className="primary-search-section">
          <form onSubmit={handleStockSearch} className="primary-search-form">
            <input
              type="text"
              value={stockSearch}
              onChange={(e) => setStockSearch(e.target.value)}
              placeholder="Enter a stock symbol (e.g., AAPL, MSFT)"
              className="primary-search-input"
              autoFocus
            />
            <button type="submit" className="primary-search-button">
              Analyze Stock
            </button>
          </form>
          <p className="search-examples">
            Try: AAPL, MSFT, TSLA, GOOGL, AMZN
          </p>
        </div>

        <div className="welcome-message">
          <h3>Welcome to Stock Analysis AI</h3>
          <p>
            Get comprehensive AI-powered analysis of any publicly traded company.
            Enter a stock symbol above to begin.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>{symbol.toUpperCase()} Stock Analysis</h2>
        <button onClick={() => navigate('/ai-summary')} className="back-button">
          Analyze Another Stock
        </button>
      </div>

      <div className="search-section">
        <form onSubmit={handleQuerySubmit} className="query-form">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder={`Ask anything about ${symbol.toUpperCase()}...`}
            className="query-input"
          />
          <button type="submit" className="query-button">
            Ask
          </button>
        </form>
      </div>

      <div className="chart-section">
        <h3>{symbol.toUpperCase()} Price Chart</h3>
        <div 
          ref={chartContainerRef} 
          className="chart-container"
          style={{ width: '100%', height: '400px' }}
        />
      </div>

      <div className="tabs">
        <button 
          className={`tab-button ${activeTab === 'analysis' ? 'active' : ''}`}
          onClick={() => setActiveTab('analysis')}
        >
          AI Analysis
        </button>
        <button 
          className={`tab-button ${activeTab === 'news' ? 'active' : ''}`}
          onClick={() => setActiveTab('news')}
        >
          Market News
        </button>
      </div>

      <div className="ai-summary-content">
        {loading ? (
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Loading data...</p>
          </div>
        ) : error ? (
          <div className="error-message">
            <p>{error}</p>
            <button onClick={() => window.location.reload()} className="retry-button">
              Retry
            </button>
          </div>
        ) : isCustomQuery ? (
          <div className="query-result">
            <h3>Response to your query:</h3>
            <div className="result-text">
              {customQueryResult.split('\n').map((paragraph, i) => (
                <p key={i}>{paragraph}</p>
              ))}
            </div>
            <button 
              onClick={() => setIsCustomQuery(false)} 
              className="show-original-button"
            >
              Show Full Analysis
            </button>
          </div>
        ) : (
          <>
            {activeTab === 'analysis' && (
              <div className="summary-section">
                <h3>Analysis of {symbol.toUpperCase()}</h3>
                <div className="summary-text">
                  {summary.split('\n').map((paragraph, i) => (
                    <p key={i}>{paragraph}</p>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'news' && (
              <div className="news-section">
                <h3>Latest News About {symbol.toUpperCase()}</h3>
                {newsSummary && (
                  <div className="news-summary">
                    <h4>News Summary:</h4>
                    <p>{newsSummary}</p>
                  </div>
                )}
                <div className="news-list">
                  {news.length > 0 ? (
                    news.map((item, index) => (
                      <div key={index} className="news-item">
                        <h4>
                          <a href={item.url} target="_blank" rel="noopener noreferrer">
                            {item.headline}
                          </a>
                        </h4>
                        <p className="news-source">{item.source} - {new Date(item.datetime * 1000).toLocaleDateString()}</p>
                      </div>
                    ))
                  ) : (
                    <p>No recent news found</p>
                  )}
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default AISummary;
