import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';

const Funds = () => {
  const { user } = useAuth();
  const [walletBalance, setWalletBalance] = useState(0);
  const [action, setAction] = useState('ADD');
  const [amount, setAmount] = useState('');
  const [bankName, setBankName] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);

  useEffect(() => {
    const fetchWalletBalance = async () => {
      try {
        const response = await fetch('/api/auth/user', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        setWalletBalance(data.walletBalance || 0);
      } catch (err) {
        console.error('Error fetching wallet balance:', err);
      }
    };

    fetchWalletBalance();
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage(null);

    try {
      const response = await fetch('/api/auth/funds', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          action,
          amount: parseFloat(amount),
          bankName
        })
      });

      const data = await response.json();
      
      if (data.success) {
        setWalletBalance(data.walletBalance);
        setMessage({
          type: 'success',
          text: `Funds ${action === 'ADD' ? 'added to' : 'withdrawn from'} wallet successfully!`
        });
        setAmount('');
        setBankName('');
      }
    } catch (err) {
      console.error('Funds error:', err);
      setMessage({
        type: 'error',
        text: 'Failed to process funds request. Please try again.'
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="funds">
      <h2>Funds Management</h2>
      
      <div className="wallet-summary">
        <h3>Current Wallet Balance</h3>
        <p className="balance">₹{walletBalance.toFixed(2)}</p>
      </div>
      
      <div className="funds-form-container">
        <div className="action-tabs">
          <button 
            className={`tab ${action === 'ADD' ? 'active' : ''}`}
            onClick={() => setAction('ADD')}
          >
            Add Funds
          </button>
          <button 
            className={`tab ${action === 'WITHDRAW' ? 'active' : ''}`}
            onClick={() => setAction('WITHDRAW')}
          >
            Withdraw Funds
          </button>
        </div>
        
        <form onSubmit={handleSubmit} className="funds-form">
          <div className="form-group">
            <label>Amount (₹):</label>
            <input
              type="number"
              min="1"
              step="0.01"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Bank Name:</label>
            <input
              type="text"
              value={bankName}
              onChange={(e) => setBankName(e.target.value)}
              required
            />
          </div>
          
          <button 
            type="submit" 
            className="submit-button"
            disabled={loading}
          >
            {loading ? 'Processing...' : action === 'ADD' ? 'Add Funds' : 'Withdraw Funds'}
          </button>
        </form>
        
        {message && (
          <div className={`message ${message.type}`}>
            {message.text}
          </div>
        )}
      </div>
    </div>
  );
};

export default Funds;
