import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import './AISummary.css';

const AISummary = () => {
  const { symbol } = useParams();
  const [analysis, setAnalysis] = useState({
    summary: '',
    loading: true,
    error: null,
    lastUpdated: null,
    rateLimited: false
  });
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [retryCount, setRetryCount] = useState(0);

  const fetchSummary = useCallback(async () => {
    try {
      const token = JSON.parse(localStorage.getItem('user'))?.token;
      if (!token) {
        logout();
        navigate('/login');
        return;
      }

      setAnalysis(prev => ({ ...prev, loading: true, error: null }));

      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/ai/summary`,
        { symbol },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 30000
        }
      );

      setAnalysis({
        summary: response.data.summary,
        loading: false,
        error: null,
        lastUpdated: new Date(),
        rateLimited: false
      });
      setRetryCount(0);
    } catch (err) {
      console.error('Analysis error:', err);
      
      let errorMessage = 'Failed to generate analysis';
      let isRateLimited = false;
      let shouldLogout = false;
      
      if (err.response?.status === 401) {
        errorMessage = 'Session expired. Please login again.';
        shouldLogout = true;
      } else if (err.response?.status === 429) {
        errorMessage = 'Too many requests. Please try again later.';
        isRateLimited = true;
        localStorage.setItem('lastRateLimitTime', Date.now());
      } else if (err.code === 'ECONNABORTED') {
        errorMessage = 'Analysis is taking longer than expected.';
      } else if (err.message.includes('Network Error')) {
        errorMessage = 'Network error. Please check your connection.';
      }

      setAnalysis(prev => ({
        ...prev,
        loading: false,
        error: errorMessage,
        rateLimited: isRateLimited
      }));

      if (shouldLogout) {
        logout();
        navigate('/login');
      }

      if (!isRateLimited && retryCount < 2) {
        const delay = Math.min(3000 * (retryCount + 1), 10000);
        setTimeout(() => {
          setRetryCount(prev => prev + 1);
        }, delay);
      }
    }
  }, [symbol, navigate, logout, retryCount]);

  useEffect(() => {
    if (symbol) {
      const timer = setTimeout(fetchSummary, 500);
      return () => clearTimeout(timer);
    }
  }, [symbol, fetchSummary]);

  const handleRetry = () => {
    if (analysis.rateLimited) {
      const lastRateLimitTime = localStorage.getItem('lastRateLimitTime');
      if (lastRateLimitTime && Date.now() - parseInt(lastRateLimitTime) < 60000) {
        setAnalysis(prev => ({
          ...prev,
          error: 'Please wait at least 1 minute after rate limiting'
        }));
        return;
      }
      localStorage.removeItem('lastRateLimitTime');
      setAnalysis(prev => ({ ...prev, rateLimited: false }));
    }
    setRetryCount(0);
    fetchSummary();
  };

  // Format markdown content
  const formatContent = (text) => {
    return text.split('\n').map((paragraph, index) => {
      if (paragraph.startsWith('## ')) {
        return <h3 key={index}>{paragraph.replace('## ', '')}</h3>;
      }
      return <p key={index}>{paragraph}</p>;
    });
  };

  if (analysis.loading) {
    return (
      <div className="ai-summary-container">
        <div className="ai-summary-header">
          <h2>AI Stock Analysis: {symbol}</h2>
        </div>
        <div className="ai-summary-loading">
          <div className="loading-spinner"></div>
          <p>Generating comprehensive analysis for {symbol}...</p>
          {retryCount > 0 && (
            <p className="loading-subtext">Attempt {retryCount + 1} of 3</p>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>AI Stock Analysis: {symbol}</h2>
        <div className="ai-summary-actions">
          <button onClick={() => navigate(-1)} className="back-button">
            Back to Watchlist
          </button>
          <button 
            onClick={handleRetry} 
            className={`refresh-button ${analysis.rateLimited ? 'rate-limited' : ''}`}
            disabled={analysis.loading || analysis.rateLimited}
          >
            {analysis.rateLimited ? 'Try Again Later' : 'Refresh Analysis'}
          </button>
        </div>
      </div>
      
      {analysis.error ? (
        <div className={`ai-summary-error ${analysis.rateLimited ? 'rate-limited' : ''}`}>
          <div className="error-icon">⚠️</div>
          <p>{analysis.error}</p>
          {!analysis.rateLimited && retryCount < 2 && (
            <button onClick={handleRetry} className="retry-button">
              Try Again
            </button>
          )}
          {analysis.rateLimited && (
            <p className="rate-limit-message">
              API rate limits reset after 1 minute. Please wait before trying again.
            </p>
          )}
        </div>
      ) : (
        <>
          {analysis.lastUpdated && (
            <div className="last-updated">
              Last updated: {analysis.lastUpdated.toLocaleString()}
            </div>
          )}
          <div className="ai-summary-content">
            {formatContent(analysis.summary)}
          </div>
          <div className="disclaimer">
            <p>Disclaimer: This AI-generated analysis is for informational purposes only and should not be considered financial advice. Data may be delayed or inaccurate.</p>
          </div>
        </>
      )}
    </div>
  );
};

export default AISummary;
