import React, { useState, useEffect } from 'react';
import './Watchlist.css';

const Watchlist = () => {
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStocks = async () => {
      try {
        // Remove credentials: 'include' unless using cookies
        const response = await fetch('http://localhost:5000/api/stocks');
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        setStocks(data);
        setError(null);
      } catch (error) {
        console.error('Error fetching stocks:', error);
        setError(error.message);
        // Fallback mock data
        setStocks([
          { symbol: 'RELIANCE', price: 2456.75, change: 12.5 },
          { symbol: 'TCS', price: 3452.10, change: -5.25 },
          { symbol: 'HDFCBANK', price: 1567.30, change: 8.75 },
          { symbol: 'INFY', price: 1678.90, change: -3.20 }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchStocks();
  }, []);

  if (loading) return <div className="watchlist">Loading stocks...</div>;
  if (error) return (
    <div className="watchlist error">
      Error: {error} (Using mock data)
      <button onClick={() => window.location.reload()}>Retry</button>
    </div>
  );

  return (
    <div className="watchlist">
      <h3>Watchlist</h3>
      <table>
        <thead>
          <tr>
            <th>Symbol</th>
            <th>Price</th>
            <th>Change</th>
          </tr>
        </thead>
        <tbody>
          {stocks.map((stock, index) => (
            <tr key={stock.symbol || index}>
              <td>{stock.symbol}</td>
              <td>{stock.price.toFixed(2)}</td>
              <td className={stock.change >= 0 ? 'positive' : 'negative'}>
                {stock.change >= 0 ? '+' : ''}{stock.change.toFixed(2)}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Watchlist;
