import React, { useState, useEffect } from 'react';
import './Watchlist.css';

const Watchlist = () => {
  const [indexData, setIndexData] = useState([
    { name: 'NIFTY 50', value: 10865.65, change: -1.37 },
    { name: 'SENSEX', value: 35400.61, change: 1.41 }
  ]);

  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchStocks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/stocks');
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        setStocks(data);
        setError(null);
      } catch (error) {
        console.error('Error fetching stocks:', error);
        setError(error.message);
        // Fallback mock data
        setStocks([
          { symbol: 'NIFTY 50 INDEX', price: 16630.65, change: -1.37 },
          { symbol: 'NIFTY BANK INDEX', price: 35022.32, change: 1.78 },
          { symbol: 'RELIANCE', price: 2363.45, change: -4.33 },
          { symbol: 'HDFCBANK', price: 1971.25, change: -1.00 }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchStocks();
  }, []);

  if (loading) return <div className="watchlist-loading">Loading market data...</div>;

  return (
    <div className="watchlist-container">
      {/* Index Overview Section */}
      <div className="index-overview">
        {indexData.map((index, i) => (
          <div key={i} className="index-item">
            <span className="index-name">{index.name}</span>
            <span className="index-value">{index.value.toLocaleString()}</span>
            <span className={`index-change ${index.change >= 0 ? 'positive' : 'negative'}`}>
              {index.change >= 0 ? '+' : ''}{index.change}%
            </span>
          </div>
        ))}
      </div>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search eg: gold mcx, infy bse, nifty fut"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {/* Stock List */}
      <div className="stock-list">
        {error && (
          <div className="watchlist-error">
            {error} (Using mock data)
            <button onClick={() => window.location.reload()}>Retry</button>
          </div>
        )}
        
        {stocks.map((stock, i) => (
          <div key={i} className="stock-item">
            <span className="stock-symbol">{stock.symbol}</span>
            <span className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
              {stock.change >= 0 ? '+' : ''}{stock.change}
            </span>
            <span className="stock-price">{stock.price.toLocaleString()}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Watchlist;
