import React, { useState, useEffect, useRef } from 'react';
import './Watchlist.css';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
// Add this new component at the top of Watchlist.js (before the Watchlist component)
const SummaryModal = ({ symbol, onClose }) => {
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockSummary = async () => {
      try {
        const OPENROUTER_API_KEY = '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';
        const apiUrl = 'https://openrouter.ai/api/v1/chat/completions';

        const response = await axios.post(apiUrl, {
          model: "openai/gpt-3.5-turbo",
          messages: [
            { 
              "role": "system", 
              "content": "You are a financial analyst providing concise stock summaries. Provide key information including company overview, recent performance, and analyst sentiment. Keep it under 200 words." 
            },
            { 
              "role": "user", 
              "content": `Provide a summary analysis of ${symbol} stock. Include key metrics, recent performance, and outlook.` 
            }
          ]
        }, {
          headers: {
            'Authorization': `Bearer ${OPENROUTER_API_KEY}`,
            'Content-Type': 'application/json'
          }
        });

        setSummary(response.data.choices[0].message.content);
        setLoading(false);
      } catch (err) {
        console.error("Error generating summary:", err);
        setError('Failed to generate summary. Please try again.');
        setLoading(false);
      }
    };

    fetchStockSummary();
  }, [symbol]);

  return (
    <div className="summary-modal">
      <div className="summary-modal-content">
        <div className="summary-modal-header">
          <h3>{symbol} Stock Summary</h3>
          <button className="close-modal" onClick={onClose}>Ã—</button>
        </div>
        <div className="summary-modal-body">
          {loading ? (
            <div className="summary-loading">Generating summary...</div>
          ) : error ? (
            <div className="summary-error">{error}</div>
          ) : (
            <div className="summary-text">
              {summary.split('\n').map((paragraph, i) => (
                <p key={i}>{paragraph}</p>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
const BuySellModal = ({ 
  type, 
  symbol, 
  price, 
  onClose, 
  onSubmit, 
  walletBalance 
}) => {
  const [quantity, setQuantity] = useState(1);
  const [product, setProduct] = useState('CNC');
  const [total, setTotal] = useState(price);

  useEffect(() => {
    setTotal((quantity * price).toFixed(2));
  }, [quantity, price]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ symbol, quantity: parseInt(quantity), price, product, type });
  };

  return (
    <div className="trade-modal">
      <div className="trade-modal-content">
        <div className="trade-modal-header">
          <h3>{type} {symbol}</h3>
          <button className="close-modal" onClick={onClose}>Ã—</button>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Price:</label>
            <input type="text" value={price} readOnly />
          </div>
          <div className="form-group">
            <label>Quantity:</label>
            <input 
              type="number" 
              min="1" 
              value={quantity} 
              onChange={(e) => setQuantity(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label>Product:</label>
            <select value={product} onChange={(e) => setProduct(e.target.value)}>
              <option value="CNC">CNC</option>
              <option value="MIS">MIS</option>
              <option value="NRML">NRML</option>
            </select>
          </div>
          <div className="form-group">
            <label>Total:</label>
            <input type="text" value={total} readOnly />
          </div>
          {type === 'BUY' && (
            <div className="wallet-info">
              <p>Wallet Balance: â‚¹{walletBalance.toFixed(2)}</p>
              {total > walletBalance && (
                <p className="error">Insufficient funds</p>
              )}
            </div>
          )}
          <button 
            type="submit" 
            className={`trade-button ${type.toLowerCase()}`}
            disabled={type === 'BUY' && total > walletBalance}
          >
            {type}
          </button>
        </form>
      </div>
    </div>
  );
};

const Watchlist = ({ onShowChart,onWatchlistUpdate }) => {
  const FINNHUB_API_KEY = 'd17tfrhr01qteuvpuh10d17tfrhr01qteuvpuh1g'; 
  const [marketIndices, setMarketIndices] = useState([]);
  const [stocks, setStocks] = useState([]);
   const ws = useRef(null);
  const [filteredStocks, setFilteredStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [socketStatus, setSocketStatus] = useState('disconnected');
  const socketRef = useRef(null);
  const [searchTerm, setSearchTerm] = useState('');
  const reconnectTimerRef = useRef(null);
  const isMountedRef = useRef(false);
  const [hoveredStock, setHoveredStock] = useState(null);
 const { user, isAuthenticated, updateWalletBalance } = useAuth();
  const [tradeModal, setTradeModal] = useState(null);
  const [walletBalance, setWalletBalance] = useState(user?.walletBalance || 0);
  const [notification, setNotification] = useState(null);
  const [chartDropdown, setChartDropdown] = useState(null);
  const [summaryModal, setSummaryModal] = useState(null);
const navigate = useNavigate();
  // Filter stocks based on search term
  useEffect(() => {
    if (searchTerm === '') {
      setFilteredStocks(stocks);
    } else {
      const filtered = stocks.filter(stock => 
        stock.symbol.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredStocks(filtered);
    }
  }, [searchTerm, stocks]);

   useEffect(() => {
    if (user?.walletBalance !== undefined) {
      setWalletBalance(user.walletBalance);
    }
  }, [user]);

  // Fetch user data including wallet balance
   useEffect(() => {
    if (isAuthenticated) {
      const fetchUserData = async () => {
        try {
          const response = await fetch('/api/auth/user', {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
          });
          const data = await response.json();
          if (data.success) {
            setWalletBalance(data.user.walletBalance);
            updateWalletBalance(data.user.walletBalance);
          }
        } catch (err) {
          console.error('Error fetching user data:', err);
        }
      };
      fetchUserData();
    }
  }, [isAuthenticated, updateWalletBalance]);

 

  // WebSocket connection management
  const setupWebSocket = useCallback(() => {
    if (ws.current) {
      // Clean up existing connection
      if (ws.current.readyState === WebSocket.OPEN) {
        ws.current.close(1000, 'Reconnecting');
      }
      ws.current = null;
    }

    setSocketStatus('connecting');
    ws.current = new WebSocket(`wss://ws.finnhub.io?token=${FINNHUB_API_KEY}`);

    ws.current.onopen = () => {
      setSocketStatus('connected');
      // Subscribe to symbols
      stocks.forEach(stock => {
        ws.current.send(JSON.stringify({ type: 'subscribe', symbol: `BSE:${stock.symbol}` }));
      });
    };

    ws.current.onmessage = (e) => {
      // Handle incoming messages
    };

    ws.current.onerror = (err) => {
      setSocketStatus('error');
      console.error('WebSocket error:', err);
    };

    ws.current.onclose = (e) => {
      setSocketStatus('disconnected');
      if (e.code !== 1000) { // If not normal closure
        setTimeout(setupWebSocket, 3000); // Reconnect after 3 seconds
      }
    };
  }, [stocks]);
  // Initialize WebSocket connection after initial data is loaded
  useEffect(() => {
    isMountedRef.current = true;
    
    if (loading || stocks.length === 0) return;
    
    setupWebSocket();

    return () => {
      isMountedRef.current = false;
      
      if (socketRef.current) {
        try {
          stocks.forEach(stock => {
            socketRef.current.send(JSON.stringify({ type: 'unsubscribe', symbol: `BSE:${stock.symbol}` }));
          });
          socketRef.current.send(JSON.stringify({ type: 'unsubscribe', symbol: 'NSE:NIFTY_50' }));
          socketRef.current.send(JSON.stringify({ type: 'unsubscribe', symbol: 'BSE:SENSEX' }));
          
          socketRef.current.close(1000, 'Component unmounting');
        } catch (err) {
          console.error('Error during WebSocket cleanup:', err);
        }
      }
      
      if (reconnectTimerRef.current) {
        clearTimeout(reconnectTimerRef.current);
      }
    };
  }, [loading, stocks]);

  // Initial data fetch
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const niftyResponse = await fetch(
          `https://finnhub.io/api/v1/quote?symbol=BSESN&token=${FINNHUB_API_KEY}`
        );
        
        const sensexResponse = await fetch(
          `https://finnhub.io/api/v1/quote?symbol=BSE:SENSEX&token=${FINNHUB_API_KEY}`
        );
        
        const niftyData = await niftyResponse.json();
        const sensexData = await sensexResponse.json();

        setMarketIndices([
          { 
            name: 'NIFTY', 
            number: '50', 
            value: niftyData.c, 
            change: niftyData.d, 
            changePercent: niftyData.dp 
          },
          { 
            name: 'SENSEX', 
            number: '', 
            value: sensexData.c, 
            change: sensexData.d, 
            changePercent: sensexData.dp 
          }
        ]);

        const stockSymbols = [
          'AAPL', 'MSFT', 'PYPL', 'BABA', 'MSTR', 
          'GOOGL', 'AMZN', 'META', 'TSLA', 'NVDA', 
          'INTC', 'AMD', 'DIS', 'NFLX', 'ASML', 
          'QCOM', 'F', 'GM', 'HD', 'COST'
        ];
        
        const stockPromises = stockSymbols.map(symbol => 
          fetch(`https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`)
        );

        const stockResponses = await Promise.all(stockPromises);
        const stockData = await Promise.all(stockResponses.map(res => res.json()));

        const initialStocks = stockSymbols.map((symbol, index) => ({
          symbol,
          price: stockData[index].c,
          change: stockData[index].d,
          changePercent: stockData[index].dp
        }));

        setStocks(initialStocks);
        setFilteredStocks(initialStocks);
        setLoading(false);
      } catch (err) {
        console.error('Initial fetch failed:', err);
        setError('Failed to load initial data. Trying real-time connection...');
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  const formatNumber = (num) => {
    if (num === undefined || num === null) return '--';
    return num.toLocaleString('en-IN', { maximumFractionDigits: 2 });
  };

  const fetchYahooChartData = async (symbol) => {
    try {
      const proxyUrl = 'https://api.allorigins.win/get?url=';
      const yahooUrl = `https://query1.finance.yahoo.com/v8/finance/chart/${symbol}?range=3mo&interval=1d`;
      
      const response = await fetch(proxyUrl + encodeURIComponent(yahooUrl));
      const data = await response.json();
      const parsedData = JSON.parse(data.contents);
      
      if (!parsedData.chart.result) {
        throw new Error('No chart data available');
      }
      
      const result = parsedData.chart.result[0];
      const timestamps = result.timestamp;
      const quotes = result.indicators.quote[0];
      
      const chartLabels = timestamps.map(timestamp => 
        new Date(timestamp * 1000).toLocaleDateString()
      );
      
      return {
        labels: chartLabels,
        datasets: [
          {
            label: `${symbol} Price`,
            data: quotes.close,
            borderColor: '#3a80e9',
            backgroundColor: 'rgba(58, 128, 233, 0.1)',
            borderWidth: 2,
            tension: 0.1,
            fill: true
          }
        ]
      };
    } catch (error) {
      console.error('Error fetching Yahoo Finance data:', error);
      throw error;
    }
  };

 const handleOptionClick = async (action, symbol, e, price, timeRange = '3mo') => {
  e.stopPropagation();
  
  if (action === 'Buy' || action === 'Sell') {
    setTradeModal({
      type: action.toUpperCase(),
      symbol,
      price
    });
  } else if (action === 'Chart') {
    try {
      setLoading(true);
      setError(null);
      
      // First try Finnhub
      try {
        const to = Math.floor(Date.now() / 1000);
        let from;
        
        // Set time range based on selection
        switch(timeRange) {
          case '1D':
            from = to - (24 * 60 * 60);
            break;
          case '5D':
            from = to - (5 * 24 * 60 * 60);
            break;
          case '1M':
            from = to - (30 * 24 * 60 * 60);
            break;
          case '6M':
            from = to - (6 * 30 * 24 * 60 * 60);
            break;
          case '1Y':
            from = to - (365 * 24 * 60 * 60);
            break;
          case '5Y':
            from = to - (5 * 365 * 24 * 60 * 60);
            break;
          default: // default to 3 months
            from = to - (90 * 24 * 60 * 60);
        }
        
        const response = await fetch(
          `https://finnhub.io/api/v1/stock/candle?symbol=${symbol}&resolution=${timeRange === '1D' ? '5' : 'D'}&from=${from}&to=${to}&token=${FINNHUB_API_KEY}`
        );
        
        const data = await response.json();
        
        if (data.s === 'ok' && data.c?.length > 0) {
          const chartLabels = data.t.map(timestamp => 
            timeRange === '1D' 
              ? new Date(timestamp * 1000).toLocaleTimeString() 
              : new Date(timestamp * 1000).toLocaleDateString()
          );
          
          const newChartData = {
  labels: chartLabels,
  datasets: [
    {
      label: `${symbol} Price`,  // Changed from chartSymbol to symbol
      data: data.c,
      borderColor: '#3a80e9',
      backgroundColor: 'rgba(58, 128, 233, 0.3)',
      borderWidth: 2,
      tension: 0, // Set to 0 for sharp edges
      fill: true
    }
  ],
  timeRange
};
          
          onShowChart(chartData, symbol);
          navigate('/dashboard');
          return;
        }
      } catch (finnhubError) {
        console.log('Finnhub failed, trying Yahoo Finance...');
      }
      
      // Fallback to Yahoo Finance
      let yahooRange;
      switch(timeRange) {
        case '1D': yahooRange = '1d'; break;
        case '5D': yahooRange = '5d'; break;
        case '1M': yahooRange = '1mo'; break;
        case '6M': yahooRange = '6mo'; break;
        case '1Y': yahooRange = '1y'; break;
        case '5Y': yahooRange = '5y'; break;
        default: yahooRange = '3mo';
      }
      
      const proxyUrl = 'https://api.allorigins.win/get?url=';
      const yahooUrl = `https://query1.finance.yahoo.com/v8/finance/chart/${symbol}?range=${yahooRange}&interval=${timeRange === '1D' ? '5m' : '1d'}`;
      
      const response = await fetch(proxyUrl + encodeURIComponent(yahooUrl));
      const yahooData = await response.json();
      const parsedData = JSON.parse(yahooData.contents);
      
      if (!parsedData.chart.result) {
        throw new Error('No chart data available');
      }
      
      const result = parsedData.chart.result[0];
      const timestamps = result.timestamp;
      const quotes = result.indicators.quote[0];
      
      const chartLabels = timestamps.map(timestamp => 
        timeRange === '1D' 
          ? new Date(timestamp * 1000).toLocaleTimeString() 
          : new Date(timestamp * 1000).toLocaleDateString()
      );
      
      const chartData = {
        labels: chartLabels,
        datasets: [
          {
            label: `${symbol} Price`,
            data: quotes.close,
            borderColor: '#3a80e9',
            backgroundColor: 'rgba(58, 128, 233, 0.1)',
            borderWidth: 2,
            tension: 0.1,
            fill: true
          }
        ],
        timeRange
      };
      
      onShowChart(chartData, symbol);
      navigate('/dashboard');
      
    } catch (err) {
      console.error('Error fetching chart data:', err);
      setError('Failed to load chart data. Please try again later.');
    } finally {
      setLoading(false);
    }
  }
};
 const handleGenerateSummary = (symbol, e) => {
  e.stopPropagation();
  navigate(`/ai-summary/${symbol}`);
};
  const handleTradeSubmit = async (tradeData) => {
  try {
    // Get token from localStorage
    const storedUser = localStorage.getItem('user');
    if (!storedUser) {
      throw new Error('User not authenticated');
    }
    
    const { token } = JSON.parse(storedUser);
    
    const response = await axios.post('/api/auth/trade', tradeData, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });

    if (response.data.success) {
      setWalletBalance(response.data.walletBalance);
      updateWalletBalance(response.data.walletBalance);
      setNotification({
        type: 'success',
        message: `${tradeData.type} order for ${tradeData.symbol} executed successfully!`
      });
      setTimeout(() => setNotification(null), 3000);
      setTradeModal(null);
    }
  } catch (err) {
    console.error('Trade error:', err);
    setNotification({
      type: 'error',
      message: err.response?.data?.message || 'Failed to execute trade. Please try again.'
    });
    setTimeout(() => setNotification(null), 3000);
  }
};


  if (loading) {
    return <div className="watchlist-loading">Loading market data...</div>;
  }

  return (
    <div className="watchlist">
      {notification && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}

      {tradeModal && (
        <BuySellModal
          type={tradeModal.type}
          symbol={tradeModal.symbol}
          price={tradeModal.price}
          onClose={() => setTradeModal(null)}
          onSubmit={handleTradeSubmit}
          walletBalance={walletBalance}
        />
      )}
{summaryModal && (
  <SummaryModal 
    symbol={summaryModal} 
    onClose={() => setSummaryModal(null)} 
  />
)}
      <div className={`connection-status ${socketStatus}`}>
        {socketStatus === 'connected' ? 'ðŸ”´ Live' : 
         socketStatus === 'connecting' ? 'Connecting...' : 
         socketStatus === 'error' ? 'Connection Error' : 'Disconnected'}
      </div>

      <div className="watchlist-search">
        <input
          type="text"
          placeholder="Search stocks..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      {error && (
        <div className="market-data-error">
          {error}
        </div>
      )}

      <div className="stock-list">
        {filteredStocks.length > 0 ? (
          filteredStocks.map((stock, i) => (
            <div 
              key={i} 
              className="stock-row"
              onMouseEnter={() => setHoveredStock(i)}
              onMouseLeave={() => setHoveredStock(null)}
            >
              <div className="stock-info">
                <div className="stock-symbol">{stock.symbol}</div>
                <div className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
                  {stock.change >= 0 ? '+' : ''}{formatNumber(stock.change)}
                </div>
              </div>
              <div className="stock-price">{formatNumber(stock.price)}</div>
              
              {hoveredStock === i && (
  <div className="stock-options-tooltip" onMouseEnter={() => setHoveredStock(i)}>
    <button 
      className="option-button buy" 
      onClick={(e) => handleOptionClick('Buy', stock.symbol, e, stock.price)}
    >
      Buy
    </button>
    <button 
      className="option-button sell" 
      onClick={(e) => handleOptionClick('Sell', stock.symbol, e, stock.price)}
    >
      Sell
    </button>
    <button 
      className="option-button chart" 
      onClick={(e) => handleOptionClick('Chart', stock.symbol, e)}
    >
      Chart
    </button>
    <button 
  className="option-button summary" 
  onClick={(e) => handleGenerateSummary(stock.symbol, e)}
>
  Summary
</button>
  </div>
)}
            </div>
          ))
        ) : (
          <div className="no-results">No stocks found matching your search</div>
        )}
      </div>
    </div>
  );
};

export default Watchlist;
