import React, { useState, useEffect } from 'react';
import './Watchlist.css';

const Watchlist = () => {
  const [marketIndices, setMarketIndices] = useState([
    { name: 'NIFTY', number: '50', value: 10865.65, change: -1.37 },
    { name: 'SENSEX', number: '', value: 35400.61, change: 1.41 }
  ]);

  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStocks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/stocks');
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        setStocks(data);
        setError(null);
      } catch (error) {
        console.error('Error fetching stocks:', error);
        setError(error.message);
        // Fallback mock data
        setStocks([
          { symbol: 'HDFCBANK', price: 1567.30, change: 8.75 },
          { symbol: 'INFY', price: 1678.90, change: -3.20 },
          { symbol: 'RELIANCE', price: 2456.75, change: 12.50 },
          { symbol: 'TCS', price: 3452.10, change: -5.25 }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchStocks();
  }, []);

  const formatNumber = (num) => {
    return num.toLocaleString('en-IN', { maximumFractionDigits: 2 });
  };

  if (loading) return <div className="watchlist-loading">Loading market data...</div>;

  return (
    <div className="watchlist">
      {/* Market Indices */}
      <div className="market-indices">
        {marketIndices.map((index, i) => (
          <div key={i} className="index-card">
            <div className="index-name">{index.name}</div>
            {index.number && <div className="index-number">{index.number}</div>}
            <div className="index-value">{formatNumber(index.value)}</div>
            <div className={`index-change ${index.change >= 0 ? 'positive' : 'negative'}`}>
              {index.change >= 0 ? '+' : ''}{index.change}%
            </div>
          </div>
        ))}
      </div>

      {/* Stock List */}
      <div className="stock-list">
        {error && (
          <div className="watchlist-error">
            {error} (Using mock data)
            <button onClick={() => window.location.reload()}>Retry</button>
          </div>
        )}
        
        {stocks.map((stock, i) => (
          <div key={i} className="stock-row">
            <div className="stock-info">
              <div className="stock-symbol">{stock.symbol}</div>
              <div className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
                {stock.change >= 0 ? '+' : ''}{stock.change}
              </div>
            </div>
            <div className="stock-price">{formatNumber(stock.price)}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Watchlist;
