import React, { useState, useEffect, useRef } from 'react';
import './Watchlist.css';

const Watchlist = () => {
  const FINNHUB_API_KEY = 'd17tfrhr01qteuvpuh10d17tfrhr01qteuvpuh1g';
  const [stocks, setStocks] = useState([
    { symbol: 'AMZN', price: 175.50, change: 2.30 },
    { symbol: 'MSFT', price: 420.72, change: -1.25 },
    { symbol: 'AAPL', price: 193.62, change: 0.85 },
    { symbol: 'GOOGL', price: 142.56, change: -0.42 },
    { symbol: 'TSLA', price: 182.63, change: 3.21 }
  ]);

  const [error, setError] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const ws = useRef(null);
  const retryCount = useRef(0);
  const maxRetries = 3;

  const connectWebSocket = () => {
    ws.current = new WebSocket(`wss://ws.finnhub.io?token=${FINNHUB_API_KEY}`);

    ws.current.onopen = () => {
      console.log('WebSocket connected');
      setIsConnected(true);
      retryCount.current = 0;
      
      stocks.forEach(stock => {
        ws.current.send(JSON.stringify({ type: 'subscribe', symbol: stock.symbol }));
      });
    };

    ws.current.onmessage = (e) => {
      try {
        const data = JSON.parse(e.data);
        if (data.type === 'trade' && data.p) {
          updateStockData(data);
        }
      } catch (err) {
        console.error('Error parsing WebSocket message:', err);
      }
    };

    ws.current.onerror = (err) => {
      console.error('WebSocket error:', err);
      setError('Realtime connection issue. Retrying...');
      setIsConnected(false);
    };

    ws.current.onclose = () => {
      setIsConnected(false);
      if (retryCount.current < maxRetries) {
        retryCount.current += 1;
        setTimeout(connectWebSocket, 3000 * retryCount.current);
      } else {
        setError('Failed to connect to realtime data. Using fallback API.');
        fetchInitialData();
      }
    };
  };

  const updateStockData = (data) => {
    setStocks(prev => 
      prev.map(stock => 
        stock.symbol === data.symbol && data.p
          ? { 
              ...stock, 
              price: data.p,
              change: parseFloat((data.p - (stock.price - stock.change)).toFixed(2)) || 0,
            }
          : stock
      )
    );
  };

  const fetchInitialData = async () => {
    try {
      const stockPromises = stocks.map(stock => 
        fetch(`https://finnhub.io/api/v1/quote?symbol=${stock.symbol}&token=${FINNHUB_API_KEY}`)
          .then(res => {
            if (!res.ok) throw new Error('Network response was not ok');
            return res.json();
          })
          .catch(err => {
            console.error(`Error fetching ${stock.symbol}:`, err);
            return { c: stock.price, pc: stock.price - stock.change };
          })
      );
      
      const stockResults = await Promise.all(stockPromises);
      
      setStocks(prev => 
        prev.map((stock, i) => ({
          ...stock,
          price: stockResults[i]?.c || stock.price,
          change: parseFloat(((stockResults[i]?.c || stock.price) - (stockResults[i]?.pc || (stock.price - stock.change)))).toFixed(2) || 0
        }))
      );
    } catch (err) {
      console.error('Fallback API failed:', err);
      setError('Unable to fetch latest data. Showing cached values.');
    }
  };

  useEffect(() => {
    connectWebSocket();
    
    return () => {
      if (ws.current) {
        stocks.forEach(stock => {
          ws.current.send(JSON.stringify({ type: 'unsubscribe', symbol: stock.symbol }));
        });
        ws.current.close();
      }
    };
  }, []);

  const formatNumber = (num) => {
    if (num === undefined || num === null || isNaN(num)) {
      return 'N/A';
    }
    return num.toLocaleString('en-US', { 
      minimumFractionDigits: 2,
      maximumFractionDigits: 2 
    });
  };

  return (
    <div className="watchlist">
      <div className="watchlist-header">
        <h2>Stock Watchlist</h2>
        <div className="connection-status">
          {isConnected ? 'ðŸŸ¢ Live' : 'ðŸ”´ Disconnected'}
          {error && <span className="error-message">{error}</span>}
        </div>
      </div>

      <div className="stock-table">
        <div className="stock-table-header">
          <div className="stock-header-cell">Symbol</div>
          <div className="stock-header-cell">Price</div>
          <div className="stock-header-cell">Change</div>
        </div>
        
        {stocks.map((stock, i) => (
          <div key={i} className="stock-row">
            <div className="stock-cell stock-symbol">{stock.symbol}</div>
            <div className="stock-cell stock-price">${formatNumber(stock.price)}</div>
            <div className={`stock-cell stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
              {stock.change >= 0 ? '+' : ''}{formatNumber(stock.change)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Watchlist;
