import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { Line } from 'react-chartjs-2';
import { useNavigate } from 'react-router-dom';
import NewsFeed from '../NewsFeed/NewsFeed';
import { useAuth } from '../../context/AuthContext';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';
import './Dashboard.css';
import axios from 'axios';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const Dashboard = ({ chartData, chartSymbol, showChart, onCloseChart }) => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const FINNHUB_API_KEY = process.env.REACT_APP_FINNHUB_API_KEY || 'd17tfrhr01qteuvpuh10d17tfrhr01qteuvpuh1g';
  
  // State management
  const [currentChartData, setCurrentChartData] = useState(chartData);
  const [currentTimeRange, setCurrentTimeRange] = useState('3mo');
  const [loading, setLoading] = useState({
    chart: false,
    prices: false
  });
  const [error, setError] = useState(null);
  const [marketPrices, setMarketPrices] = useState({});
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [wsInstance, setWsInstance] = useState(null);

  // Calculate all values using useMemo for performance
  const { holdings, totalInvestment, totalCurrentValue, totalPnl, totalPnlPercent } = useMemo(() => {
    if (!user?.positions?.length) return {
      holdings: [],
      totalInvestment: 0,
      totalCurrentValue: 0,
      totalPnl: 0,
      totalPnlPercent: 0
    };

    let totalInv = 0;
    let totalCurrVal = 0;
    
    const calculatedHoldings = user.positions.map(position => {
      const currentPrice = marketPrices[position.symbol] || position.ltp || position.avgPrice;
      const currentValue = position.quantity * currentPrice;
      const investment = position.quantity * position.avgPrice;
      const pnl = currentValue - investment;
      const pnlPercent = investment > 0 ? (pnl / investment) * 100 : 0;
      
      totalInv += investment;
      totalCurrVal += currentValue;
      
      return {
        ...position,
        currentValue,
        investment,
        pnl,
        pnlPercent,
        currentPrice
      };
    });
    
    const totalP = totalCurrVal - totalInv;
    const totalPP = totalInv > 0 ? (totalP / totalInv) * 100 : 0;
    
    return {
      holdings: calculatedHoldings,
      totalInvestment: totalInv,
      totalCurrentValue: totalCurrVal,
      totalPnl: totalP,
      totalPnlPercent: totalPP
    };
  }, [user, marketPrices]);

  // WebSocket management
  const setupWebSocket = useCallback((symbols) => {
    if (wsInstance) {
      wsInstance.close();
    }

    const ws = new WebSocket(`wss://ws.finnhub.io?token=${FINNHUB_API_KEY}`);
    setConnectionStatus('connecting');

    ws.onopen = () => {
      setConnectionStatus('connected');
      symbols.forEach(symbol => {
        ws.send(JSON.stringify({ type: 'subscribe', symbol }));
      });
    };

    ws.onmessage = (e) => {
      try {
        const data = JSON.parse(e.data);
        if (data.type === 'trade') {
          setMarketPrices(prev => ({
            ...prev,
            [data.s]: data.p
          }));
        }
      } catch (err) {
        console.error('Error processing WS message:', err);
      }
    };

    ws.onerror = (err) => {
      console.error('WebSocket error:', err);
      setConnectionStatus('error');
    };

    ws.onclose = () => {
      setConnectionStatus('disconnected');
    };

    setWsInstance(ws);
    return ws;
  }, [FINNHUB_API_KEY, wsInstance]);

  // Initialize WebSocket connection
  useEffect(() => {
    if (!user?.positions?.length) return;

    const symbols = user.positions.map(p => p.symbol);
    const ws = setupWebSocket(symbols);

    // Initial price fetch in case WebSocket is delayed
    fetchBatchPrices(symbols);

    return () => {
      if (ws && ws.readyState === WebSocket.OPEN) {
        symbols.forEach(symbol => {
          ws.send(JSON.stringify({ type: 'unsubscribe', symbol }));
        });
        ws.close();
      }
    };
  }, [user, setupWebSocket]);

  // Batch price fetching fallback
  const fetchBatchPrices = useCallback(async (symbols) => {
    if (!symbols.length) return;
    
    try {
      setLoading(prev => ({ ...prev, prices: true }));
      
      // Use batch endpoint if available (this is a placeholder - adjust based on your API)
      const response = await axios.get(
        `https://finnhub.io/api/v1/stock/symbol?exchange=US&token=${FINNHUB_API_KEY}`
      );
      
      const newPrices = {};
      response.data.forEach(stock => {
        if (symbols.includes(stock.symbol) && stock.price) {
          newPrices[stock.symbol] = stock.price;
        }
      });
      
      setMarketPrices(prev => ({ ...prev, ...newPrices }));
    } catch (err) {
      console.error('Batch price fetch failed:', err);
      // Fallback to individual fetching if batch fails
      await fetchIndividualPrices(symbols);
    } finally {
      setLoading(prev => ({ ...prev, prices: false }));
    }
  }, [FINNHUB_API_KEY]);

  // Individual price fetching as last resort
  const fetchIndividualPrices = useCallback(async (symbols) => {
    const BATCH_SIZE = 5; // Stay within rate limits
    const DELAY = 1000; // 1 second between batches
    
    for (let i = 0; i < symbols.length; i += BATCH_SIZE) {
      const batch = symbols.slice(i, i + BATCH_SIZE);
      const batchPrices = {};
      
      try {
        await Promise.all(batch.map(async symbol => {
          try {
            const response = await axios.get(
              `https://finnhub.io/api/v1/quote?symbol=${symbol}&token=${FINNHUB_API_KEY}`
            );
            if (response.data?.c) {
              batchPrices[symbol] = response.data.c;
            }
          } catch (err) {
            console.error(`Failed to fetch price for ${symbol}:`, err);
          }
        }));
        
        // Update prices after each batch
        setMarketPrices(prev => ({ ...prev, ...batchPrices }));
        
        // Respect rate limits
        if (i + BATCH_SIZE < symbols.length) {
          await new Promise(resolve => setTimeout(resolve, DELAY));
        }
      } catch (err) {
        console.error('Error in price batch:', err);
      }
    }
  }, [FINNHUB_API_KEY]);

  // Chart data handling
  useEffect(() => {
    if (showChart && chartData) {
      setCurrentChartData(chartData);
    }
  }, [showChart, chartData]);

  const getChartColors = (data) => {
    if (!data || !data.length) return { borderColor: '#3a80e9', backgroundColor: 'rgba(58, 128, 233, 0.3)' };
    const firstValue = data[0];
    const lastValue = data[data.length - 1];
    const isIncreasing = lastValue > firstValue;
    return {
      borderColor: isIncreasing ? '#4CAF50' : '#F44336',
      backgroundColor: isIncreasing ? 'rgba(76, 175, 80, 0.3)' : 'rgba(244, 67, 54, 0.3)'
    };
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        titleFont: {
          size: 14,
          weight: 'bold'
        },
        bodyFont: {
          size: 12
        },
        padding: 12,
        callbacks: {
          label: function(context) {
            let label = context.dataset.label || '';
            if (label) {
              label += ': ';
            }
            if (context.parsed.y !== null) {
              label += 'â‚¹' + context.parsed.y.toFixed(2);
            }
            return label;
          },
          title: function(context) {
            return context[0].label;
          }
        }
      }
    },
    elements: {
      point: {
        radius: 0,
        hoverRadius: 6,
        hoverBorderWidth: 2,
        hitRadius: 10
      },
      line: {
        tension: 0,
        fill: true,
        borderWidth: 2
      }
    },
    scales: {
      y: {
        beginAtZero: false,
        grid: {
          drawBorder: false,
          color: '#f0f0f0',
        },
        ticks: {
          color: '#666',
          callback: function(value) {
            return 'â‚¹' + value.toFixed(1);
          }
        }
      },
      x: {
        grid: {
          display: false,
          drawBorder: false,
        },
        ticks: {
          color: '#666',
          maxRotation: 0,
          autoSkip: true,
          maxTicksLimit: 8
        }
      }
    },
    interaction: {
      mode: 'index',
      intersect: false
    },
    hover: {
      mode: 'index',
      intersect: false
    }
  };

  const fetchChartData = async (timeRange) => {
    if (!chartSymbol) return;
    
    setLoading(prev => ({ ...prev, chart: true }));
    setError(null);
    
    try {
      // First try Finnhub
      try {
        const to = Math.floor(Date.now() / 1000);
        let from;
        let resolution = 'D';
        
        switch(timeRange) {
          case '1D':
            from = to - (24 * 60 * 60);
            resolution = '5';
            break;
          case '5D':
            from = to - (5 * 24 * 60 * 60);
            resolution = '15';
            break;
          case '1M':
            from = to - (30 * 24 * 60 * 60);
            break;
          case '6M':
            from = to - (6 * 30 * 24 * 60 * 60);
            break;
          case '1Y':
            from = to - (365 * 24 * 60 * 60);
            break;
          case '5Y':
            from = to - (5 * 365 * 24 * 60 * 60);
            break;
          default:
            from = to - (90 * 24 * 60 * 60);
        }
        
        const response = await axios.get(
          `https://finnhub.io/api/v1/stock/candle?symbol=${chartSymbol}&resolution=${resolution}&from=${from}&to=${to}&token=${FINNHUB_API_KEY}`
        );
        
        const data = response.data;
        
        if (data.s === 'ok' && data.c?.length > 0) {
          const chartLabels = data.t.map(timestamp => 
            timeRange === '1D' || timeRange === '5D'
              ? new Date(timestamp * 1000).toLocaleTimeString() 
              : new Date(timestamp * 1000).toLocaleDateString()
          );
          
          const colors = getChartColors(data.c);
          
          const newChartData = {
            labels: chartLabels,
            datasets: [
              {
                label: `${chartSymbol} Price`,
                data: data.c,
                borderColor: colors.borderColor,
                backgroundColor: colors.backgroundColor,
                borderWidth: 2,
                tension: 0,
                fill: true
              }
            ],
            timeRange
          };
          
          setCurrentChartData(newChartData);
          setCurrentTimeRange(timeRange);
          return;
        }
      } catch (finnhubError) {
        console.log('Finnhub failed, trying Yahoo Finance...');
      }
      
      // Fallback to Yahoo Finance
      let yahooRange;
      let interval;
      switch(timeRange) {
        case '1D': 
          yahooRange = '1d';
          interval = '5m';
          break;
        case '5D': 
          yahooRange = '5d';
          interval = '15m';
          break;
        case '1M': 
          yahooRange = '1mo';
          interval = '1d';
          break;
        case '6M': 
          yahooRange = '6mo';
          interval = '1d';
          break;
        case '1Y': 
          yahooRange = '1y';
          interval = '1wk';
          break;
        case '5Y': 
          yahooRange = '5y';
          interval = '1mo';
          break;
        default: 
          yahooRange = '3mo';
          interval = '1d';
      }
      
      const proxyUrl = 'https://api.allorigins.win/get?url=';
      const yahooUrl = `https://query1.finance.yahoo.com/v8/finance/chart/${chartSymbol}?range=${yahooRange}&interval=${interval}`;
      
      const response = await axios.get(proxyUrl + encodeURIComponent(yahooUrl));
      const parsedData = JSON.parse(response.data.contents);
      
      if (!parsedData.chart.result) {
        throw new Error('No chart data available');
      }
      
      const result = parsedData.chart.result[0];
      const timestamps = result.timestamp;
      const quotes = result.indicators.quote[0];
      
      const chartLabels = timestamps.map(timestamp => 
        timeRange === '1D' || timeRange === '5D'
          ? new Date(timestamp * 1000).toLocaleTimeString() 
          : new Date(timestamp * 1000).toLocaleDateString()
      );
      
      const colors = getChartColors(quotes.close);
      
      const newChartData = {
        labels: chartLabels,
        datasets: [
          {
            label: `${chartSymbol} Price`,
            data: quotes.close,
            borderColor: colors.borderColor,
            backgroundColor: colors.backgroundColor,
            borderWidth: 2,
            tension: 0,
            fill: true
          }
        ],
        timeRange
      };
      
      setCurrentChartData(newChartData);
      setCurrentTimeRange(timeRange);
      
    } catch (err) {
      console.error('Error fetching chart data:', err);
      setError('Failed to load chart data. Please try again later.');
    } finally {
      setLoading(prev => ({ ...prev, chart: false }));
    }
  };

  const getTimeRangeLabel = () => {
    switch(currentTimeRange) {
      case '1D': return '1 Day';
      case '5D': return '5 Days';
      case '1M': return '1 Month';
      case '6M': return '6 Months';
      case '1Y': return '1 Year';
      case '5Y': return '5 Years';
      default: return '3 Months';
    }
  };

  const connectionStatusIndicator = {
    connected: 'ðŸŸ¢ Live',
    connecting: 'ðŸŸ¡ Connecting...',
    disconnected: 'âšª Disconnected',
    error: 'ðŸ”´ Error'
  };

  return (
    <div className="dashboard-content">
      <h2>Dashboard Overview</h2>
      
      <div className="dashboard-sections">
        {/* Equity Section */}
        <div className="dashboard-card equity-card">
          <div className="card-header">
            <h3>Equity</h3>
          </div>
          <div className="card-content">
            <div className="card-value">
              {user?.walletBalance?.toLocaleString('en-IN', {
                maximumFractionDigits: 2,
                style: 'currency',
                currency: 'INR'
              }).replace('.00', '')}
              <span className="card-label">Margin available</span>
            </div>
            
            <div className="card-details">
              <div className="detail-item">
                <span className="detail-label">Margins used</span>
                <span className="detail-value">0</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Opening balance</span>
                <span className="detail-value">
                  {user?.walletBalance?.toLocaleString('en-IN', {
                    maximumFractionDigits: 2,
                    style: 'currency',
                    currency: 'INR'
                  }).replace('.00', '')}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Holdings Section */}
        <div className="dashboard-card holdings-card">
          <div className="card-header">
            <h3>Holdings ({holdings.length})</h3>
            <span className="connection-status">
              {connectionStatusIndicator[connectionStatus]}
            </span>
          </div>
          <div className="card-content">
            <div className="card-value">
              {totalPnl.toLocaleString('en-IN', {
                maximumFractionDigits: 2,
                style: 'currency',
                currency: 'INR'
              }).replace('.00', '')}
              <span className={`pnl-percent ${totalPnl >= 0 ? 'positive' : 'negative'}`}>
                {totalPnl >= 0 ? '+' : ''}{totalPnlPercent.toFixed(2)}%
              </span>
              <span className="card-label">P&L</span>
            </div>
            
            <div className="card-details">
              <div className="detail-item">
                <span className="detail-label">Current Value</span>
                <span className="detail-value">
                  {totalCurrentValue.toLocaleString('en-IN', {
                    maximumFractionDigits: 2,
                    style: 'currency',
                    currency: 'INR'
                  }).replace('.00', '')}
                </span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Investment</span>
                <span className="detail-value">
                  {totalInvestment.toLocaleString('en-IN', {
                    maximumFractionDigits: 2,
                    style: 'currency',
                    currency: 'INR'
                  }).replace('.00', '')}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {showChart && currentChartData && (
        <div className="chart-modal">
          <div className="chart-modal-content">
            <div className="chart-header">
              <h3>{chartSymbol} - {getTimeRangeLabel()} Price History</h3>
              <div className="chart-time-range-buttons">
                <button onClick={() => fetchChartData('1D')}>1D</button>
                <button onClick={() => fetchChartData('5D')}>5D</button>
                <button onClick={() => fetchChartData('1M')}>1M</button>
                <button onClick={() => fetchChartData('6M')}>6M</button>
                <button onClick={() => fetchChartData('1Y')}>1Y</button>
                <button onClick={() => fetchChartData('5Y')}>5Y</button>
              </div>
              <button className="close-chart" onClick={onCloseChart}>Ã—</button>
            </div>
            <div className="chart-container">
              {loading.chart ? (
                <div className="chart-loading">Loading chart data...</div>
              ) : error ? (
                <div className="chart-error">{error}</div>
              ) : (
                <Line data={currentChartData} options={chartOptions} />
              )}
            </div>
          </div>
        </div>
      )}
      
      <NewsFeed />
    </div>
  );
};

export default Dashboard;
