import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import Navbar from './components/Navbar/Navbar';
import Watchlist from './components/Watchlist/Watchlist';
import Dashboard from './components/Dashboard/Dashboard';
import Holdings from './components/Holdings/Holdings';
import Login from './components/Auth/Login';
import Signup from './components/Auth/Signup';
import PrivateRoute from './components/PrivateRoute';
import './App.css';

function App() {
  const [chartData, setChartData] = useState(null);
  const [chartSymbol, setChartSymbol] = useState('');
  const [showChart, setShowChart] = useState(false);

  const handleShowChart = (data, symbol) => {
    setChartData(data);
    setChartSymbol(symbol);
    setShowChart(true);
  };

  const handleCloseChart = () => {
    setShowChart(false);
    setChartData(null);
    setChartSymbol('');
  };

  return (
    <Router>
      <AuthProvider>
        <div className="app">
          <Navbar />
          <div className="main-content">
            <div className="left-section">
              <Watchlist onShowChart={handleShowChart} />
            </div>
            <div className="right-section">
              <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/signup" element={<Signup />} />
                <Route 
                  path="/dashboard" 
                  element={
                    <PrivateRoute>
                      <Dashboard 
                        chartData={chartData} 
                        chartSymbol={chartSymbol} 
                        showChart={showChart} 
                        onCloseChart={handleCloseChart} 
                      />
                    </PrivateRoute>
                  } 
                />
                <Route path="/holdings" element={<PrivateRoute><Holdings /></PrivateRoute>} />
                <Route path="/orders" element={<PrivateRoute><div>Orders Page</div></PrivateRoute>} />
                <Route path="/positions" element={<PrivateRoute><div>Positions Page</div></PrivateRoute>} />
                <Route path="/funds" element={<PrivateRoute><div>Funds Page</div></PrivateRoute>} />
                <Route path="/app" element={<PrivateRoute><div>App Settings</div></PrivateRoute>} />
                <Route path="/" element={<Navigate to="/dashboard" />} />
              </Routes>
            </div>
          </div>
        </div>
      </AuthProvider>
    </Router>
  );
}

export default App;
