// client/src/components/Positions/Positions.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';

const Positions = () => {
  const { user } = useAuth();
  const [positions, setPositions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user?.positions) {
      setPositions(user.positions);
      setLoading(false);
    }
  }, [user]);

  if (loading) {
    return <div>Loading positions...</div>;
  }

  return (
    <div className="positions">
      <h2>Your Positions</h2>
      {positions.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Product</th>
              <th>Instrument</th>
              <th>Qty.</th>
              <th>Avg.</th>
              <th>LTP</th>
              <th>P&L</th>
              <th>Chg.</th>
            </tr>
          </thead>
          <tbody>
            {positions.map((position, index) => {
              const pnl = (position.ltp - position.avgPrice) * position.quantity;
              const change = ((position.ltp - position.avgPrice) / position.avgPrice) * 100;
              
              return (
                <tr key={index}>
                  <td>{position.product}</td>
                  <td>{position.symbol}</td>
                  <td>{position.quantity}</td>
                  <td>₹{position.avgPrice.toFixed(2)}</td>
                  <td>₹{position.ltp.toFixed(2)}</td>
                  <td className={pnl >= 0 ? 'positive' : 'negative'}>
                    ₹{pnl.toFixed(2)}
                  </td>
                  <td className={change >= 0 ? 'positive' : 'negative'}>
                    {change.toFixed(2)}%
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      ) : (
        <p>You don't have any open positions.</p>
      )}
    </div>
  );
};

export default Positions;
