import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';
import './Auth.css';

const Signup = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [localError, setLocalError] = useState(''); // Renamed to avoid conflict with context error
  const { register, error: contextError } = useContext(AuthContext); // Destructure error from context
  const navigate = useNavigate();

  const { username, email, password, confirmPassword } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setLocalError('Passwords do not match');
      return;
    }
    try {
      await register({ username, email, password });
      // If registration is successful, navigate to dashboard
      navigate('/dashboard');
    } catch (err) {
      // The error will be handled by AuthContext, so we don't need to set it here
      console.error(err);
    }
  };

  return (
    <div className="auth-container">
      <h2>Register</h2>
      {/* Show either local error or context error */}
      {(localError || contextError) && (
        <div className="error-message">{localError || contextError}</div>
      )}
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label>Username</label>
          <input
            type="text"
            name="username"
            value={username}
            onChange={onChange}
            required
            minLength="3"
            maxLength="20"
          />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={email}
            onChange={onChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={password}
            onChange={onChange}
            required
            minLength="6"
          />
        </div>
        <div className="form-group">
          <label>Confirm Password</label>
          <input
            type="password"
            name="confirmPassword"
            value={confirmPassword}
            onChange={onChange}
            required
            minLength="6"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Register
        </button>
      </form>
      <p>
        Already have an account?{' '}
        <span onClick={() => navigate('/login')} className="auth-link">
          Login
        </span>
      </p>
    </div>
  );
};

export default Signup;
