import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import './AISummary.css';

const AISummary = () => {
  const { symbol } = useParams();
  const [analysis, setAnalysis] = useState({
    summary: '',
    loading: true,
    error: null,
    lastUpdated: null,
    rateLimited: false
  });
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [retryCount, setRetryCount] = useState(0);

  const fetchSummary = useCallback(async () => {
    try {
      const token = JSON.parse(localStorage.getItem('user'))?.token;
      if (!token) {
        logout();
        navigate('/login');
        return;
      }

      setAnalysis(prev => ({ ...prev, loading: true, error: null }));

      const response = await axios.post(
        '/api/auth/ai/summary',
        { symbol },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 60000
        }
      );

      setAnalysis({
        summary: response.data.summary,
        loading: false,
        error: null,
        lastUpdated: new Date(),
        rateLimited: false,
        model: response.data.model
      });

    } catch (error) {
      console.error('Analysis error:', error);
      
      let errorMessage = 'Failed to generate analysis';
      let shouldRetry = false;
      let retryDelay = 30000;

      if (error.response) {
        switch(error.response.status) {
          case 401:
            errorMessage = 'Session expired - please login again';
            logout();
            navigate('/login');
            break;
          case 429:
            errorMessage = 'Too many requests - please wait a minute';
            shouldRetry = true;
            retryDelay = 60000;
            break;
          case 503:
            errorMessage = 'AI service busy - try again in 30 seconds';
            shouldRetry = true;
            break;
          default:
            errorMessage = error.response.data?.message || 'Analysis failed';
        }
      } else if (error.code === 'ECONNABORTED') {
        errorMessage = 'Request timeout - the AI is taking longer than expected';
        shouldRetry = true;
      }

      setAnalysis(prev => ({
        ...prev,
        loading: false,
        error: errorMessage,
        rateLimited: shouldRetry
      }));

      if (shouldRetry && retryCount < 2) {
        setTimeout(() => {
          setRetryCount(prev => prev + 1);
          fetchSummary();
        }, retryDelay);
      }
    }
  }, [symbol, navigate, logout, retryCount]);

  useEffect(() => {
    if (symbol) {
      fetchSummary();
    } else {
      setAnalysis(prev => ({
        ...prev,
        loading: false,
        error: 'Stock symbol is required'
      }));
    }
  }, [symbol, fetchSummary]);

  const handleRetry = () => {
    setRetryCount(0);
    fetchSummary();
  };

  const formatContent = (text) => {
    if (!text) return null;
    
    return text.split('\n\n').map((section, index) => {
      if (section.startsWith('## ')) {
        return <h3 key={index}>{section.replace('## ', '')}</h3>;
      }
      return (
        <div key={index} className="analysis-section">
          {section.split('\n').map((paragraph, i) => (
            <p key={i}>{paragraph}</p>
          ))}
        </div>
      );
    });
  };

  if (analysis.loading) {
    return (
      <div className="ai-summary-container">
        <div className="ai-summary-header">
          <h2>AI Stock Analysis: {symbol}</h2>
          <div className="model-badge">{analysis.model || 'Gemini 2.0 Flash'}</div>
        </div>
        <div className="ai-summary-loading">
          <div className="loading-spinner"></div>
          <p>Generating comprehensive analysis for {symbol}...</p>
          {retryCount > 0 && (
            <p className="loading-subtext">Attempt {retryCount + 1} of 3</p>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>AI Stock Analysis: {symbol}</h2>
        <div className="model-badge">{analysis.model || 'Gemini 2.0 Flash'}</div>
        <div className="ai-summary-actions">
          <button onClick={() => navigate(-1)} className="back-button">
            Back to Watchlist
          </button>
          <button 
            onClick={handleRetry} 
            className={`refresh-button ${analysis.rateLimited ? 'rate-limited' : ''}`}
            disabled={analysis.loading}
          >
            {analysis.rateLimited ? 'Retrying...' : 'Refresh Analysis'}
          </button>
        </div>
      </div>
      
      {analysis.error ? (
        <div className={`ai-summary-error ${analysis.rateLimited ? 'rate-limited' : ''}`}>
          <div className="error-icon">⚠️</div>
          <p>{analysis.error}</p>
          {!analysis.rateLimited && (
            <button onClick={handleRetry} className="retry-button">
              Try Again
            </button>
          )}
        </div>
      ) : (
        <>
          {analysis.lastUpdated && (
            <div className="last-updated">
              Last updated: {analysis.lastUpdated.toLocaleString()}
            </div>
          )}
          <div className="ai-summary-content">
            {formatContent(analysis.summary)}
          </div>
          <div className="disclaimer">
            <p>Disclaimer: This AI-generated analysis is for informational purposes only and should not be considered financial advice. Data may be delayed or inaccurate.</p>
          </div>
        </>
      )}
    </div>
  );
};

export default AISummary;
