import React, { useState, useEffect, useRef } from 'react';
import './Watchlist.css';

const Watchlist = () => {
  const FINNHUB_API_KEY = 'd17stm1r01qteuvpqihgd17stm1r01qteuvpqii0'; // Your API key
  const [stocks, setStocks] = useState([
    { symbol: 'AMZN', price: 175.50, change: 2.30 },
    { symbol: 'MSFT', price: 420.72, change: -1.25 }
  ]);

  const [indices, setIndices] = useState([
    { name: 'NASDAQ', value: 17685.65, change: -1.37, changePercent: -1.37 },
    { name: 'DOW JONES', value: 38400.61, change: 1.41, changePercent: 1.41 }
  ]);

  const [error, setError] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const ws = useRef(null);
  const retryCount = useRef(0);
  const maxRetries = 3;

  // WebSocket connection management
  const connectWebSocket = () => {
    ws.current = new WebSocket(`wss://ws.finnhub.io?token=${FINNHUB_API_KEY}`);

    ws.current.onopen = () => {
      console.log('WebSocket connected');
      setIsConnected(true);
      retryCount.current = 0;
      
      // Subscribe to symbols
      const symbols = [
        ...stocks.map(stock => stock.symbol),
        '^IXIC', // NASDAQ
        '^DJI'   // DOW JONES
      ];
      
      symbols.forEach(symbol => {
        ws.current.send(JSON.stringify({ type: 'subscribe', symbol }));
      });
    };

    ws.current.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.type === 'trade') {
        updateData(data);
      }
    };

    ws.current.onerror = (err) => {
      console.error('WebSocket error:', err);
      setError('Realtime connection issue. Retrying...');
      setIsConnected(false);
    };

    ws.current.onclose = () => {
      setIsConnected(false);
      if (retryCount.current < maxRetries) {
        retryCount.current += 1;
        setTimeout(connectWebSocket, 3000 * retryCount.current); // Exponential backoff
      } else {
        setError('Failed to connect to realtime data. Using fallback API.');
        fetchInitialData(); // Fallback to REST API
      }
    };
  };

  // Update stocks and indices
  const updateData = (data) => {
    const symbol = data.symbol;
    
    // Update stocks
    setStocks(prev => 
      prev.map(stock => 
        stock.symbol === symbol
          ? { 
              ...stock, 
              price: data.p,
              change: parseFloat((data.p - (stock.price - stock.change)).toFixed(2)) 
            }
          : stock
      )
    );

    // Update indices
    if (symbol === '^IXIC' || symbol === '^DJI') {
      const indexName = symbol === '^IXIC' ? 'NASDAQ' : 'DOW JONES';
      setIndices(prev => 
        prev.map(index => 
          index.name === indexName
            ? {
                ...index,
                value: data.p,
                change: parseFloat((data.p - (index.value - index.change)).toFixed(2)),
                changePercent: parseFloat(((data.p - (index.value - index.change)) / (index.value - index.change) * 100).toFixed(2))
              }
            : index
        )
      );
    }
  };

  // Fallback REST API
  const fetchInitialData = async () => {
    try {
      // Fetch all stock data
      const stockPromises = stocks.map(stock => 
        fetch(`https://finnhub.io/api/v1/quote?symbol=${stock.symbol}&token=${FINNHUB_API_KEY}`)
          .then(res => res.json())
      );
      
      const stockResults = await Promise.all(stockPromises);
      
      setStocks(prev => 
        prev.map((stock, i) => ({
          ...stock,
          price: stockResults[i].c,
          change: parseFloat((stockResults[i].c - stockResults[i].pc).toFixed(2))
        }))
      );

      // Fetch indices
      const [nasdaq, dow] = await Promise.all([
        fetch(`https://finnhub.io/api/v1/quote?symbol=^IXIC&token=${FINNHUB_API_KEY}`).then(res => res.json()),
        fetch(`https://finnhub.io/api/v1/quote?symbol=^DJI&token=${FINNHUB_API_KEY}`).then(res => res.json())
      ]);

      setIndices([
        { 
          name: 'NASDAQ', 
          value: nasdaq.c, 
          change: parseFloat((nasdaq.c - nasdaq.pc).toFixed(2)),
          changePercent: parseFloat(((nasdaq.c - nasdaq.pc) / nasdaq.pc * 100).toFixed(2))
        },
        { 
          name: 'DOW JONES', 
          value: dow.c, 
          change: parseFloat((dow.c - dow.pc).toFixed(2)),
          changePercent: parseFloat(((dow.c - dow.pc) / dow.pc * 100).toFixed(2))
        }
      ]);

    } catch (err) {
      console.error('Fallback API failed:', err);
      setError('Unable to fetch latest data. Showing cached values.');
    }
  };

  useEffect(() => {
    connectWebSocket();
    
    return () => {
      if (ws.current) {
        ws.current.close();
      }
    };
  }, []);

  const formatNumber = (num) => {
    return num.toLocaleString('en-US', { maximumFractionDigits: 2 });
  };

  return (
    <div className="watchlist">
      <div className="connection-status">
        {isConnected ? 'ðŸŸ¢ Live' : 'ðŸ”´ Disconnected'}
        {error && <span className="error-message">{error}</span>}
      </div>

      {/* Indices */}
      <div className="market-indices">
        {indices.map((index, i) => (
          <div key={i} className="index-card">
            <div className="index-name">{index.name}</div>
            <div className="index-value">{formatNumber(index.value)}</div>
            <div className={`index-change ${index.change >= 0 ? 'positive' : 'negative'}`}>
              {index.change >= 0 ? '+' : ''}{index.change} ({index.changePercent}%)
            </div>
          </div>
        ))}
      </div>

      {/* Stocks */}
      <div className="stock-list">
        {stocks.map((stock, i) => (
          <div key={i} className="stock-row">
            <div className="stock-symbol">{stock.symbol}</div>
            <div className="stock-price">{formatNumber(stock.price)}</div>
            <div className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
              {stock.change >= 0 ? '+' : ''}{stock.change}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Watchlist;
