import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import './AISummary.css';

const AISummary = () => {
  const { symbol } = useParams();
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        // Get the token from where you stored it during login
        const storedUser = JSON.parse(localStorage.getItem('user'));
        const token = storedUser?.token;

        if (!token) {
          logout();
          navigate('/login');
          return;
        }

        const response = await axios.post(
          `${process.env.REACT_APP_API_URL || ''}/api/auth/ai/summary`,
          { symbol },
          {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            },
            withCredentials: true
          }
        );

        if (!response.data.success) {
          throw new Error(response.data.message || 'Failed to generate summary');
        }

        setSummary(response.data.summary);
      } catch (err) {
        console.error('Error fetching AI summary:', err);
        
        let errorMessage = 'Failed to generate summary';
        if (err.response?.status === 401) {
          errorMessage = 'Session expired. Please login again.';
          logout();
          navigate('/login');
        } else if (err.response?.status === 429) {
          errorMessage = 'Too many requests. Please try again later.';
        } else if (err.response?.data?.message) {
          errorMessage = err.response.data.message;
        }
        
        setError(errorMessage);
      } finally {
        setLoading(false);
      }
    };

    if (symbol) {
      fetchSummary();
    }
  }, [symbol, navigate, logout]);

  if (loading) {
    return <div className="ai-summary-loading">Generating AI summary for {symbol}...</div>;
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>AI Stock Analysis: {symbol}</h2>
        <button onClick={() => navigate(-1)} className="back-button">
          Back to Watchlist
        </button>
      </div>
      
      {error ? (
        <div className="ai-summary-error">{error}</div>
      ) : (
        <div className="ai-summary-content">
          {summary.split('\n').map((paragraph, index) => (
            <p key={index}>{paragraph}</p>
          ))}
        </div>
      )}
    </div>
  );
};

export default AISummary;
