import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';

const Positions = () => {
  const { user, isAuthenticated } = useAuth();
  const [positions, setPositions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPositions = async () => {
      if (!isAuthenticated) {
        setLoading(false);
        return;
      }

      try {
        const token = JSON.parse(localStorage.getItem('user'))?.token;
        if (!token) {
          throw new Error('No authentication token found');
        }

        const response = await axios.get('/api/auth/user', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (response.data.success) {
          setPositions(response.data.user.positions || []);
        }
      } catch (err) {
        console.error('Error fetching positions:', err);
        setError('Failed to load positions. Please try again.');
        // Fallback to user.positions if available
        if (user?.positions) {
          setPositions([...user.positions]);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchPositions();
  }, [user, isAuthenticated]);

  if (loading) {
    return <div className="loading">Loading positions...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="positions">
      <h2>Your Positions</h2>
      {positions && positions.length > 0 ? (
        <div className="table-responsive">
          <table className="positions-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Instrument</th>
                <th>Qty.</th>
                <th>Avg.</th>
                <th>LTP</th>
                <th>P&L</th>
                <th>Chg.</th>
              </tr>
            </thead>
            <tbody>
              {positions.map((position, index) => {
                const pnl = ((position.ltp || 0) - (position.avgPrice || 0)) * (position.quantity || 0);
                const change = position.avgPrice 
                  ? (((position.ltp || 0) - position.avgPrice) / position.avgPrice) * 100
                  : 0;
                
                return (
                  <tr key={index}>
                    <td>{position.product || 'CNC'}</td>
                    <td>{position.symbol || 'N/A'}</td>
                    <td>{position.quantity || 0}</td>
                    <td>₹{(position.avgPrice || 0).toFixed(2)}</td>
                    <td>₹{(position.ltp || 0).toFixed(2)}</td>
                    <td className={pnl >= 0 ? 'positive' : 'negative'}>
                      ₹{pnl.toFixed(2)}
                    </td>
                    <td className={change >= 0 ? 'positive' : 'negative'}>
                      {change.toFixed(2)}%
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="no-positions">You don't have any open positions.</p>
      )}
    </div>
  );
};

export default Positions;
