// client/src/components/Holdings/Holdings.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';

const Holdings = () => {
  const { user } = useAuth();
  const [holdings, setHoldings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user?.holdings) {
      setHoldings(user.holdings);
      setLoading(false);
    }
  }, [user]);

  if (loading) {
    return <div>Loading holdings...</div>;
  }

  return (
    <div className="holdings">
      <h2>Your Holdings</h2>
      {holdings.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Symbol</th>
              <th>Quantity</th>
              <th>Avg. Price</th>
              <th>Invested</th>
              <th>Product</th>
            </tr>
          </thead>
          <tbody>
            {holdings.map((holding, index) => (
              <tr key={index}>
                <td>{holding.symbol}</td>
                <td>{holding.quantity}</td>
                <td>₹{holding.avgPrice.toFixed(2)}</td>
                <td>₹{(holding.quantity * holding.avgPrice).toFixed(2)}</td>
                <td>{holding.product}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>You don't have any holdings yet.</p>
      )}
    </div>
  );
};

export default Holdings;
