import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import './Dashboard.css';
import axios from 'axios';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = ({ chartData, chartSymbol, showChart, onCloseChart }) => {
  const [currentChartData, setCurrentChartData] = useState(chartData);
  const [currentTimeRange, setCurrentTimeRange] = useState('3mo');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const FINNHUB_API_KEY = 'd17tfrhr01qteuvpuh10d17tfrhr01qteuvpuh1g';

  useEffect(() => {
    if (showChart && chartData) {
      setCurrentChartData(chartData);
    }
  }, [showChart, chartData]);

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        enabled: true,
        mode: 'index',
        intersect: false,
        callbacks: {
          label: function(context) {
            return `₹${context.parsed.y.toFixed(2)}`;
          }
        }
      },
    },
    scales: {
      y: {
        display: true,
        position: 'left',
        grid: {
          drawBorder: false,
          color: '#f0f0f0',
        },
        ticks: {
          color: '#666',
          font: {
            size: 12,
          },
          callback: function(value) {
            return value.toFixed(1);
          }
        }
      },
      x: {
        grid: {
          display: false,
          drawBorder: false,
        },
        ticks: {
          color: '#666',
          font: {
            size: 12,
          },
          maxRotation: 0,
          autoSkip: true,
          maxTicksLimit: 8
        }
      }
    },
    elements: {
      point: {
        radius: 0,
        hitRadius: 10,
        hoverRadius: 5,
      },
      line: {
        borderWidth: 2,
        tension: 0.1,
        fill: true,
      }
    },
    interaction: {
      mode: 'index',
      intersect: false,
    }
  };

  const fetchChartData = async (timeRange) => {
    if (!chartSymbol) return;
    
    setLoading(true);
    setError(null);
    
    try {
      // First try Finnhub
      try {
        const to = Math.floor(Date.now() / 1000);
        let from;
        let resolution = 'D';
        
        // Set time range based on selection
        switch(timeRange) {
          case '1D':
            from = to - (24 * 60 * 60);
            resolution = '5';
            break;
          case '5D':
            from = to - (5 * 24 * 60 * 60);
            resolution = '15';
            break;
          case '1M':
            from = to - (30 * 24 * 60 * 60);
            break;
          case '6M':
            from = to - (6 * 30 * 24 * 60 * 60);
            break;
          case '1Y':
            from = to - (365 * 24 * 60 * 60);
            break;
          case '5Y':
            from = to - (5 * 365 * 24 * 60 * 60);
            break;
          default: // default to 3 months
            from = to - (90 * 24 * 60 * 60);
        }
        
        const response = await axios.get(
          `https://finnhub.io/api/v1/stock/candle?symbol=${chartSymbol}&resolution=${resolution}&from=${from}&to=${to}&token=${FINNHUB_API_KEY}`
        );
        
        const data = response.data;
        
        if (data.s === 'ok' && data.c?.length > 0) {
          const chartLabels = data.t.map(timestamp => 
            timeRange === '1D' || timeRange === '5D'
              ? new Date(timestamp * 1000).toLocaleTimeString() 
              : new Date(timestamp * 1000).toLocaleDateString()
          );
          
          const newChartData = {
            labels: chartLabels,
            datasets: [
              {
                label: `${chartSymbol} Price`,
                data: data.c,
                borderColor: '#3a80e9',
                backgroundColor: 'rgba(58, 128, 233, 0.1)',
                borderWidth: 2,
                tension: 0.1,
                fill: true
              }
            ],
            timeRange
          };
          
          setCurrentChartData(newChartData);
          setCurrentTimeRange(timeRange);
          return;
        }
      } catch (finnhubError) {
        console.log('Finnhub failed, trying Yahoo Finance...');
      }
      
      // Fallback to Yahoo Finance
      let yahooRange;
      let interval;
      switch(timeRange) {
        case '1D': 
          yahooRange = '1d';
          interval = '5m';
          break;
        case '5D': 
          yahooRange = '5d';
          interval = '15m';
          break;
        case '1M': 
          yahooRange = '1mo';
          interval = '1d';
          break;
        case '6M': 
          yahooRange = '6mo';
          interval = '1d';
          break;
        case '1Y': 
          yahooRange = '1y';
          interval = '1wk';
          break;
        case '5Y': 
          yahooRange = '5y';
          interval = '1mo';
          break;
        default: 
          yahooRange = '3mo';
          interval = '1d';
      }
      
      const proxyUrl = 'https://api.allorigins.win/get?url=';
      const yahooUrl = `https://query1.finance.yahoo.com/v8/finance/chart/${chartSymbol}?range=${yahooRange}&interval=${interval}`;
      
      const response = await axios.get(proxyUrl + encodeURIComponent(yahooUrl));
      const parsedData = JSON.parse(response.data.contents);
      
      if (!parsedData.chart.result) {
        throw new Error('No chart data available');
      }
      
      const result = parsedData.chart.result[0];
      const timestamps = result.timestamp;
      const quotes = result.indicators.quote[0];
      
      const chartLabels = timestamps.map(timestamp => 
        timeRange === '1D' || timeRange === '5D'
          ? new Date(timestamp * 1000).toLocaleTimeString() 
          : new Date(timestamp * 1000).toLocaleDateString()
      );
      
      const newChartData = {
        labels: chartLabels,
        datasets: [
          {
            label: `${chartSymbol} Price`,
            data: quotes.close,
            borderColor: '#3a80e9',
            backgroundColor: 'rgba(58, 128, 233, 0.1)',
            borderWidth: 2,
            tension: 0.1,
            fill: true
          }
        ],
        timeRange
      };
      
      setCurrentChartData(newChartData);
      setCurrentTimeRange(timeRange);
      
    } catch (err) {
      console.error('Error fetching chart data:', err);
      setError('Failed to load chart data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const getTimeRangeLabel = () => {
    switch(currentTimeRange) {
      case '1D': return '1 Day';
      case '5D': return '5 Days';
      case '1M': return '1 Month';
      case '6M': return '6 Months';
      case '1Y': return '1 Year';
      case '5Y': return '5 Years';
      default: return '3 Months';
    }
  };

  return (
    <div className="dashboard-content">
      <h2>Dashboard Overview</h2>
      <p>Your portfolio summary and analytics will appear here.</p>

      {/* Chart Modal */}
      {showChart && currentChartData && (
        <div className="chart-modal">
          <div className="chart-modal-content">
            <div className="chart-header">
              <h3>{chartSymbol} - {getTimeRangeLabel()} Price History</h3>
              <div className="chart-time-range-buttons">
                <button 
                  className={currentTimeRange === '1D' ? 'active' : ''}
                  onClick={() => fetchChartData('1D')}
                >
                  1D
                </button>
                <button 
                  className={currentTimeRange === '5D' ? 'active' : ''}
                  onClick={() => fetchChartData('5D')}
                >
                  5D
                </button>
                <button 
                  className={currentTimeRange === '1M' ? 'active' : ''}
                  onClick={() => fetchChartData('1M')}
                >
                  1M
                </button>
                <button 
                  className={currentTimeRange === '6M' ? 'active' : ''}
                  onClick={() => fetchChartData('6M')}
                >
                  6M
                </button>
                <button 
                  className={currentTimeRange === '1Y' ? 'active' : ''}
                  onClick={() => fetchChartData('1Y')}
                >
                  1Y
                </button>
                <button 
                  className={currentTimeRange === '5Y' ? 'active' : ''}
                  onClick={() => fetchChartData('5Y')}
                >
                  5Y
                </button>
              </div>
              <button className="close-chart" onClick={onCloseChart}>×</button>
            </div>
            <div className="chart-container">
              {loading ? (
                <div className="chart-loading">Loading chart data...</div>
              ) : error ? (
                <div className="chart-error">{error}</div>
              ) : (
                <Line data={currentChartData} options={chartOptions} />
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
