import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import './AISummary.css';

const AISummary = () => {
  const { symbol } = useParams();
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [state, setState] = useState({
    summary: '',
    loading: true,
    error: null,
    model: null,
    lastUpdated: null,
    retryCount: 0
  });

  const fetchSummary = useCallback(async () => {
  try {
    // Get token correctly from localStorage
    const userData = JSON.parse(localStorage.getItem('user'));
    const token = userData?.token;
    
    if (!token) {
      logout();
      navigate('/login');
      return;
    }

    setState(prev => ({ ...prev, loading: true, error: null }));

    const response = await axios.post(
      '/api/auth/ai/summary',
      { symbol },
      {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        timeout: 20000
      }
    );


      setState({
        summary: response.data.summary,
        loading: false,
        error: null,
        model: response.data.modelUsed,
        lastUpdated: new Date(),
        retryCount: 0
      });

    } catch (error) {
      console.error('Analysis error:', error);
      
      const isRateLimit = error.response?.status === 429;
      const canRetry = state.retryCount < 2 && !isRateLimit;

      setState(prev => ({
        ...prev,
        loading: false,
        error: isRateLimit 
          ? 'Too many requests. Please wait a minute.' 
          : 'Failed to load analysis. ' + (canRetry ? 'Retrying...' : ''),
        retryCount: prev.retryCount + 1
      }));

      if (canRetry) {
        setTimeout(fetchSummary, 3000 * state.retryCount);
      }
    }
  }, [symbol, navigate, logout, state.retryCount]);

  useEffect(() => {
    if (symbol) {
      fetchSummary();
    } else {
      setState(prev => ({
        ...prev,
        loading: false,
        error: 'Stock symbol is required'
      }));
    }
  }, [symbol, fetchSummary]);

  const formatContent = (text) => {
    if (!text) return null;
    
    return text.split('\n\n').map((section, index) => (
      <div key={index} className="analysis-section">
        {section.split('\n').map((paragraph, i) => (
          <p key={i}>{paragraph}</p>
        ))}
      </div>
    ));
  };

  if (state.loading) {
    return (
      <div className="ai-summary-container">
        <div className="ai-summary-header">
          <h2>AI Analysis: {symbol}</h2>
          <div className="loading-spinner"></div>
          <p>Generating analysis...</p>
          {state.retryCount > 0 && (
            <p>Attempt {state.retryCount + 1} of 3</p>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>AI Analysis: {symbol}</h2>
        {state.model && <div className="model-badge">{state.model}</div>}
        <div className="ai-summary-actions">
          <button onClick={() => navigate(-1)} className="back-button">
            Back
          </button>
          <button 
            onClick={fetchSummary} 
            className="refresh-button"
            disabled={state.loading}
          >
            Refresh
          </button>
        </div>
      </div>
      
      {state.error ? (
        <div className="ai-summary-error">
          <p>{state.error}</p>
          {state.retryCount < 2 && !state.error.includes('wait') && (
            <button onClick={fetchSummary} className="retry-button">
              Try Again
            </button>
          )}
        </div>
      ) : (
        <div className="ai-summary-content">
          {formatContent(state.summary)}
          {state.lastUpdated && (
            <div className="last-updated">
              Last updated: {state.lastUpdated.toLocaleString()}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default AISummary;
