import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { createChart } from 'lightweight-charts';
import './AISummary.css';

const AISummary = () => {
  const { symbol } = useParams();
  const navigate = useNavigate();
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState('');
  const [customQueryResult, setCustomQueryResult] = useState(null);
  const [isCustomQuery, setIsCustomQuery] = useState(false);
  const [stockSearch, setStockSearch] = useState('');
  const [news, setNews] = useState([]);
  const [newsSummary, setNewsSummary] = useState('');
  const [activeTab, setActiveTab] = useState('analysis');
  const [prediction, setPrediction] = useState(null);
  const [sentiment, setSentiment] = useState(null);
  
  const chartContainerRef = useRef();
  const chartInstance = useRef(null);

  // API Keys
  const FINNHUB_API_KEY = 'd1aopp9r01qjhvtqeebgd1aopp9r01qjhvtqeec0';
  const ALPHA_VANTAGE_KEY = 'OH0ZQBRUAB4ABC4X';

  // Clean up chart instance safely
  const cleanupChart = useCallback(() => {
    if (chartInstance.current) {
      try {
        chartInstance.current.remove();
      } catch (e) {
        console.warn("Error during chart cleanup:", e);
      } finally {
        chartInstance.current = null;
      }
    }
  }, []);

  // Render chart with data
  const renderChart = useCallback((data) => {
    cleanupChart();
    
    const container = chartContainerRef.current;
    if (!container || !data || data.length === 0) return;
    
    container.innerHTML = ''; // Clear previous content
    
    const chart = createChart(container, {
      width: container.clientWidth,
      height: 400,
      layout: {
        backgroundColor: '#ffffff',
        textColor: '#333',
      },
      grid: {
        vertLines: {
          color: '#eee',
        },
        horzLines: {
          color: '#eee',
        },
      },
      crosshair: {
        mode: 'normal',
      },
      timeScale: {
        borderColor: '#ccc',
      },
    });
    
    chartInstance.current = chart;
    
    const candleSeries = chart.addCandlestickSeries({
      upColor: '#26a69a',
      downColor: '#ef5350',
      borderVisible: false,
      wickUpColor: '#26a69a',
      wickDownColor: '#ef5350',
    });
    
    candleSeries.setData(data);
    
    const volumeSeries = chart.addHistogramSeries({
      color: '#26a69a',
      priceFormat: {
        type: 'volume',
      },
      priceScaleId: '',
      scaleMargins: {
        top: 0.8,
        bottom: 0,
      },
    });
    
    volumeSeries.setData(data.map(item => ({
      time: item.time,
      value: item.volume,
      color: item.close > item.open ? '#26a69a' : '#ef5350'
    })));
    
    chart.timeScale().fitContent();
  }, [cleanupChart]);

  // Fetch stock chart data from Alpha Vantage
  const fetchStockChartData = useCallback(async (stockSymbol) => {
    try {
      const response = await axios.get(
        `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY&symbol=${stockSymbol}&apikey=${ALPHA_VANTAGE_KEY}`
      );
      
      if (response.data['Time Series (Daily)']) {
        const timeSeries = response.data['Time Series (Daily)'];
        const chartData = Object.entries(timeSeries).map(([time, values]) => ({
          time: new Date(time).getTime() / 1000,
          open: parseFloat(values['1. open']),
          high: parseFloat(values['2. high']),
          low: parseFloat(values['3. low']),
          close: parseFloat(values['4. close']),
          volume: parseFloat(values['5. volume'])
        })).sort((a, b) => a.time - b.time).slice(-30); // Last 30 days
        
        renderChart(chartData);
      } else {
        throw new Error('No daily data available from Alpha Vantage');
      }
    } catch (err) {
      console.error("Error fetching chart data:", err);
      setError('Failed to load chart data. Showing summary only.');
    }
  }, [ALPHA_VANTAGE_KEY, renderChart]);

  // Generate basic prediction without external API
  const generatePrediction = useCallback(async (stockSymbol) => {
    try {
      // Get recent price data for more accurate prediction
      const response = await axios.get(
        `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY&symbol=${stockSymbol}&apikey=${ALPHA_VANTAGE_KEY}`
      );
      
      let priceTrend = 'neutral';
      let volatility = 'medium';
      
      if (response.data['Time Series (Daily)']) {
        const timeSeries = response.data['Time Series (Daily)'];
        const prices = Object.values(timeSeries).slice(0, 5); // Last 5 days
        const changes = prices.map((day, i) => {
          if (i === 0) return 0;
          return (parseFloat(day['4. close']) - parseFloat(prices[i-1]['4. close'])) / parseFloat(prices[i-1]['4. close']);
        });
        
        const avgChange = changes.reduce((sum, change) => sum + change, 0) / changes.length;
        priceTrend = avgChange > 0.01 ? 'bullish' : avgChange < -0.01 ? 'bearish' : 'neutral';
        
        // Calculate volatility
        const changesAbs = changes.map(change => Math.abs(change));
        const avgVolatility = changesAbs.reduce((sum, change) => sum + change, 0) / changesAbs.length;
        volatility = avgVolatility > 0.02 ? 'high' : avgVolatility < 0.005 ? 'low' : 'medium';
      }
      
      // Get recent news sentiment
      const newsResponse = await axios.get(
        `https://finnhub.io/api/v1/company-news?symbol=${stockSymbol}&from=${new Date(Date.now() - 3 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}&to=${new Date().toISOString().split('T')[0]}&token=${FINNHUB_API_KEY}`
      );
      
      let sentimentScore = 0;
      if (newsResponse.data && newsResponse.data.length > 0) {
        const recentNews = newsResponse.data.slice(0, 5);
        sentimentScore = recentNews.reduce((sum, newsItem) => {
          // Simple sentiment analysis based on headline keywords
          const headline = newsItem.headline.toLowerCase();
          let score = 0;
          if (headline.includes('up') || headline.includes('rise') || headline.includes('gain')) score += 0.2;
          if (headline.includes('down') || headline.includes('fall') || headline.includes('drop')) score -= 0.2;
          if (headline.includes('strong') || headline.includes('beat')) score += 0.3;
          if (headline.includes('weak') || headline.includes('miss')) score -= 0.3;
          return sum + score;
        }, 0) / recentNews.length;
      }
      
      // Generate prediction based on factors
      let predictionText = `ðŸ“ˆ Smart Prediction for ${stockSymbol} (Next 24 Hours):\n\n`;
      
      // Determine direction
      let direction, confidence;
      const combinedScore = (priceTrend === 'bullish' ? 0.3 : priceTrend === 'bearish' ? -0.3 : 0) + sentimentScore;
      
      if (combinedScore > 0.2) {
        direction = 'upward';
        confidence = 'high';
      } else if (combinedScore > 0) {
        direction = 'slightly upward';
        confidence = 'medium';
      } else if (combinedScore < -0.2) {
        direction = 'downward';
        confidence = 'high';
      } else if (combinedScore < 0) {
        direction = 'slightly downward';
        confidence = 'medium';
      } else {
        direction = 'sideways';
        confidence = 'low';
      }
      
      // Generate percentage change based on volatility and direction
      let percentageChange;
      if (direction.includes('upward')) {
        percentageChange = (0.5 + Math.random() * 2).toFixed(2); // 0.5% to 2.5%
        if (volatility === 'high') percentageChange = (1 + Math.random() * 3).toFixed(2); // 1% to 4%
      } else if (direction.includes('downward')) {
        percentageChange = -(0.5 + Math.random() * 2).toFixed(2); // -0.5% to -2.5%
        if (volatility === 'high') percentageChange = -(1 + Math.random() * 3).toFixed(2); // -1% to -4%
      } else {
        percentageChange = (Math.random() > 0.5 ? 1 : -1) * (0.1 + Math.random() * 0.4).toFixed(2); // -0.5% to 0.5%
      }
      
      predictionText += `ðŸ”¹ Direction: ${direction} movement (${percentageChange}%)\n`;
      predictionText += `ðŸ”¹ Confidence: ${confidence} confidence\n\n`;
      
      predictionText += `ðŸ“Š Key Factors:\n`;
      predictionText += `- Price Trend: ${priceTrend}\n`;
      predictionText += `- Market Volatility: ${volatility}\n`;
      predictionText += `- News Sentiment: ${sentimentScore > 0 ? 'positive' : sentimentScore < 0 ? 'negative' : 'neutral'}\n\n`;
      
      predictionText += `âš ï¸ Disclaimer: This prediction is based on algorithmic analysis of recent price trends and news sentiment. It is not financial advice and should not be used as the sole basis for investment decisions.`;
      
      setPrediction(predictionText);
      setSentiment({
        sentiment: sentimentScore > 0 ? 'positive' : sentimentScore < 0 ? 'negative' : 'neutral',
        score: sentimentScore.toFixed(2)
      });
    } catch (err) {
      console.error("Error generating prediction:", err);
      setPrediction("Prediction service currently unavailable. Please check back later.");
    }
  }, [ALPHA_VANTAGE_KEY, FINNHUB_API_KEY]);

  // Fetch stock summary
  const fetchStockSummary = useCallback(async (stockSymbol, customQuestion = '') => {
    try {
      setLoading(true);
      setError(null);
      setIsCustomQuery(!!customQuestion);

      // First try to get fundamental data from Alpha Vantage
      let fundamentalData = {};
      try {
        const overviewResponse = await axios.get(
          `https://www.alphavantage.co/query?function=OVERVIEW&symbol=${stockSymbol}&apikey=${ALPHA_VANTAGE_KEY}`
        );
        fundamentalData = overviewResponse.data;
      } catch (e) {
        console.log("Couldn't fetch fundamental data", e);
      }

      // Create a basic summary from available data
      let summary = `Comprehensive Analysis of ${stockSymbol}\n\n`;
      
      if (fundamentalData && fundamentalData.Name) {
        summary += `Company: ${fundamentalData.Name}\n`;
        summary += `Sector: ${fundamentalData.Sector || 'N/A'}\n`;
        summary += `Industry: ${fundamentalData.Industry || 'N/A'}\n\n`;
        
        if (fundamentalData.Description) {
          summary += `Description: ${fundamentalData.Description.substring(0, 200)}...\n\n`;
        }
        
        summary += `Key Metrics:\n`;
        summary += `- Market Cap: ${fundamentalData.MarketCapitalization || 'N/A'}\n`;
        summary += `- P/E Ratio: ${fundamentalData.PERatio || 'N/A'}\n`;
        summary += `- Dividend Yield: ${fundamentalData.DividendYield || 'N/A'}\n`;
        summary += `- 52 Week High/Low: ${fundamentalData['52WeekHigh'] || 'N/A'}/${fundamentalData['52WeekLow'] || 'N/A'}\n\n`;
      } else {
        summary += `No detailed fundamental data available for ${stockSymbol}.\n`;
        summary += `Showing basic price information only.\n\n`;
      }

      // Add recent price data if available
      try {
        const globalQuote = await axios.get(
          `https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${stockSymbol}&apikey=${ALPHA_VANTAGE_KEY}`
        );
        
        if (globalQuote.data['Global Quote']) {
          const quote = globalQuote.data['Global Quote'];
          summary += `Current Price: $${quote['05. price'] || 'N/A'}\n`;
          summary += `Change: ${quote['10. change percent'] || 'N/A'}\n`;
          summary += `Previous Close: $${quote['08. previous close'] || 'N/A'}\n`;
        }
      } catch (e) {
        console.log("Couldn't fetch quote data", e);
      }

      if (customQuestion) {
        // For custom questions, provide basic answers
        const lowerQuestion = customQuestion.toLowerCase();
        let answer = "I can provide basic information about this stock. Please ask about price, dividends, or key metrics.";
        
        if (lowerQuestion.includes('price') && globalQuote?.data['Global Quote']) {
          const quote = globalQuote.data['Global Quote'];
          answer = `The current price of ${stockSymbol} is $${quote['05. price']} (${quote['10. change percent']}).`;
        } else if (lowerQuestion.includes('dividend') && fundamentalData.DividendYield) {
          answer = `The dividend yield for ${stockSymbol} is ${fundamentalData.DividendYield}.`;
        } else if ((lowerQuestion.includes('pe ratio') || lowerQuestion.includes('p/e')) && fundamentalData.PERatio) {
          answer = `The P/E ratio for ${stockSymbol} is ${fundamentalData.PERatio}.`;
        }
        
        setCustomQueryResult(answer);
      } else {
        setSummary(summary);
      }
    } catch (err) {
      console.error("Error generating summary:", err);
      setError('Failed to generate response. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [ALPHA_VANTAGE_KEY]);

  // Fetch market news
  const fetchStockNews = useCallback(async (stockSymbol) => {
    try {
      const response = await axios.get(
        `https://finnhub.io/api/v1/company-news?symbol=${stockSymbol}&from=${new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}&to=${new Date().toISOString().split('T')[0]}&token=${FINNHUB_API_KEY}`
      );
      
      const filteredNews = response.data
        .filter(item => item.headline && item.image && item.url)
        .slice(0, 5);
      
      setNews(filteredNews);
      
      // Generate basic news summary
      if (filteredNews.length > 0) {
        const summary = `Recent news about ${stockSymbol} includes:\n` +
          filteredNews.slice(0, 3).map(item => `- ${item.headline}`).join('\n') +
          `\n\nFor more details, please read the full articles.`;
        setNewsSummary(summary);
      } else {
        setNewsSummary(`No recent news found for ${stockSymbol}`);
      }
    } catch (err) {
      console.error("Error fetching news:", err);
      setNews([]);
      setNewsSummary(`Failed to load news for ${stockSymbol}`);
    }
  }, [FINNHUB_API_KEY]);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      if (chartInstance.current && chartContainerRef.current) {
        chartInstance.current.applyOptions({
          width: chartContainerRef.current.clientWidth
        });
      }
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      cleanupChart();
    };
  }, [cleanupChart]);

  // Load all data when symbol changes
  useEffect(() => {
    if (symbol) {
      setLoading(true);
      fetchStockSummary(symbol);
      fetchStockNews(symbol);
      fetchStockChartData(symbol);
      generatePrediction(symbol);
    }
    
    return () => {
      cleanupChart();
    };
  }, [symbol, fetchStockSummary, fetchStockNews, fetchStockChartData, generatePrediction, cleanupChart]);

  const handleQuerySubmit = (e) => {
    e.preventDefault();
    if (query.trim() && symbol) {
      fetchStockSummary(symbol, query);
      setQuery('');
    }
  };

  const handleStockSearch = (e) => {
    e.preventDefault();
    if (stockSearch.trim()) {
      navigate(`/ai-summary/${stockSearch.trim().toUpperCase()}`);
    }
  };

  if (!symbol) {
    return (
      <div className="ai-summary-container">
        <div className="ai-summary-header">
          <h2>Stock Analysis Tool</h2>
        </div>

        <div className="primary-search-section">
          <form onSubmit={handleStockSearch} className="primary-search-form">
            <input
              type="text"
              value={stockSearch}
              onChange={(e) => setStockSearch(e.target.value)}
              placeholder="Enter a stock symbol (e.g., AAPL, MSFT)"
              className="primary-search-input"
              autoFocus
            />
            <button type="submit" className="primary-search-button">
              Analyze Stock
            </button>
          </form>
          <p className="search-examples">
            Try: AAPL, MSFT, TSLA, GOOGL, AMZN
          </p>
        </div>

        <div className="welcome-message">
          <h3>Welcome to Stock Analysis Tool</h3>
          <p>
            Get comprehensive analysis of any publicly traded company.
            Enter a stock symbol above to begin.
          </p>
          <div className="features">
            <h4>Features:</h4>
            <ul>
              <li>Company overview and financial metrics</li>
              <li>Recent performance analysis</li>
              <li>Smart 24-hour price predictions</li>
              <li>Latest market news</li>
              <li>Interactive stock charts</li>
              <li>Ability to ask questions about any stock</li>
            </ul>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-summary-container">
      <div className="ai-summary-header">
        <h2>{symbol.toUpperCase()} Stock Analysis</h2>
        <button onClick={() => navigate('/ai-summary')} className="back-button">
          Analyze Another Stock
        </button>
      </div>

      <div className="search-section">
        <form onSubmit={handleQuerySubmit} className="query-form">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder={`Ask about ${symbol.toUpperCase()}...`}
            className="query-input"
          />
          <button type="submit" className="query-button">
            Ask
          </button>
        </form>
      </div>

      {/* Prediction Section */}
      {prediction && (
        <div className="prediction-section">
          <h3>Smart Prediction: Next 24 Hours</h3>
          <div className="prediction-card">
            <div className="prediction-header">
              <span className="prediction-icon">ðŸ”®</span>
              <span className="prediction-title">Algorithmic Forecast</span>
              {sentiment && (
                <span className={`sentiment-badge ${sentiment.sentiment}`}>
                  {sentiment.sentiment} sentiment ({sentiment.score})
                </span>
              )}
            </div>
            <div className="prediction-content">
              {prediction.split('\n').map((line, i) => (
                <p key={i}>{line}</p>
              ))}
            </div>
            <div className="prediction-disclaimer">
              <small>Disclaimer: Predictions are based on algorithmic analysis of market data and are not guaranteed. Always conduct your own research before making investment decisions.</small>
            </div>
          </div>
        </div>
      )}

      {/* Chart Section */}
      <div className="chart-section">
        <h3>{symbol.toUpperCase()} Price Chart (Last 30 Days)</h3>
        <div 
          ref={chartContainerRef} 
          className="chart-container"
          style={{ width: '100%', height: '400px' }}
        />
      </div>
      
      <div className="tabs">
        <button 
          className={`tab-button ${activeTab === 'analysis' ? 'active' : ''}`}
          onClick={() => setActiveTab('analysis')}
        >
          Analysis
        </button>
        <button 
          className={`tab-button ${activeTab === 'news' ? 'active' : ''}`}
          onClick={() => setActiveTab('news')}
        >
          Market News
        </button>
      </div>

      <div className="ai-summary-content">
        {loading ? (
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Loading stock data...</p>
          </div>
        ) : error ? (
          <div className="error-message">
            <p>{error}</p>
            <button onClick={() => window.location.reload()} className="retry-button">
              Retry
            </button>
          </div>
        ) : isCustomQuery ? (
          <div className="query-result">
            <h3>Response to your query:</h3>
            <div className="result-text">
              {customQueryResult.split('\n').map((paragraph, i) => (
                <p key={i}>{paragraph}</p>
              ))}
            </div>
            <button 
              onClick={() => setIsCustomQuery(false)} 
              className="show-original-button"
            >
              Show Full Analysis
            </button>
          </div>
        ) : (
          <>
            {activeTab === 'analysis' && (
              <div className="summary-section">
                <h3>Analysis of {symbol.toUpperCase()}</h3>
                <div className="summary-text">
                  {summary.split('\n').map((paragraph, i) => (
                    <p key={i}>{paragraph}</p>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'news' && (
              <div className="news-section">
                <h3>Latest News About {symbol.toUpperCase()}</h3>
                {newsSummary && (
                  <div className="news-summary">
                    <h4>News Summary:</h4>
                    <p>{newsSummary}</p>
                  </div>
                )}
                <div className="news-list">
                  {news.length > 0 ? (
                    news.map((item, index) => (
                      <div key={index} className="news-item">
                        <div className="news-item-header">
                          {item.image && (
                            <img src={item.image} alt={item.headline} className="news-image" />
                          )}
                          <div>
                            <h4>
                              <a href={item.url} target="_blank" rel="noopener noreferrer">
                                {item.headline}
                              </a>
                            </h4>
                            <p className="news-source">{item.source} - {new Date(item.datetime * 1000).toLocaleDateString()}</p>
                          </div>
                        </div>
                        <p className="news-summary">{item.summary || 'No summary available'}</p>
                      </div>
                    ))
                  ) : (
                    <p>No recent news found for {symbol.toUpperCase()}</p>
                  )}
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default AISummary;
